#define MAX_STEPS 400
#define MAX_DIST 100.
#define SURF_DIST .001

#define FK(k) floatBitsToInt(k*k/7.)^floatBitsToInt(k)
float hash(float a, float b) {
    int x = FK(a), y = FK(b);
    return float((x*x+y)*(y*y-x)-x)/2.14e9;
}

vec3 erot(vec3 p, vec3 ax, float ro) {
  return mix(dot(ax, p)*ax, p, cos(ro)) + cross(ax,p)*sin(ro);
}

vec3 face(vec3 p) {
     vec3 a = abs(p);
     return step(a.yzx, a.xyz)*step(a.zxy, a.xyz)*sign(p);
}

float sdBox(vec3 p, vec3 s) {
    p = abs(p)-s;
	return length(max(p, 0.))+min(max(p.x, max(p.y, p.z)), 0.);
}

vec3 getRo() {
    vec2 m = iMouse.xy/iResolution.xy;
    float t = 0.2 * iTime;
    float r = 3.4;
    vec3 ro = vec3(r * cos(t), 1.8, r * sin(t));
    //ro.yz *= Rot(-m.y*3.14+1.);
    //ro.xz *= Rot(-m.x*6.2831);
    return ro;
}

float GetDist(vec3 p) {
    float d = p.y;
    float h = 0.08;
    float c = length(texture(iChannel0, 0.5 + 0.5 * p.xz).rgb);
    float x = 1.;//0.5 + 0.5 * ths(10., pi * 3.5 + 0.25 * iTime);
    d -= x * thc(4., 1. * (p.x - p.z) + iTime) * h * c;
    d += x * h * thc(4., p.x - p.z + iTime);
    return 0.1 * d; // seems buggy whatever value i put here
}

float RayMarch(vec3 ro, vec3 rd, float z) {

    float dO=0.;
    float s = sign(z);
    for(int i=0; i<MAX_STEPS; i++) {
    	vec3 p = ro + rd*dO;
        float dS = GetDist(p);
        if (s != sign(dS)) { z *= 0.5; s = sign(dS); }
        if(abs(dS)<SURF_DIST || dO>MAX_DIST) break;
        dO += dS*z; 
    }

    return min(dO, MAX_DIST);
}

vec3 GetNormal(vec3 p) {
	float d = GetDist(p);
    vec2 e = vec2(.001, 0);

    vec3 n = d - vec3(
        GetDist(p-e.xyy),
        GetDist(p-e.yxy),
        GetDist(p-e.yyx));

    return normalize(n);
}

vec3 GetRayDir(vec2 uv, vec3 p, vec3 l, float z) {
    vec3 f = normalize(l-p),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f,r),
        c = f*z,
        i = c + uv.x*r + uv.y*u,
        d = normalize(i);
    return d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    vec3 ro = getRo();

    vec3 rd = GetRayDir(uv, ro, vec3(0), 5.);
    vec3 col = vec3(0);

    float d = RayMarch(ro, rd, 1.);

    float IOR = 1.05;
    if(d<MAX_DIST) {
        vec3 p = ro + rd * d;
        vec3 n = GetNormal(p);
        vec3 r = reflect(rd, n);

        vec3 pIn = p - 4. * SURF_DIST * n;
        vec3 rdIn = refract(rd, n, 1./IOR);
        float dIn = RayMarch(pIn, rdIn, -1.);

        vec3 pExit = pIn + dIn * rdIn;
        vec3 nExit = -GetNormal(pExit); // *-1.; ?

        float dif = dot(n, normalize(vec3(1,2,3)))*.5+.5;
        col = vec3(dif);

        vec3 c = texture(iChannel0, 0.5 + 0.5 * p.xz).rgb;
        col = c;
        //col *= 0.8 + 0.2 * step(cos(200. * p.x + p.z * 200.), 0.);
        //col *= clamp(exp(2.-4. * mlength(p.xz)), 0., 1.);
    }

    col = pow(col, vec3(.4545));	// gamma correction

    fragColor = vec4(col,1.0);
}

#define pi 3.14159

#define thc(a,b) tanh(a*cos(b))/tanh(a)
#define ths(a,b) tanh(a*sin(b))/tanh(a)
#define sabs(x) sqrt(x*x+1e-2)
//#define sabs(x, k) sqrt(x*x+k)-0.1

#define Rot(a) mat2(cos(a), -sin(a), sin(a), cos(a))

float cc(float a, float b) {
    float f = thc(a, b);
    return sign(f) * pow(abs(f), 0.25);
}

float cs(float a, float b) {
    float f = ths(a, b);
    return sign(f) * pow(abs(f), 0.25);
}

vec3 pal(in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d) {
    return a + b*cos( 6.28318*(c*t+d) );
}

float h21(vec2 a) {
    return fract(sin(dot(a.xy, vec2(12.9898, 78.233))) * 43758.5453123);
}

float mlength(vec2 uv) {
    return max(abs(uv.x), abs(uv.y));
}

float mlength(vec3 uv) {
    return max(max(abs(uv.x), abs(uv.y)), abs(uv.z));
}

float smin(float a, float b) {
    float k = 0.12;
    float h = clamp(0.5 + 0.5 * (b-a) / k, 0.0, 1.0);
    return mix(b, a, h) - k * h * (1.0 - h);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float time = 0.15 * iTime;

    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.xy;
    vec2 ouv = uv;
    uv.y += 0.12 * time;
    //time += uv.x * 4.;
    float sc = 10.;
    vec2 ipos = floor(sc * uv) + 0.5;
    vec2 fpos = fract(sc * uv) - 0.5;

    float h = h21(ipos);
    float h2 = 2. * pi * h21(ipos + 2.);

    vec2 p = fract(fpos - 4. * thc(2., vec2(h2, 2. * pi * h) + time)) - 0.5;

    float d = length(p);
    float k = 1. + h;
    float s = smoothstep(-k, k, -d + 0.2);

    vec2 pw = vec2(5. + 4. * thc(20., 8. * d + h2 + time));
    vec2 b = pow(1. - 4. * fpos * fpos, pw);

    vec3 e = vec3(1.5);
    // 50% chance of having white in it, .5+.5*cos gives white to some of it
    float cp = step(0.5 + 0.5 * step(h, 0.5) * cos(8. * d), 0.5);
    vec3 col = pal(h2 + 0.25 * d, e, e, e, cp * vec3(0.,0.33,0.66));
    col *= b.x * b.y * s; // * step(h21(ipos + 1.), 0.5); 
    col += 0.03;

    col *= smoothstep(-0.01, 0.01, -mlength(ouv) + 0.2);
    //col *= step(mlength(uv), 10.);

    fragColor = vec4(col,1.0);
}
