void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y * 4.0;
    float unit = 8.0 / iResolution.y;
    fragColor = vec4(0.0);

    float y = pow(uv.x, 1.0 / 3.0);
    float dx = pow(uv.x, -2.0 / 3.0) / 3.0;
    vec4 color = vec4(1.0, 0.0, 0.0, 1.0);

    if (mod(iTime, 2.0) > 1.0) {
        y = sign(uv.x) * pow(abs(uv.x), 1.0 / 3.0);
        color = vec4(0.0, 1.0, 0.0, 1.0);
    }

    fragColor = mix(fragColor, color, smoothstep(unit, 0.0, abs(uv.y - y) / sqrt(1.0 + dx * dx)));
}
