// http://www.jcgt.org/published/0009/03/02/
uvec4 pcg4d(uvec4 v) {
    v = v * 1664525u + 1013904223u;

    v.x += v.y * v.w;
    v.y += v.z * v.x;
    v.z += v.x * v.y;
    v.w += v.y * v.z;

    v ^= v >> 16u;

    v.x += v.y * v.w;
    v.y += v.z * v.x;
    v.z += v.x * v.y;
    v.w += v.y * v.z;

    return v;
}



void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord / iResolution.xy;


    float row=1.;
    float col=6.;
    float speed = 2.;


    vec2 CR = vec2(col,row);
    float RmC = row*col;
    vec2 RmCR = vec2(RmC,row);
    vec2 uv0 = (uv+vec2(0.,row-1.))/CR;
    float time = iTime*speed;

    vec4 uvPreNext =  floor((fract(time + vec4(0.,0.,1./RmC,1./RmC)))*vec4(RmCR,RmCR));

    float weight = fract(time*(RmC));
    vec4 uvAll = vec4(uv0,uv0)+uvPreNext/vec4(CR,CR)*vec4(1.,-1.,1.,-1.);



    vec4 frame0 = texture(iChannel0, uvAll.xy-vec2(1./96.,0.)*mod(floor(time*col),col)-vec2(1./128.,0.));
    vec4 frame1 = texture(iChannel0, uvAll.zw-vec2(1./96.,0.)*mod(floor(time*col)+1.0,col)-vec2(1./128.,0.));

    vec4 color = mix(frame0,frame1,weight);


    // Output to screen
    fragColor = vec4(color);
}
