// Fork of "sphere cup by a plane" by panna_pudi. https://shadertoy.com/view/stKXzy
// 2021-12-31 15:52:22

// Edit : I think I got the interior right by adding this line 10
// return max(length(p) - r, -p.y);

// Edit2 : iq arbitrary cut version
// https://www.shadertoy.com/view/ftVXRc

float splane( in vec2 p, in float r ) {
    p.x = abs(p.x);
    if (p.y > 0.) {
        return max(length(p) - r, -p.y);
    }
    return p.x < r ? -p.y: length(p - vec2(r, 0.));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.y;
    vec2 m = (2.0*iMouse.xy-iResolution.xy)/iResolution.y;

    float roundness = .5*(sin(iTime)*.5+.5);
	float d = splane(p,0.5)-roundness;

	// coloring
    vec3 col = vec3(1.0) - sign(d)*vec3(0.1,0.4,0.7);
    col *= 1.0 - exp(-3.0*abs(d));
	col *= 0.8 + 0.2*cos(150.0*d);
	col = mix( col, vec3(1.0), 1.0-smoothstep(0.0,0.01,abs(d)) );

    if( iMouse.z>0.001 )
    {
    d = splane(m,0.5)-roundness;
    col = mix(col, vec3(1.0,1.0,0.0), 1.0-smoothstep(0.0, 0.005, abs(length(p-m)-abs(d))-0.0025));
    col = mix(col, vec3(1.0,1.0,0.0), 1.0-smoothstep(0.0, 0.005, length(p-m)-0.015));
    }

	fragColor = vec4(col,1.0);
}
