#define Rot(a) mat2(cos(a),-sin(a),sin(a),cos(a))
#define antialiasing(n) n/min(iResolution.y,iResolution.x)
#define S(d,b) smoothstep(antialiasing(1.0),b,d)
#define B(p,s) max(abs(p.x)-s.x,abs(p.y)-s.y)
#define DF(a,b) length(a) * cos( mod( atan(a.y,a.x)+6.28/(b*8.0), 6.28/((b*8.0)*0.5))+(b-1.)*6.28/(b*8.0) + vec2(0,11) )
#define YELLOW vec3(0.76,0.7,0.0)

float hash(vec2 p){
    vec2 rand = fract(sin(p*123.456)*567.89);
    rand += dot(rand,rand*34.56);
    return fract(rand.x*rand.y);
}

float tigerBodyPaint(vec2 p){
    vec2 prevP = p;
    float d = B(p,vec2(0.05,0.08));
    float a = radians(-20.0);
    p.x+=0.02;
    d = max(dot(p,vec2(cos(a),sin(a))),d);
    return d;
}

float hempParts(vec2 p){
    vec2 prevP = p;

    p.x = abs(p.x)-0.033;
    p*=Rot(radians(-30.0));

    float d = B(p,vec2(0.002,0.07));
    p = prevP;
    p.y+=0.062;
    float d2 = B(p,vec2(0.07,0.002));
    d = min(d,d2);

    p = prevP;
    p.y-=0.02;
    d2 = B(p,vec2(0.002,0.035));
    d = min(d,d2);

    p = prevP;
    p.y+=0.04;
    p.x = abs(p.x)-0.033;
    p*=Rot(radians(-55.0));
    d2 = B(p,vec2(0.002,0.04));
    d = min(d,d2);    

    return d;
}

float hempParts2(vec2 p){
    vec2 prevP = p;
    p.y = abs(p.y)-0.058;
    p.y*=-1.0;
    float d = hempParts(p);
    return d;
}

float hempParts3(vec2 p){
    vec2 prevP = p;

    float d = hempParts2(p);

    p*=Rot(radians(60.0));

    float d2 = hempParts2(p);
    d = min(d,d2);
    p*=Rot(radians(60.0));
    d2 = hempParts2(p);
    d = min(d,d2);
    return d;
}

float tigerBodyPaint2(vec2 p){
    p*=1.5;
    vec2 prevP = p;

    p.x = mod(p.x,0.24)-0.12;
    p.y = mod(p.y,0.42)-0.21;
    float d = hempParts3(p*Rot(radians(90.0)));
    p = prevP;
    p.x+=0.12;
    p.y+=0.21;
    p.x = mod(p.x,0.24)-0.12;
    p.y = mod(p.y,0.42)-0.21;
    float d2 = hempParts3(p*Rot(radians(90.0)));

    return min(d,d2);
}

vec3 spring(vec2 p, vec3 col, float brightness){
    vec2 prevP = p;
    float d = B(p,vec2(0.1,0.015));
    p.x-=0.1;
    p.y=abs(p.y)-0.05;
    float d2 = abs(length(p)-0.05)-0.015;
    d = min(d,d2);
    col = mix(col,vec3(0.6)*brightness,S(d,0.0));
    return col;
}

vec3 tigerHead(vec2 p, vec3 col, float brightness){
    vec2 prevP = p;

    float d = B(p,vec2(0.17,0.1));
    float a = radians(45.0);
    p.y = abs(p.y)-0.22;
    d = max(dot(p,vec2(cos(a),sin(a))),d);
    p = prevP;

    a = radians(70.0);
    p.y+=0.1;
    d = max(-dot(p,vec2(cos(a),sin(a))),d);

    p = prevP;

    a = radians(-50.0);
    p.y-=0.205;
    d = max(-dot(p,vec2(cos(a),sin(a))),d);

    p = prevP;
    p.x-=0.07;
    p.y-=0.15;
    float d2 = B(p,vec2(0.05,0.05));

    p.x = abs(p.x)-0.031;
    a = radians(20.0);
    d2 = max(dot(p,vec2(cos(a),sin(a))),d2);
    d = min(d,d2);

    col = mix(col,YELLOW*brightness,S(d,0.0));

    p = prevP;
    p.x-=0.07;
    p.y-=0.15;
    d = B(p,vec2(0.05,0.05));

    p.x = abs(p.x)-0.02;
    a = radians(20.0);
    d = max(dot(p,vec2(cos(a),sin(a))),d);
    d = max(abs(p.y-0.005)-0.03,d);

    col = mix(col,vec3(0.7,0.0,0.0)*brightness,S(d,0.0));    

    p = prevP;
    d = length(p-vec2(-0.03,0.04))-0.04;
    col = mix(col,vec3(1.0)*brightness,S(d,0.0));

    d = length(p-vec2(-0.03,0.04))-0.025;
    col = mix(col,vec3(.0)*brightness,S(d,0.0));

    d = length(p-vec2(-0.11,0.06))-0.015;
    col = mix(col,vec3(0.0)*brightness,S(d,0.0));

    p.x-=0.12;
    p.y+=0.03;
    p*=Rot(radians(-30.));
    d = B(p,vec2(0.07,0.01));
    p = prevP;
    d = max(p.x-0.17,d);
    col = mix(col,vec3(1.0)*brightness,S(d,0.0));

    p = prevP;

    p.x+=0.06;
    p.y+=0.03;    
    p*=Rot(radians(15.));
    d = B(p,vec2(0.13,0.01));
    p = prevP;
    d = max(-p.x-0.17,d);
    col = mix(col,vec3(1.0)*brightness,S(d,0.0));    

    p.x-=0.11;
    p.y-=0.053;
    p*=2.0;
    p*=Rot(radians(45.0));
    d = tigerBodyPaint(p);

    p.x-=0.08;
    p.y-=0.001;
    d2 = tigerBodyPaint(p);

    d = min(d,d2);
    col = mix(col,vec3(0.0)*brightness,S(d,0.0)); 

    return col;
}

vec3 tigerBody(vec2 p, vec3 col, float brightness){
    vec2 prevP = p;

    float d = B(p,vec2(0.25,0.2));
    float a = radians(40.0);
    p.x = abs(p.x)-0.37;
    d = max(dot(p,vec2(cos(a),sin(a))),d);
    p = prevP;

    a = radians(-10.0);
    p.x = abs(p.x)-0.26;
    d = max(dot(p,vec2(cos(a),sin(a))),d);

    p = prevP;

    a = radians(-10.0);
    p.x = abs(p.x)-0.159;
    p.y+=0.23;

    float d2 = B(p,vec2(0.07,0.05));
    p.x = abs(p.x)-0.06;
    d2 = max(dot(p,vec2(cos(a),sin(a))),d2);

    d = min(d,d2);
    d2 = d;
    col = mix(col,YELLOW*0.98*brightness,S(d,0.0));

    p = prevP;
    d = tigerBodyPaint2(p);
    d = max(d2,d);
    col = mix(col,YELLOW*0.58*brightness,S(d,0.0));

    p = prevP;
    d2 = max(p.y+0.25,d2);
    col = mix(col,vec3(1.0)*brightness,S(d2,0.0));

    p = prevP;
    d = length(p-vec2(0.1,-0.03))-0.07;
    col = mix(col,vec3(1.0)*brightness,S(d,0.0));
    d = length(p-vec2(0.1,-0.03))-0.055;
    col = mix(col,vec3(0.0)*brightness,S(d,0.0));

    d = length(p-vec2(-0.13,-0.14))-0.04;
    col = mix(col,vec3(1.0)*brightness,S(d,0.0));
    d = length(p-vec2(-0.13,-0.14))-0.025;
    col = mix(col,vec3(0.0)*brightness,S(d,0.0));

    p.x+=0.03;
    p.y-=0.11;
    p.y*=0.9;
    d = tigerBodyPaint(p);

    d2 = tigerBodyPaint(p-vec2(0.1,0.0));
    d = min(d,d2);

    d2 = tigerBodyPaint(p-vec2(-0.1,0.0));
    d = min(d,d2);

    col = mix(col,vec3(0.0)*brightness,S(d,0.0));

    p = prevP;
    p.x+=0.23;
    p.y-=0.05;
    d = B(p,vec2(0.02,0.14));
    a = radians(-40.0);
    p.y = abs(p.y);
    p.y-=0.117;

    d = max(-dot(p,vec2(cos(a),sin(a))),d);    

    col = mix(col,vec3(0.7,0.0,0.0)*brightness,S(d,0.0));

    p = prevP;

    p.x-=0.01;
    p.y+=0.133;
    p.x*=1.5;
    p.y*=-1.2;
    d = tigerBodyPaint(p-vec2(-0.04,0.0));

    d2 = tigerBodyPaint(p-vec2(0.04,0.0));
    d = min(d,d2);

    col = mix(col,vec3(0.0)*brightness,S(d,0.0));

    return col;
}

vec3 tail(vec2 p, vec3 col, float brightness){
    vec2 prevP = p;

    p.x+=0.04;
    float mask = B(p,vec2(0.2,0.06));
    p = prevP;
    float d = B(p,vec2(0.2,0.1));
    float d2 = d;
    d = max(-mask,d);
    col = mix(col,YELLOW*0.98*brightness,S(d,0.0));

    p.x+=iTime*0.1;
    p*=Rot(radians(45.0));
    p.x = mod(p.x,0.05)-0.025;
    d = abs(p.x)-0.01;
    d = max(d2,d);
    d = max(-mask,d);
    col = mix(col,vec3(0.0)*brightness,S(d,0.0));  

    return col;
}

vec2 animateBody(vec2 p){
    float frame = mod(iTime,0.4);
    if(frame<0.2){
        p.y-=0.01;
    } else {
        p.y+=0.01;
    }
    return p;
}

vec3 tiger(vec2 p, vec3 col, float brightness){
    vec2 prevP = p;

    // shadow
    p-=vec2(0.1,-0.35);
    p.x*=0.2;
    p.y*=1.5;
    float frame = mod(iTime,0.4);
    float s = 0.1;
    if(frame<0.2){
        s = 0.09;
    }
    float d = length(p)-s;
    col = mix(col,vec3(0.0),S(d,-0.1));    
    p = prevP;

    p = animateBody(p);  

    // spring
    p-=vec2(0.4,-0.1);
    s = 1.0;
    frame = mod(iTime,0.4);
    if(frame<0.1){
        s = 1.0;
    } else if(frame>=0.1 && frame<0.2){
        s = 2.5;
    } else if(frame>=0.2 && frame<0.3){
        s = 5.0;
    } else if(frame>=0.3 && frame<0.4){
        s = -2.5;
    }

    p.y*=s;
    col = spring(p,col,brightness);

    // tail
    p = prevP;
    p = animateBody(p); 
    col = tail(p-vec2(0.25,0.15),col,brightness);    

    // head
    animateBody(p);
    p*=Rot(radians(sin(iTime*5.0)*5.0));
    col = tigerHead(p-vec2(-0.3,0.0),col,brightness);

    // body
    p = prevP;
    p = animateBody(p);
    col = tigerBody(p-vec2(0.11,-0.05),col,brightness);

    return col;
}

vec3 darumaMain(vec2 p, vec3 col){
    vec2 prevP = p;
    p.x *= mix(0.1,2.0,smoothstep(-1.0,1.0,p.y));
    float d = length(p)-0.15;
    col = mix(col,vec3(0.8,0.2,0.2),S(d,0.0));
    p = prevP;

    p.y-=0.04;
    p.y*=1.4;
    p.x *= mix(0.1,2.0,smoothstep(-1.0,1.0,p.y));
    d = length(p)-0.1;
    col = mix(col,vec3(0.9,0.8,0.7),S(d,0.0));
    p = prevP;

    p.x = abs(p.x)-0.035;
    p.y-=0.04;
    d = length(p)-0.028;
    col = mix(col,vec3(1.0),S(d,0.0));
    d = length(p)-0.02;
    col = mix(col,vec3(0.0),S(d,0.0));
    p = prevP;

    p.x = abs(p.x)-0.03;
    p.y-=0.08;
    p*=Rot(radians(-20.0)); 
    p.y*=4.0;
    d = length(p)-0.025;
    col = mix(col,vec3(0.0),S(d,0.0));
    p = prevP;

    p.x = abs(p.x)-0.03;
    p.y+=0.005;
    p*=Rot(radians(10.0)); 
    p.y*=3.0;
    d = length(p)-0.02;
    col = mix(col,vec3(0.0),S(d,0.0));
    p = prevP;    

    p.x = abs(p.x)-0.02;
    p.x *= mix(5.0,0.01,smoothstep(-1.0,1.0,p.y));
    p.y+=0.09;
    d = length(p)-0.03;
    col = mix(col,vec3(0.8,0.6,0.2),S(d,0.0));

    p.x = abs(p.x)-0.1;
    p.y-=0.01;
    d = length(p)-0.03;
    col = mix(col,vec3(0.8,0.6,0.2),S(d,0.0));

    p = prevP;
    p.x = abs(p.x)-0.09;
    p.x *= mix(5.0,0.01,smoothstep(-1.0,1.0,p.y));
    p.y+=0.06;
    d = length(p)-0.03;
    col = mix(col,vec3(0.8,0.6,0.2),S(d,0.0));
    return col;
}

vec3 daruma(vec2 p, vec3 col){
    vec2 prevP = p;

    // shadow
    p.x*=0.5;
    p.y*=1.5;
    p.y+=0.22;
    float d = length(p)-0.1;
    col = mix(col,vec3(0.0),S(d,-0.12));

    p = prevP;
    p*=Rot(radians(sin(iTime*5.0)*10.0));
    col = darumaMain(p,col);
    return col;
}

float charZero(vec2 p, float speed){
    vec2 prevP = p;
    p.y+=0.07;
    p.y+=iTime*speed;
    p.y = mod(p.y,0.14)-0.07;
    float d = abs(B(p,vec2(0.02,0.04)))-0.01;

    p = prevP;
    d = max(B(p,vec2(0.05,0.07)),d);
    return d;
}

float charTwo(vec2 p, float speed){
    vec2 prevP = p;
    p.y+=0.07;
    p.y+=iTime*speed;
    p.y = mod(p.y,0.14)-0.07;
    float d = B(p,vec2(0.03,0.05));
    p.x+=0.02;
    p.y-=0.02;
    d = max(-B(p,vec2(0.03,0.008)),d);

    p.x-=0.04;
    p.y+=0.04;
    d = max(-B(p,vec2(0.03,0.008)),d);

    p = prevP;
    d = max(B(p,vec2(0.05,0.07)),d);

    return d;
}

float flower(vec2 p){
    vec2 prevP = p;
    p = DF(p,1.25);
    p-=vec2(0.05,0.05);
    float d = length(p)-0.05;
    p = prevP;
    float d2 = length(p)-0.05;
    d = min(d,d2);

    p = DF(p,3.0);
    p-=vec2(0.02,0.02);
    p*=Rot(radians(45.0));
    d2 = B(p,vec2(0.003,0.03));
    d = max(-d2,d);
    p = prevP;
    p = DF(p,3.0);
    p-=vec2(0.04,0.04);
    d2 = length(p)-0.008;
    d = max(-d2,d);
    return d;
}

float pine(vec2 p){
    vec2 prevP = p;
    p.x = abs(p.x)-0.055;
    p*=Rot(radians(45.0));
    float d = length(p)-0.05;
    p.y+=0.01;
    float d2 = B(p,vec2(0.004,0.02));
    d = max(-d2,d);
    p = prevP;

    p.y-=0.025;
    d2 = length(p)-0.05;
    p.y+=0.01;
    d2 = max(-B(p,vec2(0.004,0.02)),d2);

    d = min(d,d2);

    return d;
}

vec3 mailSticker(vec2 p, vec3 col){
    vec2 prevP = p;
    p.x *= mix(-3.0,8.,smoothstep(-1.0,1.0,p.y));
    float d = B(p,vec2(0.2,0.15));
    float d2 = d;
    col = mix(col,vec3(1.0,0.3,0.3),S(d,0.0));

    p = prevP;
    p.x+=iTime*0.05;
    p.y-=0.08;
    p.y+=cos(p.x*100.0)*0.012;
    d = -p.y;
    d = max(d2,d);
    col = mix(col,vec3(1.0,0.7,0.7),S(d,0.0));

    p = prevP;

    d = length(p-vec2(0.08,0.18))-0.05;
    d = max(-d2,d);
    col = mix(col,vec3(1.0,0.3,0.3),S(d,0.0));

    p.y-=0.035;
    d = abs(B(p,vec2(0.2,0.23)))-0.01;
    col = mix(col,vec3(1.0,0.3,0.3),S(d,0.0));

    return col;
}

vec3 bottomUITex(vec2 p){
    vec2 prevP = p;
    p*=70.0;
    p.y-=iTime*5.0;
    vec2 uv = fract(p)-0.5;
    vec2 id = floor(p);
    float n = hash(id);

    return vec3(n);
}

// UI
vec3 drawUI(vec2 p, vec3 col){
    vec2 prevP = p;

    //mail sticker
    p*=1.5;
    p.x+=0.89;
    p.y-=0.4;
    col = mailSticker(p,col);
    p = prevP;

    // top right
    p.x-=0.08;
    p.y-=0.38;
    float d = abs(B(p,vec2(0.05,0.07)))-0.005;
    p.x= abs(p.x)-0.12;
    float d2 = abs(B(p,vec2(0.05,0.07)))-0.005;
    d = min(d,d2);
    col = mix(col,vec3(1.0,0.3,0.3),S(d,0.0));
    p = prevP;

    p.x-=0.27;
    p.y-=0.38;
    d = B(p,vec2(0.016,0.006));
    col = mix(col,vec3(1.0,0.3,0.3),S(d,0.0));

    p = prevP;
    p.x-=0.51;
    p.y-=0.38;
    p.x= abs(p.x)-0.058;
    p.x= abs(p.x)-0.058;
    p.x= abs(p.x)-0.058;
    d2 = abs(B(p,vec2(0.05,0.07)))-0.002;
    d = min(d,d2);
    col = mix(col,vec3(1.0,0.3,0.3),S(d,0.0));
    p = prevP;

    d = charTwo(p-vec2(0.335,0.38),0.1);
    d2 = charZero(p-vec2(0.452,0.38),0.15);
    d = min(d,d2);
    d2 = charTwo(p-vec2(0.568,0.38),0.2);
    d = min(d,d2);
    d2 = charTwo(p-vec2(0.685,0.38),0.25);
    d = min(d,d2);
    col = mix(col,vec3(0.0),S(d,0.0));
    p = prevP;

    // bottom
    p.y+=0.42;
    d = B(p,vec2(0.23,0.04));
    d2 = d;
    col = mix(col,vec3(1.0,0.3,0.3),S(d,0.0));

    p.x+=iTime*0.1;
    p.y+=cos(p.x*30.0)*0.025;
    d = abs(p.y)-0.005;
    d = max(d2,d);
    col = mix(col,vec3(1.0,0.6,0.6),S(d,0.0));

    p = prevP;
    p.y+=0.42;
    p.x = abs(p.x)-0.5;
    d = B(p,vec2(0.23,0.04));
    col = mix(col,(bottomUITex(p)*0.2)+vec3(1.0,0.6,0.6),S(d,0.0));

    p = prevP;
    p.x+=0.6;
    p.y+=0.23;
    p*=Rot(radians(iTime*20.0));
    d = flower(p);
    col = mix(col,vec3(1.0,0.6,0.6),S(d,0.0));

    p = prevP;
    p.x-=0.6;
    p.y+=0.3-sin(iTime*2.0)*0.01;

    d = pine(p);
    col = mix(col,vec3(1.0,0.6,0.6),S(d,0.0));

    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (fragCoord-0.5*iResolution.xy)/iResolution.y;
    vec2 prevP = p;
    vec3 col = vec3(0.9);

    p.x*=-1.8;
    p.y*=1.8;
    p.y-=0.5;
    p.x+=iTime*0.3;
    p.x = mod(p.x,4.0)-2.0;
    col = tiger(p*2.0,col,0.8);
    p = prevP;

    p*=1.3;
    p.y-=0.15;
    p.x+=iTime*0.5;
    p.x-=5.3;
    p.x = mod(p.x,4.0)-2.0;
    col = tiger(p*2.0,col,0.9);
    p = prevP;

    p.y+=0.01;
    p.x-=iTime*0.5;
    p.x+=0.3;
    p.x = mod(p.x,2.0)-1.0;
    col = daruma(p,col);

    p = prevP;
    col = tiger(p,col,1.0);
    float t = iTime;
    if(t<=0.0){
        t = 1.0;
    }
    col+=hash(p*t*0.1)*0.3;

    col = drawUI(p,col);

    /*
    col = vec3(0.9);
    float d = pine(p);
    col = mix(col,vec3(1.0,0.3,0.3),S(d,0.0));
    */

    fragColor = vec4(col,1.0);
}
