#define antialiasing(n) n/min(iResolution.y,iResolution.x)
#define S(d,b) smoothstep(antialiasing(0.8),b,d)
#define S2(d,b) smoothstep(8.0*antialiasing(1.0),b,d)
#define PI     3.14159265
#define TWO_PI 6.28318530

#define R iResolution.xy
#define s3(v,l) smoothstep(l/R.y,0.,v) // AA

vec3 DrawFigBorde(vec3 pColObj, vec3 colOut, float distObj )
{ colOut = mix(colOut,pColObj ,S2( distObj,0.0));
  colOut = mix(colOut,vec3(0.0) ,S2(abs( distObj)-0.005,0.0));
  return colOut;
}

float sdCircle( vec2 p, float r )
{ return length(p) - r;}

float sdSegment( in vec2 p, in vec2 a, in vec2 b )
{  vec2 pa = p-a, ba = b-a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h );
}

vec2 cs(float x)
{ return vec2(cos(x),sin(x) );}


vec3 Brazosv2(vec2 p, vec3 colOut)
{   float l1,l2,l3,l4a,l4b,l5a,l5b;
    float a1, a2,a3,a4a,a4b,a5a,a5b;
    vec2 p0,p1,p2,p3,p4a, p4b,p5a,p5b;

    float t=iTime;

    p0=vec2(0.0,0.0);
    a1=radians(90.0+45.*sin(t*10.0));
    a2=radians(45.*sin(t*10.0));
    a3=radians(90.0+45.0*sin(t*10.0)); //cuerpo
    //pierna
    a4a=radians(270.0-45.0*sin(t*2.0+cos(t)));
    a4b=radians(270.0+45.0*sin(t*2.0+cos(t)));

    //pies
    a5a=radians( 10.0*sin(t*10.));
    a5b=radians(-10.0*sin(t*10.));


    l1=-2.25;
    l2=-2.5;
    l3=-3.0;
    l4a=3.0;
    l4b=3.0;
    l5a=1.0;
    l5b=1.0;

    p1=p0+l1*cs(a1);
    p2=p1+l2*cs(a2);
    p3=p2+l3*cs(a3);

    p4a=p3+l4a*cs(a4a);
    p4b=p3+l4b*cs(a4b);

    p5a=p4a+l5a*cs(a5a);
    p5b=p4b+l5b*cs(a5b);


    float d1= sdSegment(p,p0,p1);
    float d2= sdSegment(p,p1,p2);
    float d3= sdSegment(p,p2,p3);
    float d4a= sdSegment(p,p3,p4a);
    float d4b= sdSegment(p,p3,p4b);

    float d5a= sdSegment(p,p4a,p5a);
    float d5b= sdSegment(p,p4b,p5b);


    float dDiagonal= sdSegment(p,vec2(-1.0,2.0),vec2(1.0,-2.0));
    float dBoca= sdSegment(p-vec2(0.25,0.5),p2,p2-vec2(-0.5,0.0));


    float dcabeza= sdCircle(p-p2-vec2(0.0,1.0),1.0);
    float dOjo= sdCircle(p-p2-vec2(0.5,1.1),0.1);
       vec3 col=colOut;

       col= DrawFigBorde(vec3(1.0),col,dcabeza);
    col= DrawFigBorde(vec3(0.0),col,dOjo);

    col= DrawFigBorde(vec3(0.0), col,abs(dBoca)-0.01 );
    col= DrawFigBorde(vec3(0.5), col,abs(dDiagonal)-0.1 );
    col= DrawFigBorde(vec3(0.0), col,abs(d1)-0.1 );
    col= DrawFigBorde(vec3(1.0,0.0,0.0), col,abs(d2)-0.1 );
    col= DrawFigBorde(vec3(0.0,0.0,1.0), col,abs(d3)-0.1 );
    col= DrawFigBorde(vec3(1.0,0.0,1.0), col,abs(d4a)-0.1 );
    col= DrawFigBorde(vec3(1.0,0.0,1.0), col,abs(d4b)-0.1 );

    col= DrawFigBorde(vec3(1.0,1.0,0.0), col,abs(d5a)-0.1 );
    col= DrawFigBorde(vec3(1.0,1.0,0.0), col,abs(d5b)-0.1 );
    return col;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{   vec2 uv = -1.0 + 2.0 * fragCoord.xy/iResolution.xy;
	uv.x *= iResolution.x/iResolution.y;
    uv-=vec2(0.0,-0.5);
    float esc=8.0;
    uv*=esc;
    vec3 col=vec3(0.0,0.5,1.0);
    col= Brazosv2(uv-vec2(0.0,5.0), col);
    fragColor = vec4(col,1.0);
}
