float sum(vec2 v) { return v.x + v.y; }

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 col = vec3(1);
    float map = 4., 
          t = iTime;

    // Pixel Normalizations //
    vec2 uv101 = fragCoord.xy/iResolution.xy - 0.5,
         uv = uv101*map * vec2(iResolution.x / iResolution.y, 1);

    // Rotation //
    float r = t/1.5; // Angle of Rotation
    uv *= mat2( cos(r), -sin(r), sin(r), cos(r) );

    // Figures //
    if (sum(cos(uv*30.)) > 0.9) col = vec3(uv.x, uv.y, 1); // Multicolored Circles

    float k = (cos(t*2.)*0.5+0.5)*3.+0.5;
    if (sum( pow(abs(uv), vec2(k)) ) < 1.) col -= 1.; // Black Figure

    col *= (1. - length(uv101/2.))*1.2; // Vignette

    fragColor = vec4(col,1.0);
}
