// "Interactive Mandelbulb 2" by dr2 - 2021
// License: Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License

// Updated with transparent / reflecting envelope.

/*
  Explore the sublime Mandelbulb; use mouse to spin and sliders to zoom and override
  automatically varying envelope.

  Adapted from something I wrote a long time ago while learning CUDA; further
  details can be found at the skytopia, subblue and iquilezles sites.
*/

#define AA   0     // (= 0/1) optional antialiasing

#if 0
#define VAR_ZERO min (iFrame, 0)
#else
#define VAR_ZERO 0
#endif

float Minv2 (vec2 p);
float Maxv2 (vec2 p);
vec3 HsvToRgb (vec3 c);
vec2 Rot2D (vec2 q, float a);
mat3 StdVuMat (float el, float az);
float Fbm2 (vec2 p);
vec4 Loadv4 (int idVar);

vec3 ltDir;
float dstFar, tCur, sFac, reflFac;
int nIt, nStep;
const int maxStep = 150;
const float pi = 3.1415927;

float ObjDf (vec3 p)
{
  vec4 aa, sa, ca;
  vec3 c, q, qd;
  float qLen, qdLen, q2, q4, phi, theta, thetad, phid;
  q = p.xzy / sFac;
  c = q;
  qLen = length (q);
  phi = atan (q.y, q.x);
  theta = (qLen > 0.) ? acos (q.z / qLen) : 0.;
  thetad = 0.;
  phid = 0.;
  qdLen = 1.;
  nIt = 0;
  for (int n = VAR_ZERO; n < 5; n ++) {
    q2 = qLen * qLen;
    q4 = q2 * q2;
    aa = vec4 (8. * vec2 (theta, phi), 7. * vec2 (theta, phi) + vec2 (thetad, phid));
    sa = sin (aa);
    ca = cos (aa);
    q = q4 * q4 * vec3 (sa.x * vec2 (ca.y, sa.y), ca.x) + c;
    qd = 8. * q4 * q2 * qLen * qdLen * vec3 (sa.z * vec2 (ca.w, sa.w), ca.z) + 1.;
    qLen = length (q);
    phi = atan (q.y, q.x);
    theta = (qLen > 0.) ? acos (q.z / qLen) : 0.;
    qdLen = length (qd);
    phid = atan (qd.y, qd.x);
    thetad = (qdLen > 0.) ? asin (qd.z / qdLen) : 0.;
    ++ nIt;
    if (qLen > 3.1623) break;
  }
  return (qLen > 0.) ? sFac * 0.5 * qLen * log (qLen) / qdLen : 0.;
}

float ObjRay (vec3 ro, vec3 rd)
{
  float dHit, d, eps;
  eps = 1e-3;
  dHit = 0.;
  nStep = 0;
  for (int j = VAR_ZERO; j < maxStep; j ++) {
    d = ObjDf (ro + dHit * rd);
    dHit += d;
    ++ nStep;
    if (d < eps || dHit > dstFar) break;
  }
  if (d >= eps) dHit = dstFar;
  return dHit;
}

vec3 ObjNf (vec3 p, float del)
{
  vec4 v;
  vec2 e;
  e = del * vec2 (1., -1.);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = ObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

vec3 ShowWg (vec2 uv, vec2 canvas, vec3 col, vec2 parmV)
{
  vec4 wgBx[2];
  vec3 cc[2], cw;
  vec2 ut, ust, c;
  float vW[2], asp;
  c = vec2 (1., 0.5);
  cc[0] = c.xyy;
  cc[1] = c.yxy;
  asp = canvas.x / canvas.y;
  for (int k = 0; k < 2; k ++)
     wgBx[k] = vec4 ((0.41 + 0.05 * float (k)) * asp, -0.15, 0.014 * asp, 0.18);
  vW[0] = parmV.x;
  vW[1] = parmV.y;
  for (int k = 0; k < 2; k ++) {
    ut = 0.5 * uv - wgBx[k].xy;
    ust = abs (ut) - wgBx[k].zw * vec2 (0.7, 1.);
    if (Maxv2 (ust) < 0.) {
      if (Minv2 (abs (ust)) * canvas.y < 2.) cw = 0.3 * cc[k];
      else cw = (mod (0.5 * ((0.5 * uv.y - wgBx[k].y) / wgBx[k].w - 0.99), 0.1) *
         canvas.y < 6.) ? 0.3 * cc[k] : 0.6 * cc[k];
    }
    ut.y -= (vW[k] - 0.5) * 2. * wgBx[k].w;
    cw = mix (cc[k], cw, smoothstep (0., 0.5, abs (length (ut * vec2 (1., 2.)) - 0.025) *
       canvas.y - 3.));
  }
  return mix (col, cw, length (cw));
}

vec3 BgCol (vec3 rd)
{
  vec3 col;
  vec2 u;
  float el, f;
  rd.xz = Rot2D (rd.xz, 0.01 * tCur);
  el = asin (rd.y);
  u = vec2 (atan (rd.z, - rd.x) + pi, tan (2. * atan (0.5 * el))) / (2. * pi);
  f = 64.;
  col = mix (vec3 (0., 0.1, 0.3), vec3 (0.8), mix (Fbm2 (f * u),
     Fbm2 (f * (u - vec2 (1., 0.))), u.x));
  col = mix (col, vec3 (0.2, 0.3, 0.4), smoothstep (0.95, 0.98, abs (el) / (0.5 * pi)));
  return col;
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec3 vn, col, roo, rdo, colS[2];
  float dHit, b, rLim;
  int nItT;
  roo = ro;
  rdo = rd;
  rLim = 3.;
  b = - dot (ro, rd);
  for (int k = VAR_ZERO; k < 2; k ++) {
    sFac = 0.9 + 0.1 * float (k);
    ro = roo;
    rd = rdo;
    dHit = b * b - dot (ro, ro) + sFac * rLim;
    if (dHit > 0.) {
      ro += (b - sqrt (dHit)) * rd;
      dHit = ObjRay (ro, rd);
    } else dHit = dstFar;
    if (dHit < dstFar) {
      ro += dHit * rd;
      nItT = nIt;
      vn = ObjNf (ro, ((k == 0) ? 0.001 : 0.1));
      if (k == 0) {
        colS[k] = HsvToRgb (vec3 (fract (0.6 * log (float (nItT))) + fract (0.1 * tCur),
           1., 1. - 0.5 * smoothstep (0.7, 1., float (nStep) / float (maxStep))));
        colS[k] = colS[k] * (0.3 + 0.7 * max (dot (vn, ltDir), 0.) +
           pow (max (dot (ltDir, reflect (rd, vn)), 0.), 32.));
      } else {
        rd = reflect (rd, vn);
        colS[k] = mix (BgCol (rd), vec3 (0.9, 0.9, 1.),
           pow (max (dot (ltDir, rd), 0.), 1024.));
      }
    } else colS[k] = BgCol (rdo);
  }
  col = mix (colS[0], colS[1], reflFac);
  return clamp (col, 0., 1.);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 stDat;
  vec3 ro, rd, col;
  vec2 canvas, uv;
  float az, el, zmFac, zmVar, sr;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  stDat = Loadv4 (0);
  el = stDat.x;
  az = stDat.y;
  stDat = Loadv4 (2);
  zmVar = stDat.x;
  zmFac = 30. * zmVar + 8.;
  reflFac = stDat.y;
  dstFar = 20.;
  vuMat = StdVuMat (el, az);
  ro = vuMat * vec3 (0., 0., -10.);
  rd = vuMat * normalize (vec3 (uv, zmFac));
  ltDir = vuMat * normalize (vec3 (1., 1., -1.));
#if ! AA
  const float naa = 1.;
#else
  const float naa = 3.;
#endif  
  col = vec3 (0.);
  sr = 2. * mod (dot (mod (floor (0.5 * (uv + 1.) * canvas), 2.), vec2 (1.)), 2.) - 1.;
  for (float a = float (VAR_ZERO); a < naa; a ++) {
    rd = vuMat * normalize (vec3 (uv + step (1.5, naa) * Rot2D (vec2 (0.5 / canvas.y, 0.),
       sr * (0.667 * a + 0.5) * pi), zmFac));
    col += (1. / naa) * ShowWg (uv, canvas, ShowScene (ro, rd), vec2 (zmVar, reflFac));
  }
  fragColor = vec4 (col, 1.);
}

float Minv2 (vec2 p)
{
  return min (p.x, p.y);
}

float Maxv2 (vec2 p)
{
  return max (p.x, p.y);
}

vec3 HsvToRgb (vec3 c)
{
  return c.z * mix (vec3 (1.), clamp (abs (fract (c.xxx + vec3 (1., 2./3., 1./3.)) * 6. - 3.) - 1.,
     0., 1.), c.y);
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

mat3 StdVuMat (float el, float az)
{
  vec2 ori, ca, sa;
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  return mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
         mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
}

const float cHashM = 43758.54;

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (dot (p, cHashVA2) + vec2 (0., cHashVA2.x)) * cHashM);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float Fbm2 (vec2 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int i = 0; i < 5; i ++) {
    f += a * Noisefv2 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

#define txBuf iChannel0
#define txSize iChannelResolution[0].xy

const float txRow = 32.;

vec4 Loadv4 (int idVar)
{
  float fi;
  fi = float (idVar);
  return texture (txBuf, (vec2 (mod (fi, txRow), floor (fi / txRow)) + 0.5) / txSize);
}

// "Interactive Mandelbulb 2" by dr2 - 2021
// License: Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License

float SmoothBump (float lo, float hi, float w, float x);
vec4 Loadv4 (int idVar);
void Savev4 (int idVar, vec4 val, inout vec4 fCol, vec2 fCoord);

const float txRow = 32.;
const float pi = 3.1415927;

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  vec4 wgBx[2], stDat, mPtr, mPtrP;
  vec2 iFrag, canvas, ust;
  float tCur, tCurP, el, az, vW, asp, zmVar, autoMod, reflFac, tMan;
  int pxId, wgSel, wgReg, kSel;
  canvas = iResolution.xy;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  iFrag = floor (fragCoord);
  pxId = int (iFrag.x + txRow * iFrag.y);
  if (iFrag.x >= txRow || pxId >= 3) discard;
  wgSel = -1;
  wgReg = -2;
  if (iFrame <= 3) {
    el = -0.2 * pi;
    az = 0.;
    zmVar = 0.;
    reflFac = 0.5;
    mPtrP = mPtr;
    autoMod = 1.;
    tMan = tCur;
    tCurP = tCur;
  } else {
    stDat = Loadv4 (0);
    el = stDat.x;
    az = stDat.y;
    autoMod = stDat.z;
    tMan = stDat.w;
    stDat = Loadv4 (1);
    mPtrP = vec4 (stDat.xyz, 0.);
    wgSel = int (stDat.w);
    stDat = Loadv4 (2);
    zmVar = stDat.x;
    reflFac = stDat.y;
    tCurP = stDat.z;
  }
  wgReg = -2;
  asp = canvas.x / canvas.y;
  if (mPtr.z > 0.) {
    for (int k = 0; k < 2; k ++)
       wgBx[k] = vec4 ((0.41 + 0.05 * float (k)) * asp, -0.15, 0.014 * asp, 0.18);
    for (int k = 0; k < 2; k ++) {
      ust = abs (mPtr.xy * vec2 (asp, 1.) - wgBx[k].xy) - wgBx[k].zw;
      if (max (ust.x, ust.y) < 0.) wgReg = k;
    }
    if (mPtrP.z <= 0.) wgSel = wgReg;
    autoMod = 0.;
    tMan = tCur;
  } else {
    wgSel = -1;
    wgReg = -2;
  }
  if (wgSel < 0) {
    if (mPtr.z > 0.) {
      az += 0.07 * (1. - 0.85 * zmVar) * mPtr.x;
      el += 0.07 * (1. - 0.85 * zmVar) * mPtr.y;
    }
  } else {
    for (int k = 0; k < 2; k ++) {
      if (wgSel == k) {
        kSel = k;
        vW = clamp (0.5 + 0.5 * (mPtr.y - wgBx[k].y) / wgBx[k].w, 0., 0.99);
        break;
      }
    }
    if (kSel == 0) zmVar = vW;
    else if (kSel == 1) reflFac = vW;
  }
  if (autoMod == 1.) {
    az = mod (az + 0.13 * (tCur - tCurP), 2. * pi);
    el = mod (el + 0.1 * (tCur - tCurP), 2. * pi);
    reflFac = SmoothBump (0.22, 0.78, 0.2, fract (0.03 * tCur));
  } else if (tCur - tMan > 10.) {
    autoMod = 1.;
    tMan = tCur;
  }
  if (pxId == 0) stDat = vec4 (el, az, autoMod, tMan);
  else if (pxId == 1) stDat = vec4 (mPtr.xyz, float (wgSel));
  else if (pxId == 2) stDat = vec4 (zmVar, reflFac, tCur, 0.);
  Savev4 (pxId, stDat, fragColor, fragCoord);
}

float SmoothBump (float lo, float hi, float w, float x)
{
  return (1. - smoothstep (hi - w, hi + w, x)) * smoothstep (lo - w, lo + w, x);
}

#define txBuf iChannel0
#define txSize iChannelResolution[0].xy

vec4 Loadv4 (int idVar)
{
  float fi;
  fi = float (idVar);
  return texture (txBuf, (vec2 (mod (fi, txRow), floor (fi / txRow)) + 0.5) / txSize);
}

void Savev4 (int idVar, vec4 val, inout vec4 fCol, vec2 fCoord)
{
  vec2 d;
  float fi;
  fi = float (idVar);
  d = abs (fCoord - vec2 (mod (fi, txRow), floor (fi / txRow)) - 0.5);
  if (max (d.x, d.y) < 0.5) fCol = val;
}
