// Complete copy of https://iquilezles.org/articles/fbm
// https://www.shadertoy.com/view/WsV3zz
float gnoise( float nn ) 
{
    int n = int(nn);
    n = (n << 13) ^ n;
    n = n * (n * n * 15731 + 789221) + 1376312589;
    return -1.0+2.0*float( n & ivec3(0x0fffffff))/float(0x0fffffff);
}
float fbm( in float x, in float G )
{    
    x += 25.06;
    float n = -.30;
    float s = 1.20;
    float a = 1.10;
    float f = .50+sin(floor(iTime*8.)*6.66);    
    for( int i=0; i<16; i++ )
    {
        n += s*gnoise(x*f);
        a += s;
        s *= G;
        f *= 2.0;
        x += 0.31;
    }
    return n;
}
vec3 pal(float t){return .5+.5*cos(6.2813*(1.*t+vec3(.0,.3,.7)));}
mat2 rot(float a){float c=cos(a),s=sin(a);return mat2(c,-s,s,c);}
void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = (fragCoord.xy -.5* iResolution.xy)/iResolution.y;
    uv*=2.;


    vec3 d =vec3(0.);
    const float lim=100.;

    for(float i =0.;i<=lim;i++){
    uv*=rot(.1);
    float f = fract(i/lim+iTime*.1);
    float s = mix(12.,.1,f);
    vec2 uuv = uv*s;
    uuv.y = -abs(uuv.y);
    float q = fbm(uuv.x+sin(i*3.33-gnoise(i/lim)*.4)*2.,.25-gnoise(i/lim)*.2);
    d += pal(i/lim)*step(0.,1.-abs(uuv.y+3.)-q)*f;
    }
    d/=lim;
    vec3 col = vec3(d);
    col = mix(col,1.-col,sqrt(col)*1.5);
    fragColor = vec4(col,1.0);
}
