// CC0: A Sunday Distance field
//  Combination of an earlier eye shader and a hand shader in an effort to
//  to recreate the classic hand eye pattern

#define RESOLUTION iResolution
#define TIME       iTime

#define PI  3.141592654
#define TAU (2.0*PI)

// License: MIT, author: Inigo Quilez, found: https://iquilezles.org/www/index.htm
vec3 postProcess(vec3 col, vec2 q) {
  col = clamp(col, 0.0, 1.0);
  col = pow(col, vec3(1.0/2.2));
  col = col*0.6+0.4*col*col*(3.0-2.0*col);
  col = mix(col, vec3(dot(col, vec3(0.33))), -0.4);
  col *=0.5+0.5*pow(19.0*q.x*q.y*(1.0-q.x)*(1.0-q.y),0.7);
  return col;
}


// License: MIT OR CC-BY-NC-4.0, author: mercury, found: https://mercury.sexy/hg_sdf/
float mod1(inout float p, float size) {
  float halfsize = size*0.5;
  float c = floor((p + halfsize)/size);
  p = mod(p + halfsize, size) - halfsize;
  return c;
}

// License: MIT, author: Inigo Quilez, found: https://iquilezles.org/articles/distfunctions2d
float vesica(vec2 p, float r, float d) {
  p = abs(p);
  float b = sqrt(r*r-d*d);
  return ((p.y-b)*d>p.x*b) ? length(p-vec2(0.0,b))
                           : length(p-vec2(-d,0.0))-r;
}

// License: MIT, author: Inigo Quilez, found: https://iquilezles.org/articles/distfunctions2d
float unevenCapsule(vec2 p, float r1, float r2, float h) {
  p.x = abs(p.x);
  float b = (r1-r2)/h;
  float a = sqrt(1.0-b*b);
  float k = dot(p,vec2(-b,a));
  if( k < 0.0 ) return length(p) - r1;
  if( k > a*h ) return length(p-vec2(0.0,h)) - r2;
  return dot(p, vec2(a,b) ) - r1;
}

// License: MIT, author: Inigo Quilez, found: https://iquilezles.org/articles/distfunctions2d
float parabola(vec2 pos, float wi, float he) {
  pos.x = abs(pos.x);
  float ik = wi*wi/he;
  float p = ik*(he-pos.y-0.5*ik)/3.0;
  float q = pos.x*ik*ik*0.25;
  float h = q*q - p*p*p;
  float r = sqrt(abs(h));
  float x = (h>0.0) ? 
      pow(q+r,1.0/3.0) - pow(abs(q-r),1.0/3.0)*sign(r-q) :
      2.0*cos(atan(r/q)/3.0)*sqrt(p);
  x = min(x,wi);
  return length(pos-vec2(x,he-x*x/ik)) * 
         sign(ik*(pos.y-he)+pos.x*pos.x);
}

// License: MIT, author: Inigo Quilez, found: https://iquilezles.org/articles/smin
float pmin(float a, float b, float k) {
  float h = clamp(0.5+0.5*(b-a)/k, 0.0, 1.0);
  return mix(b, a, h) - k*h*(1.0-h);
}

float pmax(float a, float b, float k) {
  return -pmin(-a, -b, k);
}

float segmenty(vec2 p, float off) {
  p.y = abs(p.y);
  p.y -= off;
  float d0 = abs(p.x);
  float d1 = length(p);
  return p.y > 0.0 ? d1 : d0;
}

vec2 toPolar(vec2 p) {
  return vec2(length(p), atan(p.y, p.x));
}

float circle(vec2 p, float r) {
  return length(p) - r;
}

float eye(vec2 p) {
  float a  = mix(0.0, 0.85, smoothstep(0.995, 1.0, cos(TAU*TIME/5.0)));
  const float b = 4.0;
  float rr = mix(1.6, b, a);
  float dd = mix(1.12, b, a);
  vec2 p0 = p;
  p0 = p0.yx;
  float d0 =  vesica(p0, rr, dd);
  float d5 = d0;
  vec2 p1 = p;
  p1.y -= 0.28;
  float d1 = circle(p1, 0.622);
  d1 = max(d1,d0);
  vec2 p2 = p;
  p2 -= vec2(-0.155, 0.35);
  float d2 = circle(p2, 0.065);
  vec2 p3 = p;
  p3.y -= 0.28;
  p3 = toPolar(p3);
  float n3 = mod1(p3.x, 0.05);
  float d3 = abs(p3.x)-0.0125*(1.0-length(p1));
  vec2 p4 = p;
  p4.y -= 0.28;
  float d4 = circle(p4, 0.285);
  d3 = max(d3,-d4);
  d1 = pmax(d1,-d2, 0.0125);
  d1 = max(d1,-d3);
  float t0 = abs(0.9*p.x);
  t0 *= t0;
  t0 *= t0;
  t0 *= t0;
  t0 = clamp(t0, 0.0, 1.0);
  d0 = abs(d0)-mix(0.0125, -0.0025, t0);
  float d = d0;
  d = pmin(d, d1, 0.0125);
  return d;
}

float hand(vec2 p) {
  p.x = abs(p.x);
  vec2 p0 = p;
  p0 -= vec2(0.0, 0.180+0.00);
  float d0 = segmenty(p0, 0.61)-0.1;
  vec2 p1 = p;
  p1 -= vec2(0.2, 0.125);
  float d1 = segmenty(p1,0.55)-0.09;
  vec2 p2 = p;
  p2 -= vec2(0.0, -0.38+0.3);
  p2.y = -p2.y;
  float d2 = unevenCapsule(p2, 0.3, 0.38, 0.3);
  vec2 p3 = p;
  p3 -= vec2(0.47, -0.31);
  float d3 = parabola(p3, 0.37, 0.5);
  vec2 p4 = p;
  p4 -= vec2(0.99, -0.4);
  float d4 = circle(p4, 0.61);
  d3 = max(d3, -d4);
  vec2 p5 = p;
  p5 -= vec2(0.0, -0.47);
  float d5 = vesica(p5.yx, 0.175, 0.1)-0.2;
  d0 = min(d0, d1);  
  d3 = p.y > -0.40 ? d3 : d2;
  float d = d3;
  d = min(d, d2);
  d = pmax(d, -(d0-0.01), 0.025);
  d = min(d, d0);
  float od = d;
  od = abs(od-0.02)-0.0075;
  d = min(d, od);
  d = pmin(d, d5, 0.01);
  return d;
}

float df(vec2 p) {
  float dh = hand(p);
  const float ze = 0.3;
  vec2 pe = p;
  pe -= vec2(0.0, -0.45);
  pe /= ze;
  float de = eye(pe);
  de *= ze;
  float d = dh;
  d = max(d, -de);
  return d;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  vec2 q = fragCoord/RESOLUTION.xy;
  vec2 p = -1.0 + 2.0 * q;
  p.x    *= RESOLUTION.x/RESOLUTION.y;
  float aa = 2.0/RESOLUTION.y;

  vec3 col = vec3(0.1*q.y);
  float d = df(p);
  col = mix(col, mix(vec3(1.0, 0.5, 0.5), vec3(.5, 0.55, 0.95), q.y), smoothstep(aa, -aa, d));
  col = postProcess(col, q);
  fragColor = vec4(col, 1.0);
}
