#define pi 3.14159
#define thc(a,b) tanh(a*cos(b))/tanh(a)

#define rot(a) mat2(cos(a),-sin(a),sin(a),cos(a))

float h21(vec2 a) {
    return fract(sin(dot(a.xy, vec2(12.9898, 78.233))) * 43758.5453123);
}

vec3 pal(in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d) {
    float k = 1. + 0.99 * cos(10. * length(d) * pi + iTime);
    return a + b*thc(k, 6.28318*(c*t+d) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;
    vec2 ouv = uv;
    float t = 0.25 * iTime;

    float v = log(length(uv));//0.75 * log(1. + uv.x);

    vec3 e = vec3(0.5);
    vec3 col = pal(v, e, e, e, 0.25 * vec3(0,1,2)/3.);

    float m = 0.;
    float n = 9.;
    for (float i = 0.; i < n; i++) {
        float io = 2. * pi * i / n;
        float l = m * iTime + pi * length(col) / 12.;
        uv *= rot(l);  
        uv = abs(uv);
        v = log(length(uv));
        v += h21(floor(1.5 * pi * vec2(atan(uv.x,uv.y), 0.125 * iTime - 0.2 * log(length(uv)))));
        col = pal(v + 0.25 * iTime + 1. * thc(10., iTime + 0.111 * io) * atan(uv.x,uv.y)/pi, e, e, e, 0.6 * col);
       // m += 0.08;
    }
    col = clamp(col, 0., 1.);
    col = mix(col, vec3(1), clamp(exp(0.5-12. * length(ouv)), 0., 1.) );
    fragColor = vec4(col,1.0);
}
