const vec3 lightDir = normalize(vec3(1,-1,1));

void renderParticle(in fxParticle p, in vec2 fragCoord, inout vec4 fragColor)
{   
    vec2 closest;
    p.pos.x *= iResolution.y / iResolution.x;
    float dist = sqrt(linePointDist2(p.pos, p.pos - p.vel, fragCoord, iResolution, closest));
    int maxParticles = min(int(iResolution.x * iResolution.y) / NUM_PARTICLE_DATA_TYPES, MAX_PARTICLES);

    float wd =  2./(min(iResolution.x, iResolution.y));

    float drawSize = min(PARTICLE_REPEL_SIZE, p.size);

    vec3 norm = normalize(vec3(p.pos-fragCoord, drawSize));
    float diffuse = dot(lightDir, norm)*0.7 + 0.3;

    vec3 reflection = reflect(lightDir, norm);
    float spec = -reflection.z;
    spec = spec > 0. ? pow(abs(spec), 15.0) : 0.;

    fragColor.xyz = mix(fragColor.xyz, p.color * diffuse + spec, 1.-smoothstep(drawSize - wd, drawSize + wd, dist));

    // Render neighbor lines
    if (keyDown(KEY_SHIFT))
    {
        for(int i = 0; i < 4; i++){
            ivec4 neighbors = p.neighbors[i];
            for (int j = 0; j < 4; ++j)
            {
                int cid = neighbors[j];
                if(cid==-1 || cid >= maxParticles || cid == 0) continue;

                vec2 otherPos = fxGetParticleData(cid, POS_VEL).xy;
                otherPos.x *= iResolution.y / iResolution.x;

                float distToLin = linePointDist2(p.pos, p.pos + 0.5 * (otherPos - p.pos), fragCoord, iResolution, closest);
                fragColor.xyz += iResolution.x* max(0.0, 0.001 - sqrt(distToLin)) / (0.0004);
            }
        }
    }
}

float computeSpecular(
    in float specularCoefficient,
    in float specularExponent,
    in vec3 nvNormal,
    in vec3 nvFragToLight,
    in vec3 nvFragToCam)
{
    vec3 blinnH = normalize(nvFragToLight + nvFragToCam);
    float valSpecular = pow(max(0.0, dot(nvNormal, blinnH)), specularExponent);
    valSpecular *= specularCoefficient;

    return valSpecular;
}

void mainImage( out vec4 fragColor, vec2 fragCoord )
{    
    // Simple vignette effect by Ippokratis
    // https://www.shadertoy.com/view/lsKSWR
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv *=  1.0 - uv.yx;   //vec2(1.0)- uv.yx; -> 1.-u.yx; Thanks FabriceNeyret 
    float vig = uv.x*uv.y * 15.0; // multiply with sth for intensity
    vig = pow(vig, 0.25); // change pow for modifying the extend of the  vignette
    //fragColor = vec4(vig*.1, vig*.1, vig, 1);
    fragColor = vec4(0);

  	vec2 p = fragCoord/iResolution.xy;

    //get the id's of the 4 particles that (should be) closest.
    //the 4 ids are stored in .x, .y, .z, .w
    ivec4 ids = fxGetClosest(ivec2(fragCoord));

    //draw the particles
    for(int i = 0; i < 4; i++)
    {
        //get the particles position
        int id = ids[i];
        fxParticle particle = fxGetParticle(id);

        renderParticle(particle, p, fragColor);
    }

    fxState state = fxGetState();
    vec2 steelCoord = vec2(fragCoord.x * 2.0, (fragCoord.y) * 0.004);
    vec2 adjusted = (p - 0.5) * vec2(iResolution.x / iResolution.y, 1.);
    if (length(adjusted) < 0.5/1.2 + 0.02)
    {
        float a = atan(adjusted.y, adjusted.x) + state.rotation;
        steelCoord = vec2(a*400., length(adjusted)*20.);
    }

    float wallDist = distanceFromWalls(p * vec2(iResolution.x / iResolution.y, 1.), iResolution, state.rotation);

    vec3 steelNorm = texture(iChannel2, steelCoord/iResolution.xy).xyz;
    steelNorm.xz += getNormalFromWalls(p * vec2(iResolution.x / iResolution.y, 1.), iResolution, state.rotation) * (1.0 - smoothstep(0.007, 0.01, -wallDist));

    steelNorm = normalize(steelNorm);
    float steelSpec = computeSpecular(0.8, 15.0, steelNorm, lightDir, vec3(0.0, 1.75-p.x, 0.5 - p.y));
    vec4 steel = vec4(vec3(steelSpec), 1.0) * 0.4 + 0.2;

    fragColor = sqrt(mix(fragColor, steel, vec4(clamp(-wallDist*iResolution.y, 0.0, 1.0))));
}

//amount of particles
const int MAX_PARTICLES = 1000; 
const float PARTICLE_REPEL_SIZE = 0.01;

const float PI = 3.141592653589793;

uvec4 hash(uvec4 x){
    x = ((x >> 16u) ^ x.yzwx) * 0x45d9f3bu;
    x = ((x >> 16u) ^ x.yzwx) * 0x45d9f3bu;
    x = ((x >> 16u) ^ x.yzwx) * 0x45d9f3bu;
    x = ((x >> 16u) ^ x.yzwx) * 0x45d9f3bu;
    //x = (x >> 16u) ^ x;
    return x;
}

//hashing noise by IQ
float hash( int k ) {
    uint n = uint(k);
	n = (n << 13U) ^ n;
    n = n * (n * n * 15731U + 789221U) + 1376312589U;
    return uintBitsToFloat( (n>>9U) | 0x3f800000U ) - 1.0;
}

vec2 world2screenInternal(in vec2 p, in vec2 resolution)
{
    return p * resolution;
}

#define world2screen(X) world2screenInternal(X, iResolution.xy)

vec2 screen2worldInternal(in vec2 p, in vec2 resolution)
{
    return (p / resolution);
}

#define screen2world(X) screen2worldInternal(X, iResolution.xy)

float linePointDist2(in vec2 newPos, in vec2 oldPos, in vec2 fragCoord, in vec3 resolution, out vec2 closest)
{
    vec2 pDelta = (fragCoord - oldPos);
    vec2 delta = newPos - oldPos;
    float deltaLen2 = dot(delta, delta);

    // Find the closest point on the line segment from old to new
    closest;
    if (deltaLen2 > 1e-10)
    {
        float deltaInvSqrt = inversesqrt(deltaLen2);
        vec2 deltaNorm = delta * deltaInvSqrt;
        closest = oldPos + deltaNorm * max(0.0, min(1.0 / deltaInvSqrt, dot(deltaNorm, pDelta)));
    }
    else
    {
        closest = oldPos;
    }

    // Distance to closest point on line segment
    vec2 closestDelta = closest - fragCoord;
    closestDelta *= resolution.xy / resolution.y;
    return dot(closestDelta, closestDelta);
}

float sdBox( in vec2 p, in vec2 b )
{
    vec2 d = abs(p)-b;
    return length(max(d,0.0)) + min(max(d.x,d.y),0.0);
}

mat2 rot2(float a)
{
    vec2 sc = vec2(sin(a), cos(a));
    return mat2(sc.x, sc.y, sc.y, -sc.x);
}

float distanceFromWalls(vec2 point, vec3 resolution, float time)
{
    vec2 c = (point - vec2(.5*resolution.x/resolution.y,  .5))*1.2;
    float minDist = 1e30;
    minDist = min(minDist, .5 - length(c));
    const int NUM_PADDLES = 10;
    for (int i = 0; i < NUM_PADDLES; ++i)
    {
        minDist = min(minDist, sdBox((rot2(time + float(i) *2.* PI / float(NUM_PADDLES))*c + vec2(0.0, 0.5)), vec2(0.01, 0.1)));
    }
    return minDist;
}

vec2 getNormalFromWalls( vec2 point, vec3 resolution, float time )
{
	vec2 tinyChangeX = vec2( 0.001, 0.0 );
    vec2 tinyChangeY = vec2( 0.0 , 0.001 );

   	float upTinyChangeInX   = distanceFromWalls( point + tinyChangeX, resolution, time ); 
    float downTinyChangeInX = distanceFromWalls( point - tinyChangeX, resolution, time ); 

    float tinyChangeInX = upTinyChangeInX - downTinyChangeInX;


    float upTinyChangeInY   = distanceFromWalls( point + tinyChangeY, resolution, time ); 
    float downTinyChangeInY = distanceFromWalls( point - tinyChangeY, resolution, time ); 

    float tinyChangeInY = upTinyChangeInY - downTinyChangeInY;


	vec2 normal = vec2(
         			tinyChangeInX,
        			tinyChangeInY
    	 		  );

	return normalize(normal);
}

//returns the ids of the four closest particles from the input
ivec4 fxGetClosestInternal(sampler2D sampler, ivec2 xy)
{
    return ivec4(texelFetch(sampler, xy, 0));
}

#define fxGetClosest(X) fxGetClosestInternal(iChannel1, X)

#define UL_NEIGHBORS 0
#define UR_NEIGHBORS 1
#define LL_NEIGHBORS 2
#define LR_NEIGHBORS 3
#define POS_VEL 4
#define SIZE 5
#define COLOR 6
#define NUM_PARTICLE_DATA_TYPES 7

//returns the location of the particle within the particle buffer corresponding with the input id 
ivec2 fxLocFromIDInternal(int width, int id, int dataType)
{
    int index = id * NUM_PARTICLE_DATA_TYPES + dataType;
    return ivec2( index % width, index / width);
}

#define fxLocFromID(X, Y) fxLocFromIDInternal(int(iResolution.x), X, Y)

struct fxParticle
{
    vec2 pos;
    vec2 vel;

    ivec4 neighbors[4];
    float size;
    float closestDist;
    vec3 color;
};

//get the particle corresponding to the input id
fxParticle fxGetParticleInternal(sampler2D sampler, int resolutionWidth, int id)
{
    vec4 particleData0 = texelFetch(sampler, fxLocFromIDInternal(resolutionWidth, id, UL_NEIGHBORS), 0);
    vec4 particleData1 = texelFetch(sampler, fxLocFromIDInternal(resolutionWidth, id, UR_NEIGHBORS), 0);
    vec4 particleData2 = texelFetch(sampler, fxLocFromIDInternal(resolutionWidth, id, LL_NEIGHBORS), 0);
    vec4 particleData3 = texelFetch(sampler, fxLocFromIDInternal(resolutionWidth, id, LR_NEIGHBORS), 0);
    vec4 particleData4 = texelFetch(sampler, fxLocFromIDInternal(resolutionWidth, id, POS_VEL), 0);
    vec4 particleData5 = texelFetch(sampler, fxLocFromIDInternal(resolutionWidth, id, SIZE), 0);
    vec4 particleData6 = texelFetch(sampler, fxLocFromIDInternal(resolutionWidth, id, COLOR), 0);

    fxParticle particle;
    particle.neighbors[0] = ivec4(particleData0);
    particle.neighbors[1] = ivec4(particleData1);
    particle.neighbors[2] = ivec4(particleData2);
    particle.neighbors[3] = ivec4(particleData3);
    particle.pos = particleData4.xy;
    particle.vel = particleData4.zw;
    particle.size = particleData5.x;
    particle.closestDist = particleData5.y;
    particle.color = particleData6.xyz;

    return particle;
}


vec4 fxSaveParticle(fxParticle p, int dataType)
{    
    switch(dataType)
    {
    case UL_NEIGHBORS:
        return vec4(p.neighbors[0]);
    case UR_NEIGHBORS:
        return vec4(p.neighbors[1]);
    case LL_NEIGHBORS:
        return vec4(p.neighbors[2]);
    case LR_NEIGHBORS:
        return vec4(p.neighbors[3]);
    case POS_VEL:  
        return vec4(p.pos, p.vel);
    case SIZE:
        return vec4(p.size, p.closestDist, 0, 0);
    case COLOR:
        return vec4(p.color, 0);
    }
}

#define fxGetParticle(X) fxGetParticleInternal(iChannel0, int(iResolution.x), X)

vec4 fxGetParticleDataInternal(sampler2D sampler, int resolutionWidth, int id, int dataType)
{
    return texelFetch(sampler, fxLocFromIDInternal(resolutionWidth, id, POS_VEL), 0);
}

#define fxGetParticleData(X, Y) fxGetParticleDataInternal(iChannel0, int(iResolution.x), X, Y)

struct fxState
{
    float resolution;
    float rotation;
    float velocity;
    float mouse;
};

fxState fxGetStateInternal(sampler2D sampler)
{
    vec4 data = texelFetch(sampler, ivec2(0), 0);
    fxState state;
    state.resolution = data.x;
    state.rotation = data.y;
    state.velocity = data.z;
    state.mouse = data.w;
    return state;
}

#define fxGetState() fxGetStateInternal(iChannel1)

vec4 fxPutState(fxState state)
{
    return vec4(state.resolution, state.rotation, state.velocity, state.mouse);
}

float SPHKernel (float x)
{
    if (x < 1.0)
        return 4.0 * cos(x*PI) + cos((x + x) * PI) + 3.0;
    else
        return 0.0;
}

float SPHgradKernel (float x)
{
    if (x < 4.0)
    {
        float xx = x*x;
        float xxx = xx*x;
        float xxxx = xxx*x;
        return 0.000 + 3.333 * x + -3.167 * xx + 0.917 * xxx + -0.083 * xxxx;
    }
    else
        return 0.0;
}

float length2(vec2 v)
{
    return dot(v, v);
}

float square(float x)
{
    return x * x;
}

#define keyClick(ascii)   ( texelFetch(iChannel3,ivec2(ascii,1),0).x > 0.)
#define keyDown(ascii)    ( texelFetch(iChannel3,ivec2(ascii,0),0).x > 0.)

#define KEY_SHIFT 16
#define KEY_SPACE 32
#define KEY_P 80

void insertion_sort(inout ivec4 i, inout vec4 d, int i_, float d_){	
    if(any(equal(ivec4(i_),i))) return;
    if     (d_ < d[0])             
        i = ivec4(i_,i.xyz),    d = vec4(d_,d.xyz);
    else if(d_ < d[1])             
        i = ivec4(i.x,i_,i.yz), d = vec4(d.x,d_,d.yz);
    else if(d_ < d[2])            
        i = ivec4(i.xy,i_,i.z), d = vec4(d.xy,d_,d.z);
    else if(d_ < d[3])           
        i = ivec4(i.xyz,i_),    d = vec4(d.xyz,d_);
}

const vec2 GRAVITY = vec2(0.0000, -0.00001);
const float PARTICLE_REPEL = 0.0000002;
const float VISCOSITY = 0.0000001;
const float COLLISION_BOUNCE = 0.5;
const float POSITION_CORRECTION = 0.2;

void sort0(inout ivec4 bestIds, inout vec4 bestDists, int id, int searchId, int dataType, in vec2 myPos);

#define RGB(R, G, B) vec3(float(R), float(G), float(B)) / 255.0

void mainImage( out vec4 fragColor, vec2 fragCoord ){
    ivec2 iFragCoord = ivec2(fragCoord);

    //we only simulate PARTICLES amount of particles
    int maxParticles = min(int(iResolution.x * iResolution.y) / NUM_PARTICLE_DATA_TYPES, MAX_PARTICLES);
    int index = iFragCoord.x + iFragCoord.y*int(iResolution.x);
    int id = index / NUM_PARTICLE_DATA_TYPES;
    int dataType = index - id * NUM_PARTICLE_DATA_TYPES;
    if(id>=maxParticles) return;
    if (id == 0) return;

    fxParticle data = fxGetParticle(id);
    fxState state = fxGetState();

    if (dataType >= POS_VEL)
    {
        if (iFrame == 0 || state.resolution < 0.)
        {
            //pick a "random" starting position
            float particlesPerRow = sqrt(float(maxParticles));
            float i = float(id % int(particlesPerRow));
            float j = float(id / int(particlesPerRow)) + float(id & 1) * 0.5;

            data.pos = (vec2((i + 0.5*(mod(j, 2.0))) / particlesPerRow, j / particlesPerRow) * vec2(0.4, 0.6) + vec2(0.3, 0.2)) * vec2(iResolution.x / iResolution.y, 1.0);
            data.vel = vec2(0);
            data.size = 1e6;

            switch(int(5.* i / particlesPerRow))
            {
                case 0: data.color = RGB(255,46,0); break;
                case 1: data.color = RGB(221,150,2); break;
                case 2: data.color = RGB(4,150,7); break;
                case 3: data.color = RGB(48,11,53); break;
                case 4: data.color = RGB(140,4,12); break;           
            }
         }
        else
        {
            vec2 force = GRAVITY;
            float minDist = 1e6;
            float minCirc = 1e6;

            // Compute neighborhood density and density gradient, and init solver particles
            for(int i = 0; i < 4; i++){
                ivec4 neighbors = data.neighbors[i];
                for (int j = 0; j < 4; ++j)
                {
                    int cid = neighbors[j];
                    if(cid==id || cid==-1 || cid == 0 || cid >= maxParticles) continue;

                    fxParticle n = fxGetParticle(cid);

                    vec2 deltaPos = data.pos - n.pos;
                    float dist = length(deltaPos) + 0.0001;
                    vec2 dir = deltaPos / dist;

                    // Absorb some velocity from nearby particles
                    force += VISCOSITY * n.vel / square(dist);

                    // Apply SPH attract/repel force
                    force -= PARTICLE_REPEL * dir * SPHgradKernel(dist / (20.*PARTICLE_REPEL_SIZE));

                    // Repel small neighbors (restores density)
                    if (data.size < PARTICLE_REPEL_SIZE*.8 && n.size < data.size && n.size > 1e-6 && data.size > 1e-6 && iFrame > 10)
                    {
                        force += 9.*(PARTICLE_REPEL_SIZE - data.size) * dir * (data.size - n.size);
                    }

                    // Collide with neighbor
                    if (dist < PARTICLE_REPEL_SIZE * 2.)
                    {
                        data.pos -= dir * (dist - PARTICLE_REPEL_SIZE * 2.) * POSITION_CORRECTION;
                        float normalVel = dot(dir, data.vel - n.vel);
                        if (normalVel < 0.)
                        {
                            data.vel -= COLLISION_BOUNCE * dir * normalVel;
                        }
                    }

                    // Record some data for size computation
                    minDist = min(minDist, dist*0.5);
                    minCirc = min(minCirc, max(dist*0.5, 0.5*(dist - n.size)));
                }
            }       

            // Apply force
            data.vel = data.vel + force;

            // Record computed size
            data.closestDist = minDist;
            data.size = minCirc;

            // Boundary
            float distToScene = distanceFromWalls(data.pos, iResolution, state.rotation);
            float distToSceneOld = distanceFromWalls(data.pos, iResolution, state.rotation - state.velocity);
            vec2 distNormal = getNormalFromWalls(data.pos, iResolution, state.rotation);

            if (distToScene < PARTICLE_REPEL_SIZE)
            {
                data.pos -= 1.0 * distNormal * (distToScene - PARTICLE_REPEL_SIZE);
                data.vel -= 1.5 * distNormal * min(0., dot(distNormal, data.vel));
                data.vel += 0.0005 * distNormal;
            }

            // Damping
            data.vel -= data.vel * 0.01;

            // Clamping
            float maxSpeed = 0.05; // Dictated by voronoi update speed
            float velLength2 = length2(data.vel);
            if (velLength2 > maxSpeed * maxSpeed)
            {
                data.vel *= inversesqrt(velLength2) * maxSpeed;
            }

            // Integrate position
            data.pos = data.pos + data.vel;
            data.pos = clamp(data.pos, vec2(-iResolution.x / iResolution.y, -1.0), vec2(iResolution.x / iResolution.y, 1.0));
        }
    }
    else
    {
        // Nearest particle sort inspired by michael0884's Super SPH: https://www.shadertoy.com/view/tdXBRf
        //sort neighbors and neighbor neighbors
        ivec4 closest = fxGetClosest(ivec2(world2screen(data.pos * vec2(iResolution.y / iResolution.x, 1.0))));
        ivec4 bestIds = ivec4(-1);
        vec4 bestDists = vec4(1e6);

        for (int i = 0; i < 4; ++i)
        {
            sort0(bestIds, bestDists, id, int(data.neighbors[0][i]), dataType, data.pos);  //sort this
            sort0(bestIds, bestDists, id, int(data.neighbors[1][i]), dataType, data.pos);  //sort this
            sort0(bestIds, bestDists, id, int(data.neighbors[2][i]), dataType, data.pos);  //sort this
            sort0(bestIds, bestDists, id, int(data.neighbors[3][i]), dataType, data.pos);  //sort this
            sort0(bestIds, bestDists, id, int(closest[i]), dataType, data.pos);  //sort this
        }

        int searchIterations = 10;
        for(int k = 0; k < searchIterations; k++){
            //random hash. We should make sure that two pixels in the same frame never make the same hash!
            float h = hash(
                iFragCoord.x + 
                iFragCoord.y*int(iResolution.x) + 
                iFrame*int(iResolution.x*iResolution.y) +
                k
            );
            //int maxParticles = min(iFragCoord.x * iFragCoord.y / NUM_PARTICLE_DATA_TYPES, MAX_PARTICLES);
            //pick random id of particle
            int p = int(mod(h*34534.0, float(MAX_PARTICLES)));
            sort0(bestIds, bestDists, id, int(p), dataType, data.pos);  //sort this
        }
        fragColor = vec4(bestIds);
        return;
    }

    fragColor = fxSaveParticle(data, dataType);
}

bool iscoincidence(in ivec4 bestIds, int currentId, int id)
{
    return id < 0 || id >= MAX_PARTICLES ||
      	id == currentId ||
        any(equal(bestIds,ivec4(id)));
}

void sort0(inout ivec4 bestIds, inout vec4 bestDists, int currentId, int searchId, int dataType, in vec2 myPos)
{
    if(iscoincidence(bestIds, currentId, searchId)) return; //particle already sorted

    vec2 nbX = fxGetParticleData(searchId, POS_VEL).xy; 

    vec2 dx = nbX - myPos;
    int dir = int(2.*(atan(dx.y, dx.x)+PI)/PI); 

    if(dir != dataType) return; //not in this sector

    float t = length2(dx);

    insertion_sort(bestIds, bestDists, searchId, t);
}

// Gijs's Basic : Voronoi Tracking: https://www.shadertoy.com/view/WltSz7

// Voronoi Buffer
// every pixel stores the 4 closest particles to it
// every frame this data is shared between neighbours

float distance2Particle(int id, vec2 fragCoord){
    if(id==-1) return 1e20;
    vec2 delta = (fxGetParticleData(id, POS_VEL).xy * vec2(iResolution.y / iResolution.x, 1.0))-fragCoord;
    return dot(delta, delta);
}

void mainImage( out vec4 fragColor, vec2 fragCoord ){
   	ivec2 iFragCoord = ivec2(fragCoord);

    if (iFragCoord == ivec2(0))
    {
        fxState state = fxGetState();

        if (iFrame == 0 || iResolution.x * iResolution.y != abs(state.resolution) || keyDown(KEY_SPACE))
        {
            state.resolution = -iResolution.x * iResolution.y;
            state.velocity = state.rotation = 0.0;
        }
        else
        {
            state.resolution = abs(state.resolution);

            // Apply acceleration and damping
            state.velocity += 0.0001;
            state.velocity *= 0.97;

            // Handle mouse input
            if (iMouse.z > 0.)
            {
                if (iMouse.w > 0.)
                {
                    state.velocity = 0.;
                    state.mouse = iMouse.x + iMouse.y;
                }
                else
                {
                    state.velocity = clamp((state.mouse - iMouse.x - iMouse.y) / iResolution.x, -0.1, 0.1);
                    state.mouse = iMouse.x + iMouse.y;
                }
            }

            // Integrate rotation
            state.rotation += state.velocity;
        }

        fragColor = fxPutState(state);
        return;
    }

    //in this vector the four new closest particles' ids will be stored
    ivec4 new = ivec4(-1);
    //in this vector the distance to these particles will be stored 
    vec4 dis = vec4(1e6);

    ivec4 old   = fxGetClosest( iFragCoord );      

    for(int j=0; j<4; j++){
        int id = old[j];
        float dis2 = distance2Particle(id, screen2world(fragCoord));
        insertion_sort( new, dis, id, dis2 );
    }

    // Search randomly in the nearest 15x15 neighbors instead of just
    // the next-door neighbors. Seems to allow faster particle movement
    uint searchRange = 15u;
    uint searchCount = 16u;

    for(uint i=0u; i<searchCount; ++i)
    {
        uvec4 h0 = hash(uvec4(fragCoord, iFrame, iResolution.x) * i);

        ivec4 old   = fxGetClosest( iFragCoord + ivec2( h0.xy % searchRange - searchRange / 2u) );      
        for(int j=0; j<2; j++){
            int id = old[j];
            float dis2 = distance2Particle(id, screen2world(fragCoord));
            insertion_sort( new, dis, id, dis2 );
        }        
    }

    int searchIterations = 1;
    if (iFrame < 5)
    {
        searchIterations = 10;
    }
    for(int k = 0; k < searchIterations; k++)
    {
        //random hash. We should make sure that two pixels in the same frame never make the same hash!
        float h = hash(
            iFragCoord.x + 
            iFragCoord.y*int(iResolution.x) + 
            iFrame*int(iResolution.x*iResolution.y) +
            k
        );
        //pick random id of particle
        int p = int(h*float(MAX_PARTICLES));
        insertion_sort(new, dis, p, distance2Particle(p, screen2world(fragCoord)));
    }

    fragColor = vec4(new); 
}
