        vec3 nor=GetNormal(p);
        vec3 colobj;
        colobj=GetMaterial( p, nor, ro, rd,  int(hit.y),col);
        vec3 result;
         float d1=HacerCuadroRnd(p.xz*0.25);
         if(d1>0.6)
             d1=1.0;
         result= lightingv3(nor, p,light_pos1, rd,ro,colobj,hit.x)*light_color1*d1;
        result+= lightingv3(nor, p,light_pos2,rd, ro,colobj,hit.x)*light_color2*d1;
        col= result/2.0;
        col= (ACESFilm(col)+linear2srgb(col)+col+ exposureToneMapping(3.0, col))/4.0 ;        
    }
