#define PI 3.1415926535

/// 二生一的随机函数
float hash(vec2 uv)
{
    return fract(sin(dot(uv, vec2(154.45, 64.548))) * 124134.54)  ;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // 归一化纹理坐标 [-1, 1]
    vec2 uv = (2.*fragCoord - iResolution.xy) / iResolution.y;
    // 通过随机函数生成星空，通过幂函数放大对比度
    float stars = pow(hash(uv), 200.);

    vec3 colorM = vec3(0.,0.,0.);
    vec3 colorA = vec3(0.5,0.3,0.9);
    vec3 colorB = vec3(0.0,0.0,0.0);
    //选择范围插值改变底色
        colorM = mix(colorA, colorB, sin((uv.y+1.0)*PI/4.));


    // 从上到下的渐变，并着色
    vec3 night = vec3(uv.y * 0.5 + 0.5) * vec3(stars) + colorM;




    fragColor = vec4(night, 1.0);
}
