vec2 GetDist(vec3 p  ) 
{	
    vec2 res= vec2(9999.0, -1.0);  
    //vec3 p0=p;

    // XY plane repeat scale.
    const vec2 sc = vec2(9, 12);
    // Cell ID.
    // I put the ".5" outside the floor function, but this way 
    // matches your original.
    vec2 ip = floor(p.xy/sc + .5);
    p.xy -= ip*sc; // Same as the two lines below.
    //p.x= opRep1D(p.x, 9.0);
    //p.y= opRep1D(p.y,12.0);


    float d1=sdBox( p, vec3(5.0,6.0,2.0));
    float d2a=sdBox( p-vec3(0.0,0.0,0.0), vec3(4.0,5.0,1.0));
    float d2b=sdBox( p-vec3(0.0,0.0,-1.0), vec3(4.0,5.0,2.0));

    float rnd = hash21(ip);
    // Rotate local cell coordinates by random multiples of PI/2.
    vec3 q = vec3(rot2(floor(rnd*32.)*3.14159/2.)*p.xy, p.z); 
    float d3a=sdBox( q-vec3(-1.0,0.0,0.0), vec3(1.0,2.0,3.0));
    float d3b=sdBox( q-vec3(1.5,2.0,0.0), vec3(1.0,1.0,3.0));

    d1=differenceSDF(d1,d2b);
    res =opU2(res, vec2(d1,100.0));
    res =opU2(res, vec2(d2a,101.0));
    res =opU2(res, vec2(d3a,100.0));
    res =opU2(res, vec2(d3b,100.0));
    return res;
}


