#define pi 3.14159

float sfloor(float a, float b) {
    return floor(b) + 0.5 + 0.5 * tanh(a * (fract(b) - 0.5)) / tanh(0.5 * a);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;

    float r = 0.04;
    float or = 0.49 - r;

    // sfloor time
    float t = 0.5 * pi * sfloor(5., 0.5 * iTime);
    vec2 p = or * vec2(cos(t), sin(t));  
    float d = length(uv - p);
    float s = step(d, r);

    // Ordinary time
    float t2 = 0.25 * pi * iTime;
    vec2 p2 = (or-2.*r) * vec2(cos(t2), sin(t2));  
    float d2 = length(uv - p2);
    float s2 = step(d2, r);

    // Squareroot two sfloor times
    float t3 = 0.5 * pi * sfloor(50., 0.5 * iTime);
    float t4 = 0.5 * pi * sfloor(10., 0.5 * iTime);
    t3 = sqrt(0.5 * (t3 * t3 + t4 * t4));
    vec2 p3 = (or-4.*r) * vec2(cos(t3),sin(t3));
    float d3 = length(uv - p3);
    float s3 = step(d3, r);   

    // sfloor + bounce
    float f = 2. * fract(0.5*iTime) - 1.;
    t4 -= (pi/2.) * pow(f, 8.);
    vec2 p4 = (or-6.*r) * vec2(cos(t4),sin(t4));
    float d4 = length(uv - p4);
    float s4 = step(d4, r);

    vec3 col = vec3(s,s2,s3) + s4;

    fragColor = vec4(col,1.0);
}
