// Hash from "Hash without Sine" by Dave_Hoskins (https://www.shadertoy.com/view/4djSRW)
float Hash21(in vec2 p) {
	vec3 p3 = fract(p.xyx * vec3(0.1031, 0.103, 0.0973));
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.xx + p3.yz) * p3.zy).x;
}

vec2 sdTruchet(in vec2 p) {
    vec2 cId = floor(p);
    vec2 cUv = fract(p) - 0.5;

    cUv.x *= sign(Hash21(cId) - 0.5);
    cUv -= cUv.x < -cUv.y ? -0.5 : 0.5;

    float t = atan(cUv.y, cUv.x);
    t *= sign(mod(cId.x + cId.y, 2.0) - 0.5);

    return vec2(abs(length(cUv) - 0.5), t);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 center = 0.5 * iResolution.xy;
    vec2 uv = (fragCoord - center) / iResolution.y * 4.0 + 0.25 * iTime;

    vec2 truchet = sdTruchet(uv);
    float d = truchet.x - 0.1;

    // Nice colors from iq
    vec3 color = 1.0 - sign(d) * vec3(0.1, 0.4, 0.7);
	color *= 1.0 - exp(-4.0 * abs(d));
	color *= 0.8 + 0.2 * cos(140.0 * d);
	color = mix(color, vec3(1.0), 1.0 - smoothstep(0.0, 0.015, abs(d) * 0.6));

    if (d < 0.02) {
        truchet.y -= 0.5 * iTime;
        float fade = 0.5 + 0.5 * sin(iTime);
        float vertStripes = abs(mod(d + 0.0333, 0.0666) - 0.0333);
        float horiStripes = abs(mod(truchet.y + 0.1, 0.2) - 0.1) * 0.5;
        color += fade * smoothstep(0.03, 0.0, min(vertStripes, horiStripes));
    }

    fragColor = vec4(color, 1.0);
}
