#define R iResolution.xy
#define T(U) texture(iChannel0,U/R)//.xywz

void mainImage( out vec4 C, in vec2 U )
{   

    float m = 4.*min(T(U).w, 0.5);//masse
    float v = length(T(U).xy)/1.;//velocity
    float p = T(U).z/10.;//pressure

    C = m*(vec4(1,1.2,2,1));

    if (U.x < 0.2*R.x&&U.y < 0.5*R.y) C = vec4(0.5);
    if (U.x < 0.4*R.x&&U.y < 0.3*R.y) C = vec4(0.5);

}

#define dt .1
#define g 25.
#define k 20.
#define l 15.
#define nu 0.1
#define mu 0.1
#define kappa .0

#define t iTime
#define R iResolution.xy

#define T(p) texture(iChannel0,(p)/iResolution.xy)

void mainImage( out vec4 Q, in vec2 U )
{   

    vec4 a = T(U+vec2(1,0)),b = T(U+vec2(0,1)),c = T(U+vec2(-1,0)),d = T(U+vec2(0,-1)),e=.25*(a+b+c+d);

    vec2 vu = T(U-dt*e.xy*.5).xy;//advection vector estimate
    Q = T(U-dt*vu);//advection

 	vec2 gp = vec2(a.z-c.z,b.z-d.z);//pressure gradient
    vec2 gw = vec2(a.w-c.w,b.w-d.w);//density gradient
    float div = (a.x-c.x+b.y-d.y);//divergence


    Q.xy -= dt *(k*gp + Q.w*l*gw + Q.w*vec2(0.,g));//forces
    Q.z = e.z - 0.025*div;//pressure
    Q -= dt * vec4(nu,nu,mu,kappa) * 4.*(Q-e);//diffusion

    //Boundaries
    if (iFrame < 1) Q = vec4(0,0,1,0);
    if (U.x < 1.||U.y < 1.) Q.xy *= 0.;
    if (R.x-U.x < 1.||R.y-U.y < 1.) Q.w *= 0.;
    if (U.x < 0.2*R.x&&U.y < 0.5*R.y) Q.xy *= 0.;
    if (U.x < 0.4*R.x&&U.y < 0.3*R.y) Q.xy *= 0.;
 	if (length(U-vec2(0,0.9*R.y)) < 8.) {Q.xy= Q.xy*.5+.5*vec2(1.+0.4*sin(1.5*t),0); Q.w = 1.;};

    //click mouse to add a circular obstacle
    if(length(iMouse.xy - U) < 20. && iMouse.z > 0.5){
        Q.xyw = vec3(0.);
    }


    Q = clamp(Q, vec4(-15,-15,0,0), vec4(15,15,15,15));
}
