vec3 GetDist(vec3 p  ) 
{	
	vec3 res;
    vec3 pp;
    res = vec3(9999.0, -1.0,-1.0);

	float planeDist1 = p.y+0.0;  //piso inf
    float planeDist2 = 40.0-p.y;  //piso sup
    float planeDist3 = p.x+20.0; //pared izq
    float planeDist4 = 20.0-p.x;  //pared der
    float planeDist5 = -p.z+30.0;  //pared frente
    float planeDist6 = p.z+30.0;  //pared atras

    res =opU3(res, vec3(planeDist1,COLOR_NO,4)); //inf
    res =opU3(res, vec3(planeDist2,COLOR_NO,4)); 
    res =opU3(res, vec3(planeDist3,COLOR_NO,6)); 
    res =opU3(res, vec3(planeDist4,COLOR_NO,6)); 
  	res =opU3(res, vec3(planeDist5,COLOR_NO,3)); 
    res =opU3(res, vec3(planeDist6,COLOR_NO,3)); 

    //res =opU3(res, vec3(1.0,0.0,MATERIAL_NO)); 
    //return (dist, id_color, id_material)
    return res;
}

///-----------------------------------------
vec3 LightShading(vec3 Normal,vec3 toLight,vec3 toEye,vec3 color)
{
    vec3 toReflectedLight=reflect(-toLight, Normal);
    vec3 diffuse = max(0.,dot(Normal,-toLight))*color;
    //vec3 specular = pow(max(0.,dot(Normal,normalize(-toLight-V))),100.)*vec3(1.,1.,1.); 
    float specularf=max(dot(toReflectedLight, toEye),0.0);
    specularf=pow(specularf, 100.0);
    vec3 specular =specularf*vec3(1.0);
    return diffuse + specular;
}
//------------------------------------------------
vec3 GetNormal(vec3 p)
{
	float d = GetDist(p).x;
    vec2 e = vec2(.001, 0);
    vec3 n = d - vec3(
        GetDist(p-e.xyy).x,
        GetDist(p-e.yxy).x,
        GetDist(p-e.yyx).x);
    return normalize(n);
}
//---------actualizacion por Shane---28-may-2020    ...gracias
float RayMarch(vec3 ro, vec3 rd){
    float dO = 0.; 
    vec3 dS=vec3(9999.0,-1.0,-1.0);
    float marchCount = 0.0;
    vec3 p;
    for(int i=0; i<MAX_STEPS; i++) 
    {
    	p = ro + rd*dO;
        dS = GetDist(p);
        if(dO>MAX_DIST || abs(dS.x)<MIN_DIST) break;
        dO += dS.x;
        marchCount++;
    }

    mObj.dist = dO;
    mObj.id_color = dS.y;
    mObj.marchCount=marchCount;
    mObj.id_material=dS.z;
    mObj.normal=GetNormal(p);
    return dO;
}

//---------------------------------------------------
float GetShadow(vec3 p, vec3 plig)
{
    vec3 lightPos = plig;
    vec3 l = normalize(lightPos-p);
    vec3 n = GetNormal(p);
    float dif = clamp(dot(n, l), 0., 1.);
    float d = RayMarch(p+n*MIN_DIST*2., l );
    if(d<length(lightPos-p)) dif *= .1;
    return dif;
}
