void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
   mObj.uv=uv;
    float t;
    t=mod(iTime*1.0,360.0);
    itime=t;
	mObj.blnShadow=false;

 	light_pos1= vec3(10.0, 6.0, 10.0 );  light_color1 = vec3( 1.0 );
 	light_pos2= vec3( -5.0, 6.0, -5.0 ); light_color2 = vec3( 1.0, 1.0, 1.0 );



     ///--------------- por spalmer rotacion orbital
    ///---------------
    vec2 R = iResolution.xy;
    vec2 M = iMouse.xy;
     M.y = M.y+1.0;
    vec2 q = StoQ(   fragCoord, R);
    vec2 m = StoQ(M.xy, R);
    if (length(M.xy) <= 1.0)

        m = vec2(.2 + .1*iTime,-.015); // unattended, mouse in bottom left corner?


    vec3 object_pos = vec3(0., radius, 0.);

    vec3 camera_dir = OrbitCamera(m);
    vec3 camera_pos = object_pos - orbit_scale * radius * camera_dir; // LookAt



    camera_pos.y = max(camera_pos.y, 1e-2); // do not go beneath ground
    camera_dir = normalize(object_pos - camera_pos); // redo LookAt after clamp


    mat3 cam = CameraMatrix(camera_dir);
    const float hfovy = PI/6.;
    vec3 vd = normalize(vec3(q, 1./sin(hfovy)));
    vec3 ro = camera_pos;
    vec3 rd = normalize(cam * vd); // view ray
    //-------------------


    light_pos1+=ro;
    light_pos2+=ro;


    vec3 col = vec3(0);
    TObj Obj;
    mObj.rd=rd;
    mObj.ro=ro;

    float d = RayMarch(ro, rd);
    Obj=mObj;
    vec3 p = (ro + rd * d ); 

    mObj.p=p;
    mObj.dist =d;
    vec3 nor=mObj.normal;

    vec3 colobj;
    colobj=GetColorYMaterial( p, nor, ro, rd,  int( Obj.id_color), Obj.id_material);


	float dif1=1.0;
   	vec3 result;
    result=  Getluz( p,ro,rd, nor, colobj ,light_pos1);
    result+= Getluz( p,ro,rd, nor, colobj ,light_pos2);
    result/=1.25;
    col= result*dif1;


  	//sugerencia por dean_the_coder,
    //col *= 1.0 - pow(d / 100.0, 1.5);
    col *= 1.0 - pow(d /(100.0) , 3.5);    
    col = pow(col, vec3(1.0/2.2));  
    fragColor = vec4(col,1.0);
}

