// v1.1 - 2019 02 08 - horizontal scan
// v1.2 - 2019 02 11 - FabriceNeyret2 recommandations

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 UV = 3.0 * fragCoord/iResolution.y;

    // Time varying Soften :
    float Soften = fract(iTime/2.0 + UV.x*0.01); // <- For demo
	//float Soften = 0.1; // <- Real case use

    vec2 PartieNegative = abs(fract((2.0*UV-Soften)/2.0)-0.5);
    vec2 PartiePositive = abs(fract((2.0*UV+Soften)/2.0)-0.5);
    vec2 Pente = (PartieNegative-PartiePositive) / Soften;

    // Output to screen
    vec4 Color1 = vec4(1,1,0,1);
    vec4 Color2 = vec4(0,0.5,1,1);
    fragColor = mix(Color1, Color2, 0.5 - 0.5*Pente.x*Pente.y);
}
