float rnd(float x){return fract(999.123*sin(x*99.52125 ));}

vec2 grid = vec2(128., 64.);
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    
    vec2 uv = fragCoord/iResolution.xy;
    uv=floor(uv*grid)/grid;
    float t = floor(iTime*.5+rnd(uv.x+uv.y*.1137));


    bool isFall = true;
    if (sin(t+uv.x*2.5+uv.y*1.5)>0.) isFall = false;

    vec3 trunks;
    trunks = vec3(rnd(uv.x+uv.y*.1137));
    trunks=step(.1,trunks);
    trunks*=step(.5,rnd(floor(uv.x*grid.x/2.)/grid.x+6.));
    trunks*= step(0.45
                  +sin(uv.x*20.)*.03
                  +sin(uv.x*37.)*.1
                  +rnd(uv.x+uv.y*.1132)*.2
                 , 1.-uv.y);

    vec3 bg;
    bg.g = rnd(uv.x+uv.y*.1133)/4.;
    bg.b = rnd(uv.x+uv.y*.1135)/4.;

    vec3 treetops, treetops2, defoliation;
    treetops += rnd(uv.x+uv.y*.1133+t)/4.+.75;

    if(isFall) {
        treetops.g = treetops.r/2. + rnd(uv.x+uv.y*.1131+t)/4.;
	    treetops.b = 0.;
    }

    //treetops *= 0.;
    treetops -= rnd(floor(uv.x*grid.x/4.)/(grid.x/4.)
                    +floor(uv.y*grid.y/4.)/(grid.y/4.)
                    *.1132+t)*.2;

    treetops2 = treetops;
    defoliation = treetops;

    treetops *= step(.5, uv.y+rnd(uv.x+uv.y*.1132+t)*.2);

    uv+=1./grid;
    defoliation *= step(.99-step(.96,1.-uv.y)*.5, rnd(uv.x+uv.y*.1132+t));

    if(defoliation.r>0.){
    	fragColor = vec4(defoliation,1.0);
    	return;
    }

    fragColor = vec4(max(bg,max(trunks, treetops)),1.0);
}
