/*
  The autocatalytic Gray-Scott system involves two chemical reactions
  U + 2V -> 3V and V -> P.
  In terms of partial differential equations, where U = U(x,y,t), etc,
  with diffusion added (fixed diffusion constants D_u and D_v that
  differ significantly), these correspond to
    dU/dt = D_u del^2 U - UV^2 + f(1 - U)
    dV/dt = D_v del^2 V + UV^2 - (f + k)V
  where f is the feed rate (coupled to reservoirs with U=1 and V=0) and
  k is the rate constant for V->P.

  The equations are numerically integrated using the Euler method (on a regular 
  CPU use the stable ADI method that allows a 10x larger time step). The initial 
  state is either random or a small cluster with a little randomness added to break 
  symmetry; each run is different; boundaries are periodic.

  The left and right sliders set the k and f parameters; the values are
  shown x100. The left button cycles through several interesting parameter
  presets; there are many other combinations worth examining. The right button
  chooses the type of initial state. Some patterns are slow to evolve; patience.
  (The color and lighting are not related to the process but simply enhance 
  the visuals.)

  Online sources include:
   http://blog.hvidtfeldts.net/index.php/2012/08/reaction-diffusion-systems/
   http://mrob.com/pub/comp/xmorphia/pearson-classes.html
   https://www.shadertoy.com/view/MdVGRh
*/

vec3 HsvToRgb (vec3 c);
vec3 VaryNf (vec3 p, vec3 n, float f);
vec4 Loadv4 (vec2 vId);
vec3 ShowWg (vec2 uv, vec2 canvas, vec3 col, vec2 slVal);

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  vec4 hs;
  vec3 col, vn, ltDir;
  vec2 canvas, uv, ut, gv, sv, e;
  float h[4], tCur, gSize, w, hue, colStyle;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  ltDir = normalize (vec3 (cos (0.5 * tCur), 3., sin (0.5 * tCur)));
  colStyle = 1.;
  ut = abs (uv) - vec2 (1.);
  if (max (ut.x, ut.y) > 0.) {
    col = ShowWg (uv, canvas, vec3 (0.82), Loadv4 (vec2 (3., 0.)).zw);
  } else {
    gSize = Loadv4 (vec2 (0., 0.)).w;
    gv = gSize * (0.5 * uv + 0.5);
    sv = floor (gv);
    e = vec2 (1., 0.);
    h[0] = Loadv4 (sv).x;
    h[1] = Loadv4 (sv + e).x;
    h[2] = Loadv4 (sv + e.yx).x;
    h[3] = Loadv4 (sv + e.xx).x;
    hs = vec4 (h[0], h[1], h[2], h[3]);
    vn = normalize (vec3 (hs.x - hs.w - hs.y + hs.z, 0.5, hs.x - hs.w + hs.y - hs.z));
    gv -= sv;
    w = mix (mix (hs.x, hs.y, gv.x), mix (hs.z, hs.w, gv.x), gv.y);
    if (colStyle == 0.) {
      col = mix (vec3 (1., 1., 0.3), vec3 (0., 0., 0.5), smoothstep (0.3, 0.7, w));
      col *= 0.2 + 0.8 * max (dot (vn, ltDir), 0.) +
         0.5 * pow (max (0., dot (ltDir, reflect (vec3 (0., -1., 0.), vn))), 32.);
    } else {
      hue = mod (0.01 * tCur, 1.);
      col = HsvToRgb (vec3 (hue, 0.9, 1.)) * mix (max (dot (vn, ltDir), 0.), 0.05, w) +
         pow (max (0., dot (ltDir, reflect (vec3 (0., -1., 0.), vn))), 64.);
      if (false) col = min (1000. * HsvToRgb (vec3 (hue, 0.3, 1.)) * 
         pow (dot (VaryNf (32. * vec3 (uv, 0.).xzy, vn, 2.), ltDir), 1024.) +
         col * (0.2 + 0.8 * max (dot (vn, ltDir), 0.)), 1.);
    }
  }
  fragColor = vec4 (clamp (col, 0., 1.), 1.);
}

vec3 HsvToRgb (vec3 c)
{
  return c.z * mix (vec3 (1.), clamp (abs (fract (c.xxx + vec3 (1., 2./3., 1./3.)) * 6. - 3.) - 1., 0., 1.), c.y);
}

vec2 Hashv2v2 (vec2 p)
{
  const float cHashM = 43758.54;
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (vec2 (dot (p, cHashVA2), dot (p + vec2 (1., 0.), cHashVA2))) * cHashM);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);  
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float Fbmn (vec3 p, vec3 n)
{
  vec3 s;
  float a;
  s = vec3 (0.);
  a = 1.;
  for (int i = 0; i < 5; i ++) {
    s += a * vec3 (Noisefv2 (p.yz), Noisefv2 (p.zx), Noisefv2 (p.xy));
    a *= 0.5;
    p *= 2.;
  }
  return dot (s, abs (n));
}

vec3 VaryNf (vec3 p, vec3 n, float f)
{
  vec3 g;
  vec3 e = vec3 (0.1, 0., 0.);
  g = vec3 (Fbmn (p + e.xyy, n), Fbmn (p + e.yxy, n), Fbmn (p + e.yyx, n)) - Fbmn (p, n);
  return normalize (n + f * (g - n * dot (n, g)));
}

#define txBuf iChannel0
#define txSize iChannelResolution[0].xy

vec4 Loadv4 (vec2 vId)
{
  return texture (txBuf, (vId + 0.5) / txSize);
}

float DigSeg (vec2 q)
{
  return (1. - smoothstep (0.13, 0.17, abs (q.x))) *
     (1. - smoothstep (0.5, 0.57, abs (q.y)));
}

float ShowDig (vec2 q, int iv)
{
  float d;
  int k, kk;
  const vec2 vp = vec2 (0.5, 0.5), vm = vec2 (-0.5, 0.5), vo = vec2 (1., 0.);
  if (iv < 5) {
    if (iv == -1) k = 8;
    else if (iv == 0) k = 119;
    else if (iv == 1) k = 36;
    else if (iv == 2) k = 93;
    else if (iv == 3) k = 109;
    else k = 46;
  } else {
    if (iv == 5) k = 107;
    else if (iv == 6) k = 122;
    else if (iv == 7) k = 37;
    else if (iv == 8) k = 127;
    else k = 47;
  }
  q = (q - 0.5) * vec2 (1.7, 2.3);
  d = 0.;  kk = k / 2;  if (kk * 2 != k) d += DigSeg (q.yx - vo);
  k = kk;  kk = k / 2;  if (kk * 2 != k) d += DigSeg (q.xy - vp);
  k = kk;  kk = k / 2;  if (kk * 2 != k) d += DigSeg (q.xy - vm);
  k = kk;  kk = k / 2;  if (kk * 2 != k) d += DigSeg (q.yx);
  k = kk;  kk = k / 2;  if (kk * 2 != k) d += DigSeg (q.xy + vm);
  k = kk;  kk = k / 2;  if (kk * 2 != k) d += DigSeg (q.xy + vp);
  k = kk;  kk = k / 2;  if (kk * 2 != k) d += DigSeg (q.yx + vo);
  return d;
}

float ShowInt (vec2 q, vec2 cBox, float mxChar, float val)
{
  float nDig, idChar, s, sgn, v;
  q = vec2 (- q.x, q.y) / cBox;
  s = 0.;
  if (min (q.x, q.y) >= 0. && max (q.x, q.y) < 1.) {
    q.x *= mxChar;
    sgn = sign (val);
    val = abs (val);
    nDig = (val > 0.) ? floor (max (log (val) / log (10.), 0.) + 0.001) + 1. : 1.;
    idChar = mxChar - 1. - floor (q.x);
    q.x = fract (q.x);
    v = val / pow (10., mxChar - idChar - 1.);
    if (idChar == mxChar - nDig - 1. && sgn < 0.) s = ShowDig (q, -1);
    if (idChar >= mxChar - nDig) s = ShowDig (q, int (mod (floor (v), 10.)));
  }
  return s;
}

vec3 ShowWg (vec2 uv, vec2 canvas, vec3 col, vec2 slVal)
{
  vec4 wgBx[4];
  vec3 c1, c2;
  vec2 ut, ust;
  float vW[2], asp;
  asp = canvas.x / canvas.y;
  wgBx[0] = vec4 (0.36 * asp, 0., 0.012 * asp, 0.25);
  wgBx[1] = vec4 (0.44 * asp, 0., 0.012 * asp, 0.25);
  wgBx[2] = vec4 (0.36 * asp, -0.4, 0.013 * asp, 0.);
  wgBx[3] = vec4 (0.44 * asp, -0.4, 0.013 * asp, 0.);
  vW[0] = slVal.x;
  vW[1] = slVal.y;
  c1 = vec3 (0.1, 0.1, 0.7);
  c2 = vec3 (0.5, 0.5, 1.);
  for (int k = 0; k < 2; k ++) {
    ut = 0.5 * uv - wgBx[k].xy;
    ust = abs (ut) - wgBx[k].zw * vec2 (0.7, 1.);
    if (max (ust.x, ust.y) < 0.) {
      if  (min (abs (ust.x), abs (ust.y)) * canvas.y < 2.) col = c2;
      else col = (mod (0.5 * ((0.5 * uv.y - wgBx[k].y) / wgBx[k].w - 0.99), 0.05) *
         canvas.y < 5.) ? c2 : vec3 (0.7);
    }
    ut.y -= (vW[k] - 0.5) * 2. * wgBx[k].w;
    ut = abs (ut) * vec2 (1., 2.);
    if (abs (max (ut.x, ut.y) - 0.015) < 0.006) col = c1;
    col = mix (col, c1, ShowInt (0.5 * uv - wgBx[k].xy - wgBx[k].zw * vec2 (1., -1.) - vec2 (0.0045, -0.06),
       0.028 * vec2 (asp, 1.), 2., clamp (floor (1000. * vW[k] * 0.1 + 1e-4), 1., 99.)));
  }
  for (int k = 2; k < 4; k ++) {
    if (length (0.5 * uv - wgBx[k].xy) < wgBx[k].z) {
      col = (length (0.5 * uv - wgBx[k].xy) < 0.7 * wgBx[k].z) ? c1 : c2;
    }
  }
  return col;
}

