float Hashfv2 (vec2 p);
vec4 Loadv4 (vec2 vId);
void Savev4 (vec2 vId, vec4 val, inout vec4 fCol, vec2 fCoord);

vec2 difC, constKF;
float itRand, gSize, delT, bgnClust;
const float pi = 3.14159;

vec2 Step (vec2 sv)
{
  vec4 dv;
  vec2 c, del2c;
  c = Loadv4 (sv).xy;
  dv = mod (vec4 (sv.xx, sv.yy) + vec4 (1., -1., 1., -1.), gSize);
  del2c = Loadv4 (vec2 (dv.x, sv.y)).xy +
          Loadv4 (vec2 (dv.y, sv.y)).xy +
          Loadv4 (vec2 (sv.x, dv.z)).xy +
          Loadv4 (vec2 (sv.x, dv.w)).xy - 4. * c;
  c += delT * (difC * del2c - constKF.y * c +
     vec2 (constKF.y, - constKF.x * c.y) + c.x * c.y * c.y * vec2 (-1., 1.));
  return c;
}

vec2 Init (vec2 sv)
{
  vec2 c;
  float s, iSize;
  if (bgnClust == 0.) {
    s = step (0.1, Hashfv2 (mod (16. * floor (sv / 16.) + itRand + 10.1, 2. * pi)));
    c = vec2 (s, 1. - s);
  } else {
    iSize = (gSize > 128.) ? 10. : 5.;
    if (max (abs (sv.x - 0.5 * gSize), abs (sv.y - 0.5 * gSize)) <= iSize) {
      s = Hashfv2 (mod (17. * sv + itRand + 10.1, 2. * pi));
      c = vec2 (0.5 + 0.02 * (s - 0.5), 0.25 - 0.02 * (s - 0.5));
    } else c = vec2 (1., 0.);
  }
  return c;
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  const int nPre = 7;
  vec4 wgBx[4], stDat, mPtr, mPtrP;
  vec2 presetKF[nPre], iFrag, canvas, ust, c, slVal;
  float tCur, asp, vW, nStep, idPre;
  int wgSel, wgReg, kSel;
  bool doInit, doPre;
  canvas = iResolution.xy;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  gSize = (canvas.y >= 1024.) ? 1024. : ((canvas.y >= 512.) ? 512. : ((canvas.y >= 256.) ? 256. : 128.));
  if (max (fragCoord.x, fragCoord.y) >= gSize) discard;
  iFrag = floor (fragCoord);
  doInit = (iFrame <= 5);
  doPre = false;
  wgSel = -1;
  wgReg = -2;
  difC = vec2 (0.2, 0.1);
  delT = 1.;
  presetKF[0] = vec2 (0.047, 0.010);
  presetKF[1] = vec2 (0.059, 0.020);
  presetKF[2] = vec2 (0.055, 0.025);
  presetKF[3] = vec2 (0.060, 0.025);
  presetKF[4] = vec2 (0.063, 0.050);
  presetKF[5] = vec2 (0.065, 0.040);
  presetKF[6] = vec2 (0.065, 0.055);
  if (doInit) {
    mPtrP = mPtr;
    itRand = 0.;
    idPre = 0.;
    bgnClust = 0.;
    doPre = true;
    nStep = 0.;
  } else {
    stDat = Loadv4 (vec2 (0., 0.));
    nStep = stDat.z;
    stDat = Loadv4 (vec2 (1., 0.));
    mPtrP.xy = stDat.zw;
    stDat = Loadv4 (vec2 (2., 0.));
    mPtrP.zw = vec2 (stDat.z, 0.);
    itRand = stDat.w;
    stDat = Loadv4 (vec2 (3., 0.));
    slVal = stDat.zw;
    constKF = slVal.xy * 0.1;
    stDat = Loadv4 (vec2 (4., 0.));
    idPre = stDat.z;
    bgnClust = stDat.w;
    stDat = Loadv4 (vec2 (5., 0.));
    wgSel = int (stDat.z);
  }
  asp = canvas.x / canvas.y;
  if (mPtr.z > 0.) {
    wgBx[0] = vec4 (0.36 * asp, 0., 0.012 * asp, 0.25);
    wgBx[1] = vec4 (0.44 * asp, 0., 0.012 * asp, 0.25);
    wgBx[2] = vec4 (0.36 * asp, -0.4, 0.013 * asp, 0.);
    wgBx[3] = vec4 (0.44 * asp, -0.4, 0.013 * asp, 0.);
    for (int k = 0; k < 2; k ++) {
      ust = abs (mPtr.xy * vec2 (asp, 1.) - wgBx[k].xy) - wgBx[k].zw;
      if (max (ust.x, ust.y) < 0.) wgReg = k;
    }
    for (int k = 2; k < 4; k ++) {
      if (length (mPtr.xy * vec2 (asp, 1.) - wgBx[k].xy) < wgBx[k].z) wgReg = k;
    }
    if (mPtrP.z <= 0.) {
      wgSel = wgReg;
      itRand = tCur;
    }
  } else {
    wgSel = -1;
    wgReg = -2;
  }
  if (wgSel >= 0) {
    if (wgSel < 2) {
      for (int k = 0; k < 2; k ++) {
        if (wgSel == k) {
          kSel = k;
          vW = clamp (0.5 + 0.5 * (mPtr.y - wgBx[k].y) / wgBx[k].w, 0., 1.);
          break;
        }
      }
      vW = 0.01 * floor (100. * vW + 0.5);
      if (kSel == 0) {
        slVal.x = clamp (vW, 0.3, 0.7);
        constKF.x = slVal.x * 0.1;
      } else if (kSel == 1) {
        slVal.y = clamp (vW, 0.05, 0.8);
        constKF.y = slVal.y * 0.1;
      }
    } else if (wgSel == 2 && mPtrP.z <= 0.) {
      idPre = mod (idPre + 1., float (nPre));
      doPre = true;
    } else if (wgSel == 3 && mPtrP.z <= 0.) {
      bgnClust = 1. - bgnClust;
    }
    doInit = true;
  }
  if (gSize == 128. && nStep >= 2000.) {
    idPre = mod (idPre + 1., float (nPre));
    doPre = true;
    doInit = true;
  }
  if (doPre) {
    for (int k = 0; k < nPre; k ++) {
      if (idPre == float (k)) {
        constKF = presetKF[k];
        slVal = constKF / 0.1;
        break;
      }
    }
  }
  if (! doInit && gSize != Loadv4 (vec2 (0., 0.)).w) doInit = true;
  if (doInit) {
    nStep = 0.;
    c = Init (iFrag);
  } else {
    ++ nStep;
    c = Step (iFrag);
  }
  stDat = vec4 (c, 0., 0.);
  if (iFrag.y == 0.) {
    if      (iFrag.x == 0.) stDat.zw = vec2 (nStep, gSize);
    else if (iFrag.x == 1.) stDat.zw = mPtr.xy;
    else if (iFrag.x == 2.) stDat.zw = vec2 (mPtr.z, itRand);
    else if (iFrag.x == 3.) stDat.zw = slVal;
    else if (iFrag.x == 4.) stDat.zw = vec2 (idPre, bgnClust);
    else if (iFrag.x == 5.) stDat.zw = vec2 (float (wgSel), 0.);
  }
  Savev4 (iFrag, stDat, fragColor, fragCoord);
}

float Hashfv2 (vec2 p)
{
  const float cHashM = 43758.54;
  return fract (sin (dot (p, vec2 (37., 39.))) * cHashM);
}

#define txBuf iChannel0
#define txSize iChannelResolution[0].xy

vec4 Loadv4 (vec2 vId)
{
  return texture (txBuf, (vId + 0.5) / txSize);
}

void Savev4 (vec2 vId, vec4 val, inout vec4 fCol, vec2 fCoord)
{
  vec2 d;
  d = abs (fCoord - vId - 0.5);
  if (max (d.x, d.y) < 0.5) fCol = val;
}

