#define PI 3.14159265359
#define matRotateX(rad) mat3(1,0,0,0,cos(rad),-sin(rad),0,sin(rad),cos(rad))
#define matRotateY(rad) mat3(cos(rad),0,-sin(rad),0,1,0,sin(rad),0,cos(rad))

float sdBox( vec3 p, vec3 b )
{
  vec3 d = abs(p) - b;
  return length(max(d,0.0)) + min(max(d.x,max(d.y,d.z)),0.0); 
}

float field(vec3 p) {
	float s = 0.2+sin(p.y+iTime*2.0)*0.15+cos(p.x+iTime*1.5)*0.15;
    p.y -= iTime*1.2;
    p.x += sin(p.y+iTime*2.0);
    p *= matRotateY(radians(-iTime*30.0));
    p = mod(p-0.4,0.8)-0.4;
    return length(p)-s;
}

vec4 map(vec3 p){
    float speed = iTime*3.0;
    float s = 1.5+sin(p.y+iTime*1.2)*0.5;
    return vec4(vec3(0.3),max(sdBox(p+vec3(0.0,3.0,0.0), vec3(s)),field(p)));
}

vec3 normalMap(vec3 p){
	float d = 0.0001;
	return normalize(vec3(
		map(p + vec3(  d, 0.0, 0.0)).w - map(p + vec3( -d, 0.0, 0.0)).w,
		map(p + vec3(0.0,   d, 0.0)).w - map(p + vec3(0.0,  -d, 0.0)).w,
		map(p + vec3(0.0, 0.0,   d)).w - map(p + vec3(0.0, 0.0,  -d)).w
	));
}

float shadowMap(vec3 ro, vec3 rd){
	float h = 0.0;
	float c = 0.001;
	float r = 1.0;
	float shadow = 0.5;
	for(float t = 0.0; t < 30.0; t++){
		h = map(ro + rd * c).w;
		if(h < 0.001){
			return shadow;
		}
		r = min(r, h * 16.0 / c);
		c += h;
	}
	return 1.0 - shadow + r * shadow;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
	vec2 p = (fragCoord.xy * 2.0 - iResolution.xy) / min(iResolution.x, iResolution.y);
	mat3 camRotY = matRotateY(radians(iTime*10.0));

	vec3 ro=vec3(0.,-3.0,-4.);
    vec3 rd=normalize(vec3(p,1.0));

    float t, dist;
	t = 0.0;
	vec3 distPos = vec3(0.0);
	vec4 distCl = vec4(0.0);
	for(int i = 0; i < 60; i++){
		distCl = map(distPos);
		dist = distCl.w;
		if(dist < 1e-4){break;}
        if(t>13.)break;
		t += dist;
		distPos = (ro+rd*t)*camRotY;
	}

	vec3 color;
	float shadow = 1.0;

	if(t < 13.){
		// lighting
		vec3 lightDir = vec3(1.0, 10.0, 1.0);
		vec3 light = normalize(lightDir);
		vec3 normal = normalMap(distPos);

		// difuse color
		float diffuse = clamp(dot(light, normal), 1.0, 1.0);
		float lambert = max(.0, dot( normal, light));

		// shadow
		shadow = shadowMap(distPos + normal * 0.001, light);

		// result
		color += vec3(lambert);
		color = diffuse*(distCl.xyz+(.1-length(p.xy)/3.))*vec3(1.0, 1.0, 1.0);
	}else{
        color =.84*max(mix(vec3(1.1,1.31,1.35)+(.1-length(p.xy)/3.),vec3(1),.1),0.);
	}

	// rendering result
	float brightness = 1.0;
	vec3 dst = (color * max(0.5, shadow))*brightness;
	fragColor = vec4(dst, 1.0);

}
