// SHADERTOBER 09 Swing
// Poulet Vert 09-10-2019
// Thanks to iq, leon, patricio&jen

#define VOLUME 0.001
#define PI 3.14159


////////////////////////////////////////////////////////////////////////////////
mat2 rot (float a) { float c=cos(a),s=sin(a); return mat2(c,-s,s,c); }
float sat(float a) {return clamp(a, .0, 1.); }

float opU(float s1, float s2) { return min(s1, s2);}

vec2 opU2(vec2 d1, vec2 d2) { return (d1.x < d2.x) ? d1 : d2; }

float opS( float d1, float d2 ) { return max(-d1,d2); }

float opI( float d1, float d2 ) { return max(d1,d2); }


float sdCappedCylinder( vec3 p, float h, float r )
{
  vec2 d = abs(vec2(length(p.xz),p.y)) - vec2(h,r);
  return min(max(d.x,d.y),0.0) + length(max(d,0.0));
}

float sdBox( vec3 p, vec3 b )
{
  vec3 d = abs(p) - b;
  return length(max(d,0.0))
         + min(max(d.x,max(d.y,d.z)),0.0);
}

float random (float s) {return fract(sin(dot(s,1.9898))*1.5453123);}

vec2 map(vec3 pos)
{
    vec2 t = vec2(0.0, 0.0);

    // ground
    t.x = pos.y;

    // strange shape
    vec3 sp = pos + vec3(1.0, -1.0, 2.0);
    sp.xy *= rot(PI/2.0);

    float s = 1.0;

    for(int i=0;i<35;i++)
    {
        float rs = random(float(i)*.0005);
        sp.y -= rs*2.0;
        s = opU(s, sdCappedCylinder(
            sp, 
            1.0+sin(float(i)+iTime)*.2, 
            rs));
    }

    vec2 strange = vec2(s, 1.0);

    // walls
    vec3 wap = pos + vec3(3.0, 0.0, 2.0);
    float wa = sdBox(wap, vec3(1.7, 5.0, 1.0));
    wap = pos + vec3(-3.0, 0.0, 2.0);
    wa = opU(wa, sdBox(wap, vec3(1.7, 5.0, 1.0)));

    vec2 walls = vec2(wa, 2.0);

    t = opU2(t, strange);
    t = opU2(t, walls);

    return t;
}

float GetShadow (vec3 pos, vec3 at, float k) {
    vec3 dir = normalize(at - pos);
    float maxt = length(at - pos);
    float f = 01.;
    float t = VOLUME*50.;
    for (float i = 0.; i <= 1.; i += 1./15.) {
        vec2 dist = map(pos + dir * t);
        if (dist.x < VOLUME) return 0.;
        f = min(f, k * dist.x / t);
        t += dist.x;
        if (t >= maxt) break;
    }
    return f;
}

vec2 CastRay(vec3 ro, vec3 rd)
{
    float t = 0.0;

    for(int i=0 ; i<64 ; i++)
    {
        vec2 ray = map(ro + rd * t);

        if(ray.x < (0.0001*t))
        {
            return vec2(t, ray.y);
        }

        t += ray.x;
    }

    return vec2(-1.0, 0.0);
}

vec3 GetNormal (vec3 p) 
{ 
    vec2 e = vec2(0.01, 0.0); 
    return normalize(vec3(
        map(p+e.xyy).x-map(p-e.xyy).x,
        map(p+e.yxy).x-map(p-e.yxy).x,
        map(p+e.yyx).x-map(p-e.yyx).x
        )); 
}

vec3 Render(vec3 ro, vec3 rd, vec2 uv)
{
    vec2 t = CastRay(ro, rd);
    vec3 pos = ro+rd*t.x;
    vec3 col = vec3(0.0);
    float depth = clamp(pos.z, 0.0, 1.0);
    depth = 1.0 - sat(pos.z*.1);

    if(t.x == -1.0)
    {        
        col = vec3(rd.y);

    }
    else
    {
        float glp = clamp(length(pos)*.1, 0.0, 1.0);
        vec3 N = GetNormal(ro+rd*t.x);
        vec3 L = vec3(0.0, 1.0, 1.0);
        float light = dot(N,L);
        float shade = GetShadow(pos, L, 4.);

        if(t.y == 0.0) 
        {
            col = vec3(.2+pos.z*.1);
            col *= shade;
        }
        if(t.y == 1.0) 
        {
            col = vec3(sin(pos.x+iTime*1.), 0.2, 0.2);
            col *= vec3(light)*.7;
        }
        if(t.y == 2.0) 
        {
            col = vec3(light*.2);
        }
    }

    return col;
}

vec3 GetViewDir(vec2 uv, vec3 cp, vec3 ct)
{
    vec3 forward = normalize(ct - cp);
    vec3 right = normalize(cross(vec3(0.0, -1.0, 0.0), forward));
    vec3 up = normalize(cross(right, forward));

    return normalize(uv.x * right + uv.y * up + 2.0 * forward);
}



void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = 2.0 * fragCoord.xy / iResolution.xy - 1.0;
    uv.x *= iResolution.x/iResolution.y;

    float time = iTime*.25;

    vec3 cp = vec3(0.0, 1.0, 5.);
    vec3 ct = vec3(0.0, 1.0, 0.0);

    vec3 vd = GetViewDir(uv, cp, ct);

    vec3 col = vec3(length(uv));
    vec2 screenUV = fragCoord.xy / iResolution.xy;

    col = Render(cp, vd, uv);
    col.z += screenUV.y*.2;
    col *= 1.0-length(uv)*.8;

    fragColor = vec4(col,1.0);
}
