/*
 Tunnel travel by handcar (mouseable); based on "Scrolling Texture Heightmap". 
 Tunnel surface uses a scrolling (nonrepeating) heightmap stored in a 
 continually updated texture. This is faster than the nontextured version but 
 should be visually the same; fullscreen mode gives highest resolution 
 texture.
*/

#define USE_TEX 1    // = 0 (in both shaders) for non-textured version

#define AA      0    // optional antialiasing

#define DRONES  1    // optional drones

float PrBoxDf (vec3 p, vec3 b);
float PrRound2BoxDf (vec3 p, vec3 b, float r);
float PrSphDf (vec3 p, float r);
float PrCylDf (vec3 p, float r, float h);
float PrTorus4Df (vec3 p, float ri, float rc);
float Fbm2 (vec2 p);
float Fbm3 (vec3 p);
vec3 VaryNf (vec3 p, vec3 n, float f);
float Maxv3 (vec3 p);
float SmoothMin (float a, float b, float r);
float SmoothBump (float lo, float hi, float w, float x);
vec2 Rot2D (vec2 q, float a);
#if DRONES
float PrCylAnDf (vec3 p, float r, float w, float h);
float PrRoundCylDf (vec3 p, float r, float rt, float h);
vec2 Rot2Cs (vec2 q, vec2 cs);
#endif
vec4 Loadv4 (vec2 vId);
#if USE_TEX
vec4 LoadTx (vec2 uv);
#else
float Hashfv2 (vec2 p);
vec2 Hashv2v2 (vec2 p);
void HexVorInit ();
vec4 HexVor (vec2 p);
#endif

#define N_CAR   2

mat3 carMat[N_CAR], crMat;
vec3 carPos[N_CAR], crPos, ltDir, qHit;
vec2 trkFx, trkFy, trkAx, trkAy, trkFw, trkAw;
float dstFar, tCur, tunRad, lmpSep, cSzFac, levAng;
#if USE_TEX
float txOrg, txAsp;
#endif
int idObj;
const int idWal = 1, idFlr = 2, idLmp = 3, idArch = 4, idRail = 5, idTie = 6,
   idCrLev = 11, idCrBase = 12, idCrWhl = 13;
const float pi = 3.14159;

#if DRONES
#define N_DRONE 2
mat3 droneMat[N_DRONE], flMat;
vec3 dronePos[N_DRONE], flPos;
vec2 csProp;
float dSzFac;
const int idDrBod = 21, idDrLamp = 22, idDrCam = 23, idDrProp = 24;
#endif

float TSurfHt (vec2 tc)
{
#if USE_TEX
  return LoadTx (tc * txAsp).x;
#else
  vec4 vc;
  vc = HexVor (4. * pi * tunRad * tc);
  return max (0.3 * SmoothMin (0.4, 0.3 * vc.x, 0.1) * (1. - 0.3 * dot (vc.yz, vc.yz)) *
     (1. + 2.5 * vc.w), 0.);
#endif
}

float RdTun (vec3 p)
{
  float a, rh;
  a = 0.5 * (atan (- p.x, p.y) / pi + 1.);
#if USE_TEX
  rh = TSurfHt (vec2 ((p.z - txOrg) / (2. * pi * tunRad), a));
#else
  rh = TSurfHt (vec2 (p.z / (2. * pi * tunRad), a));
#endif
  return tunRad + dot (trkAw, sin (trkFw * p.z)) -
     rh * (1. - 0.5 * smoothstep (0.5, 1., p.y / tunRad));
}

vec3 TrackPos (float z)
{
  return vec3 (dot (trkAx, sin (trkFx * z)), dot (trkAy, sin (trkFy * z)), z);
}

vec3 TrackVel (float z)
{
  return normalize (vec3 (dot (trkAx, trkFx * cos (trkFx * z)),
     dot (trkAy, trkFy * cos (trkFy * z)), 1.));
}

vec3 TrackAcc (float z)
{
  return normalize (vec3 (- dot (trkAx, trkFx * trkFx * sin (trkFx * z)),
     - dot (trkAy, trkFy * trkFy * cos (trkFy * z)), 0.));
}

#define DMIN(id) if (d < dMin) { dMin = d;  idObj = id; }
#define DMINQ(id) if (d < dMin) { dMin = d;  idObj = id;  qHit = q; }

float TunDf (vec3 p)
{
  vec3 q;
  vec2 rt;
  float dMin, d;
  dMin = dstFar;
  p.xy -= TrackPos (p.z).xy;
  d = RdTun (p) - length (p.xy);
  DMIN (idWal);
  d = (0.82 - 0.02 * p.x * p.x) * tunRad + p.y;
  DMIN (idFlr);
  return 0.5 * dMin;
}

float TunRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = 0; j < 160; j ++) {
    d = TunDf (ro + dHit * rd);
    dHit += d;
    if (d < 0.0005 || dHit > dstFar) break;
  }
  return dHit;
}

vec3 TunNf (vec3 p)
{
  vec4 v;
  vec2 e = vec2 (0.001, -0.001);
  v = vec4 (- TunDf (p + e.xxx), TunDf (p + e.xyy), TunDf (p + e.yxy), TunDf (p + e.yyx));
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

#if DRONES
float DroneDf (vec3 p, float dMin)
{
  vec3 q;
  float d;
  p.xz = vec2 (p.x - p.z, p.z + p.x) / sqrt (2.);
  q = p;
  q.y -= 0.05;
  d = PrRoundCylDf (q.xzy, 0.2, 0.03, 0.07);
  DMIN (idDrBod);
  q -= vec3 (-0.7, -0.05, 0.7);
  d = PrRoundCylDf (q.xzy, 0.07, 0.04, 0.12);
  DMIN (idDrLamp);
  q = p;
  q.xz = abs (q.xz) - 0.7;
  d = min (PrCylAnDf (q.xzy, 0.5, 0.05, 0.05), PrCylDf (q.xzy, 0.1, 0.03));
  DMIN (idDrBod);
  q.xz = Rot2Cs (q.xz, csProp);
  d = PrCylDf (q, 0.02, 0.415);
  DMIN (idDrProp);
  q = p;
  q.xz = abs (q.xz);
  q -= vec3 (0.3, -0.07, 0.3);
  d = PrRoundCylDf (q.xzy, 0.05, 0.03, 0.15);
  DMIN (idDrBod);
  q -= vec3 (-0.3, 0.13, -0.3);
  q.xz = vec2 (q.x - q.z, q.z + q.x) / sqrt (2.);
  d = min (PrRoundCylDf (q, 0.05, 0.02, 1.), PrRoundCylDf (q.zyx, 0.05, 0.02, 1.));
  DMIN (idDrBod);
  q = p;
  q.y -= -0.05;
  d = PrSphDf (q, 0.17);
  DMIN (idDrCam);
  return dMin;
}
#endif

float CarDf (vec3 p, float dMin)
{
  vec3 q;
  float d;
  p.y -= -0.65;
  q = p;
  q.y -= -0.1;
  d = PrCylDf (q.xzy, 0.02, 0.25);
  q.y -= 0.25;
  d = min (d, PrCylDf (q.yzx, 0.03, 0.03));
  q.yz = Rot2D (q.yz, levAng);
  d = min (d, PrCylDf (q, 0.015, 0.15));
  q.z = abs (q.z) - 0.15;
  d = min (d, PrCylDf (q.yzx, 0.01, 0.1));
  DMIN (idCrLev);
  q = p;
  q.y -= -0.26;
  d = PrRound2BoxDf (q.xzy, vec3 (0.01, 0.04, 0.4).xzy, 0.2);
  DMINQ (idCrBase);
  q = p;
  q = vec3 (abs (q.x) - 0.23, q.y + 0.3, abs (q.z) - 0.2);
  d = PrCylDf (q.yzx, 0.06 - sign (q.x) * 0.01, 0.03);
  DMIN (idCrWhl);
  return dMin;
}

float ObjDf (vec3 p)
{
  vec3 q, qq;
  float dMin, d;
  dMin = dstFar;
  qq = p;
  qq.xy -= TrackPos (qq.z).xy;
  q = qq;
  d = length (max (abs (vec2 (abs (q.x) - 0.8, q.y + 0.82 * tunRad)) -
      vec2 (0.07, 0.25), 0.));
  DMIN (idRail);
  q.y -= -0.82 * tunRad - 0.05;
  q.z = mod (q.z + 1., 2.) - 1.;
  d = PrBoxDf (q, vec3 (1., 0.2, 0.1));
  DMIN (idTie);
  q = qq;
  q.z = mod (q.z + lmpSep, 2. * lmpSep) - lmpSep;
  d = PrTorus4Df (q, 0.16, tunRad - 0.16) - 0.15 *
     (0.7 + 0.3 * sqrt (abs (sin (31. * atan (q.y, q.x)))));
  DMIN (idArch);
  q.y -= tunRad - 0.5;
  d = PrSphDf (q, 0.3);
  DMIN (idLmp);
#if DRONES
  dMin *= dSzFac;
  for (int k = 0; k < N_DRONE; k ++) {
    q = dSzFac * droneMat[k] * (p - dronePos[k]);
    dMin = DroneDf (q, dMin);
  }
  dMin /= dSzFac;
#endif
  dMin *= cSzFac;
  for (int k = 0; k < N_CAR; k ++) {
    q = cSzFac * carMat[k] * (p - carPos[k]);
    levAng = 0.2 * pi * sin (0.2 * pi * carPos[k].z);
    dMin = CarDf (q, dMin);
  }
  dMin /= cSzFac;
  return dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = 0; j < 120; j ++) {
    d = ObjDf (ro + dHit * rd);
    dHit += d;
    if (d < 0.0005 || dHit > dstFar) break;
  }
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.001, -0.001);
  v = vec4 (- ObjDf (p + e.xxx), ObjDf (p + e.xyy), ObjDf (p + e.yxy), ObjDf (p + e.yyx));
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

float RippleHt (vec2 p)
{
  vec2 q;
  float s1, s2;
  q = Rot2D (p, -0.02 * pi);
  s1 = abs (sin (4. * pi * abs (q.y + 1.5 * Fbm2 (0.7 * q))));
  s1 = (1. - s1) * (s1 + sqrt (1. - s1 * s1));
  q = Rot2D (p, 0.01 * pi);
  s2 = abs (sin (3.1 * pi * abs (q.y + 1.9 * Fbm2 (0.5 * q))));
  s2 = (1. - s2) * (s2 + sqrt (1. - s2 * s2));
  return mix (s1, s2, 0.1 + 0.8 * smoothstep (0.3, 0.7, Fbm2 (2. * p)));
}

vec3 RippleNorm (vec2 p, vec3 vn, float f)
{
  vec2 e;
  float h;
  e = vec2 (0.002, 0.);
  h = RippleHt (p);
  vn.xy = Rot2D (vn.xy, f * (RippleHt (p + e) - h));
  vn.zy = Rot2D (vn.zy, f * (RippleHt (p + e.yx) - h));
  return vn;
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 col4;
  vec3 vn, col, q;
  float dstObj, dstTun, dFac, bh, s, att;
#if ! USE_TEX
  HexVorInit ();
#endif
#if DRONES
  dSzFac = 2.;
  csProp = sin (5.3 * 2. * pi * tCur + vec2 (0.5 * pi, 0.));
#endif
  cSzFac = 0.33;
  dstTun = TunRay (ro, rd);
  dstObj = ObjRay (ro, rd);
  if (min (dstTun, dstObj) < dstFar) {
    if (dstTun < dstObj) {
      ro += rd * dstTun;
      vn = TunNf (ro);
      dFac = (1. - smoothstep (0.2, 0.5, dstTun / dstFar));
      q.xy = TrackPos (ro.z).xy;
      ro.xy -= q.xy;
      if (idObj == idWal) {
        vn = VaryNf (8. * ro, vn, 8. * dFac * dFac * (1. - 0.5 * smoothstep (0.7, 1., - vn.y)));
        s = Fbm3 (32. * ro);
        col4 = vec4 (mix (vec3 (0.4, 0.4, 0.45), vec3 (0.5, 0.4, 0.3), smoothstep (0.4, 0.5, s)), 0.05);
      } else if (idObj == idFlr) {
        s = dFac * (1. - smoothstep (-0.2, -0.1, dot (rd, vn)));
        vn = RippleNorm (ro.zx, vn, 4. * s);
        vn = VaryNf (16. * ro, vn, s);
        col4 = vec4 (vec3 (0.6, 0.35, 0.1) * (1. - 0.2 * smoothstep (0.4, 0.85,
           length (vec2 (ro.x, mod (ro.z + lmpSep, 2. * lmpSep) - lmpSep)) / tunRad)), 0.);
      }
      ro.xy += q.xy;
    } else {
      ro += rd * dstObj;
      vn = ObjNf (ro);
      if (idObj == idLmp) {
        col4 = vec4 (vec3 (1., 1., 0.7) * 0.5 * (1. - vn.y), -1.);
      } else if (idObj == idRail) {
        col4 = vec4 (0.5, 0.5, 0.55, 0.3);
      } else if (idObj == idTie) {
        col4 = vec4 (0.25, 0.15, 0., 0.);
      } else if (idObj == idArch) {
        vn = VaryNf (32. * ro, vn, 2.);
        col4 = vec4 (0.3, 0.3, 0.2, 0.05);
      } else if (idObj == idCrLev) {
        col4 = vec4 (0.5, 0.4, 0.2, 0.4);
      } else if (idObj == idCrBase) {
        col4 = vec4 (0.2, 0.5, 0.7, 0.05);
        col4.rgb *= 0.7 + 0.3 * ((qHit.y > 0.037) ? SmoothBump (0.15, 0.85, 0.02, mod (32. * qHit.x, 1.)) :
           SmoothBump (0.2, 0.8, 0.02, mod (32. * qHit.y, 1.)));
        s = length (qHit.xy * vec2 (0.4, 1.));
        if (s < 0.035) {
          if (s > 0.025) col4.rgb *= 0.6 + 0.4 * smoothstep (0.025, 0.035, s);
          else col4 = vec4 (mix (vec3 (0., 0., 1.), vec3 (1., 0.8, 0.), step (0., sin (13. * tCur))), -1.);
        }
      } else if (idObj == idCrWhl) {
        col4 = vec4 (0.25, 0.25, 0.3, 0.3);     
#if DRONES
      } else if (idObj == idDrBod) {
        col4 = vec4 (0.6, 0.7, 0.8, 0.3);
      } else if (idObj == idDrLamp) {
        col4 = vec4 (mix (vec3 (0., 1., 0.), vec3 (1., 0., 0.), step (0., sin (10. * tCur))), -1.);
      } else if (idObj == idDrCam) {
        col4 = vec4 (0.1, 0.1, 0.1, 0.3);
      } else if (idObj == idDrProp) {
        col4 = vec4 (1., 1., 0.5, 0.5);
#endif
      }
      if (col4.a >= 0.) col4.rgb *= (1. - 0.3 * smoothstep (0.4, 0.85,
           abs (mod (ro.z + lmpSep, 2. * lmpSep) - lmpSep) / tunRad));
      else col = col4.rgb;
    }
    if (col4.a >= 0.) col = col4.rgb * (0.2 + 0.1 * max (vn.y, 0.) + 0.8 * max (dot (vn, ltDir), 0.) +
       col4.a * pow (max (dot (normalize (ltDir - rd), vn), 0.), 32.));
    if (idObj == idFlr || idObj == idRail || idObj == idTie) {
      for (int k = 0; k < N_CAR; k ++) {
        col *= 0.7 + 0.3 * smoothstep (0., 0.1, length (max (abs (cSzFac * carMat[k] *
           (ro - carPos[k])).xz - vec2 (0.01, 0.4), 0.)) - 0.2);
      }
    }
    s = min (dstTun, dstObj);
    att = SmoothMin (350. / pow (s, 2.), 1., 0.5) * (1. - smoothstep (60., 70., s));
    col *= att;
  } else col = vec3 (0.);
  return clamp (mix (col, vec3 (col.b), 0.2) * mix (1., smoothstep (0., 1., Maxv3 (col)), 0.2), 0., 1.);
}

#if DRONES
void FlyerPM (float t)
{
  vec3 vd, cb, sb;
  float flPtch, flRol, flYaw;
  flPos = TrackPos (t);
  vd = TrackVel (t);
  flPtch = -0.1 * pi;
  flRol = 0.3 * TrackAcc (t).x;
  flYaw = -0.5 * pi - atan (vd.z, - vd.x);
  cb = cos (vec3 (flPtch, flYaw, flRol));
  sb = sin (vec3 (flPtch, flYaw, flRol));
  flMat = mat3 (1., 0., 0., 0., cb.x, - sb.x, 0., sb.x, cb.x) *
          mat3 (cb.z, - sb.z, 0., sb.z, cb.z, 0., 0., 0., 1.) *
          mat3 (cb.y, 0., - sb.y, 0., 1., 0., sb.y , 0., cb.y);
}
#endif

void CarPM (float t)
{
  vec3 vd, cb, sb;
  float crPtch, crRol, crYaw;
  crPos = TrackPos (t);
  vd = TrackVel (t);
  crPtch = 0.;
  crRol = 0.;
  crYaw = -0.5 * pi - atan (vd.z, - vd.x);
  cb = cos (vec3 (crPtch, crYaw, crRol));
  sb = sin (vec3 (crPtch, crYaw, crRol));
  crMat = mat3 (1., 0., 0., 0., cb.x, - sb.x, 0., sb.x, cb.x) *
          mat3 (cb.z, - sb.z, 0., sb.z, cb.z, 0., 0., 0., 1.) *
          mat3 (cb.y, 0., - sb.y, 0., 1., 0., sb.y , 0., cb.y);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 stDat;
  vec3 ro, rd, vd, col;
  vec2 canvas, uv, ori, ca, sa;
  float el, az, tunPos, zmFac, sr;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  stDat = Loadv4 (vec2 (0., 0.));
  tunPos = stDat.y;
#if USE_TEX
  txOrg = stDat.z;
  txAsp = stDat.w;
#endif
  stDat = Loadv4 (vec2 (1., 0.));
  tunRad = stDat.y;
  az = stDat.z;
  el = stDat.w;
  stDat = Loadv4 (vec2 (2., 0.));
  tCur = stDat.y;
  trkFx = 2. * pi * vec2 (0.005, 0.0087);
  trkAx = tunRad * vec2 (2.2, 1.7);
  trkFy = 2. * pi * vec2 (0.017, 0.0227);
  trkAy = tunRad * vec2 (0.13, 0.07);
  trkFw = 2. * pi * vec2 (0.05, 0.028);
  trkAw = tunRad * vec2 (0.07, 0.04);
  vd = TrackVel (tunPos);
  az += 0.5 * pi + atan (- vd.z, vd.x);
  el += asin (vd.y) - 0.02 * pi;
  zmFac = 3.5 - 0.5 * SmoothBump (0.3, 0.7, 0.1, abs (az) / pi);
#if DRONES
  for (int k = 0; k < N_DRONE; k ++) {
    FlyerPM (tunPos + sign (0.5 * pi - abs (az)) * tunRad * (1.5 + 3. * float (k)));
    dronePos[k] = flPos;
    droneMat[k] = flMat;
    dronePos[k].y += 0.1 * tunRad * (2. + ((0.5 - float (k)) * sin (0.2 * tCur)));
  }
#endif
  for (int k = 0; k < N_CAR; k ++) {
    CarPM (tunPos + tunRad * (3. + 3. * float (k)));
    carPos[k] = crPos;
    carMat[k] = crMat;
  }
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  vuMat = mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
          mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
  ro = TrackPos (tunPos) + vuMat * vec3 (0., 0.15, - 0.5) * tunRad;
  dstFar = 100.;
  lmpSep = 10.;
  ltDir = vuMat * normalize (vec3 (0., 0.5, -1.));
#if ! AA
  const float naa = 1.;
#else
  const float naa = 3.;
#endif  
  col = vec3 (0.);
  sr = 2. * mod (dot (mod (floor (0.5 * (uv + 1.) * canvas), 2.), vec2 (1.)), 2.) - 1.;
  for (float a = 0.; a < naa; a ++) {
    rd = vuMat * normalize (vec3 (uv + step (1.5, naa) * Rot2D (vec2 (0.5 / canvas.y, 0.),
       sr * (0.667 * a + 0.5) * pi), zmFac));
    rd.zx = Rot2D (rd.zx, 0.1 * atan (ro.x));
    col += (1. / naa) * ShowScene (ro, rd);
  }
  fragColor = vec4 (pow (clamp (col, 0., 1.), vec3 (0.8)), 1.);
}

float PrBoxDf (vec3 p, vec3 b)
{
  vec3 d;
  d = abs (p) - b;
  return min (max (d.x, max (d.y, d.z)), 0.) + length (max (d, 0.));
}

float PrRound2BoxDf (vec3 p, vec3 b, float r)
{
  return max (length (max (abs (p.xy) - b.xy, 0.)) - r, abs (p.z) - b.z);
}

float PrSphDf (vec3 p, float r)
{
  return length (p) - r;
}

float PrCylDf (vec3 p, float r, float h)
{
  return max (length (p.xy) - r, abs (p.z) - h);
}

float PrTorus4Df (vec3 p, float ri, float rc)
{
  vec2 q;
  q = vec2 (length (p.xy) - rc, p.z);
  q *= q;
  return sqrt (sqrt (dot (q * q, vec2 (1.)))) - ri;
}

#if ! USE_TEX

vec2 gVec[7], hVec[7];
const float sqrt3 = 1.73205;

vec2 PixToHex (vec2 p)
{
  vec3 c, r, dr;
  c.xz = vec2 ((1./sqrt3) * p.x - (1./3.) * p.y, (2./3.) * p.y);
  c.y = - c.x - c.z;
  r = floor (c + 0.5);
  dr = abs (r - c);
  r -= step (dr.yzx, dr) * step (dr.zxy, dr) * dot (r, vec3 (1.));
  return r.xz;
}

vec2 HexToPix (vec2 h)
{
  return vec2 (sqrt3 * (h.x + 0.5 * h.y), (3./2.) * h.y);
}

void HexVorInit ()
{
  vec3 e = vec3 (1., 0., -1.);
  gVec[0] = e.yy;
  gVec[1] = e.xy;
  gVec[2] = e.yx;
  gVec[3] = e.xz;
  gVec[4] = e.zy;
  gVec[5] = e.yz;
  gVec[6] = e.zx;
  for (int k = 0; k < 7; k ++) hVec[k] = HexToPix (gVec[k]);
}

vec4 HexVor (vec2 p)
{
  vec4 sd, udm;
  vec2 ip, fp, d, u;
  float amp, a;
  amp = 0.8;
  p = p.yx;
  ip = PixToHex (p);
  fp = p - HexToPix (ip);
  sd = vec4 (4.);
  udm = vec4 (4.);
