void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  vec4 mPtr, stDat, dateCur;
  vec2 canvas, pxIv;
  float tCur, tBgn, az, el, tunRad, tunPos, s, si;
#if USE_TEX
  float txOrg, txAsp;
#endif
  canvas = iResolution.xy;
  tCur = iTime;
  dateCur = iDate;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  pxIv = floor (fragCoord);
#if USE_TEX
  txAsp = 3./8.;
  if (pxIv.x >= txSize.x || pxIv.y >= txAsp * txSize.x) discard;
#else
  if (pxIv.x >= 3. || pxIv.y >= 2.) discard;
#endif
  if (iFrame <= 5) tBgn = mod (floor (337. * dateCur.w), 3600.);
  else tBgn = Loadv4 (vec2 (2., 0.)).z;
  tCur = mod (tCur, 3600.) + tBgn;
  az = 0.;
  el = 0.;
  if (mPtr.z > 0.) {
    el = clamp (el + 0.5 * pi * mPtr.y, -0.1 * pi, 0.1 * pi);
    az = az + 2. * pi * mPtr.x;
  }
  tunRad = 4.;
  tunPos = 4. * tCur;
#if USE_TEX
  HexVorInit ();
  txOrg = tunPos;
  s = 2. * pi * tunRad / txAsp;
  if (abs (az) > 0.6 * pi) txOrg -= s - tunRad;
  else if (abs (az) > 0.4 * pi) txOrg -= 0.5 * s;
  else txOrg -= tunRad;
  si = txSize.x / (2. * s);
  txOrg = floor (txOrg * si) / si;
  stDat = vec4 (TSurfHt (2. * ((s * pxIv / txSize + vec2 (txOrg, 0.)))), 0., 0., 0.);
#endif
  if (pxIv.y == 0.) {
#if USE_TEX
    if (pxIv.x == 0.) stDat.yzw = vec3 (tunPos, txOrg, txAsp);
#else
    if (pxIv.x == 0.) stDat.y = tunPos;
#endif
    else if (pxIv.x == 1.) stDat.yzw = vec3 (tunRad, az, el);
    else if (pxIv.x == 2.) stDat.yz = vec2 (tCur, tBgn);
  }
  Savev4 (pxIv, stDat, fragColor, fragCoord);
}

