float CilindroConAlas(vec3 p, float numItem)
{
    float tau = atan(1.0) * 8.0;
    vec3 pp;
    pp=p;
    p.xy *= genRotMat(iTime);/// genera la rotacion del engrane



    vec3 rep2 = opAngRep(p, tau / numItem);
    float sdsp1= sdCylinderXY(p, vec2(2.0,6.0) );
    float sdb1= sdBox(rep2, vec3(6.5,0.2,5.5));

