// Attract & Repel shader overlay - Red=Repel, Blue=Attract

float Lines(float x,float dir)
{
    //x+=sin(iTime*2.0)*1.5; // offset
    float d = 1.0-abs(x);
    float r = pow(abs(x*0.5),3.0);
	float v = 0.75 + 0.5*sin((r+dir)/0.006);
    return v * smoothstep(-0.2,0.8,d);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (iResolution.xy - 2.0*fragCoord.xy)/iResolution.y; 
    float t = iTime;

    uv += (sin(t*1.5+uv*16.0)*0.025).yx;   	// +=wibble
    if (iMouse.z>0.5)
        uv = vec2(dot(uv,uv));

    // Change X,Y,Direction,Colour every 4 seconds
    float mcount = t - mod(t,4.0);
    float cmult1 = step(8.0, mod(mcount,16.0));
    float cmult2 = step(4.0, mod(mcount,8.0));
    float speed = mix(0.035,-0.035,cmult1);
    vec3 col = mix(vec3(0.4,0.5,0.9), vec3(0.9,0.2,0.2), cmult1);
    float v = mix(uv.x,uv.y,cmult2);
    float c = Lines(v,t*speed);
	fragColor = vec4(col*c,1.0);
}
