mat3 getRotZ(float theta)
{
    float c=cos(theta);
    float s=sin(theta);

    return mat3( c ,s, 0.,
                -s, c, 0.,
                 0.,0.,1.);
}

mat3 getRotY(float theta)
{

    float c=cos(theta);
    float s=sin(theta);


    return  mat3(c, 0., -s,
        	     0.,1., 0.,
             	 s, 0., c);
}

mat3 getRotX(float theta)
{
    float c=cos(theta);
    float s=sin(theta);


    return mat3(1., 0. ,0. ,
                0., c,  s ,
                0.,-s,  c );
}


mat3 getRotZInv(float theta)
{
    float c=cos(theta);
    float s=sin(theta);

    return mat3( c ,-s, 0.,
                 s, c, 0.,
                 0.,0.,1.);
}



vec4 qMult(vec4 q1,vec4 q2){
    return vec4(q1.x*q2.x-dot(q1.yzw,q2.yzw),
                q1.x*q2.yzw+q2.x*q1.yzw+cross(q1.yzw,q2.yzw)); 
}


vec3 rotate(vec3 v,float angle,vec3 axis){ 
    axis=normalize(axis);
    vec4 p=vec4(cos(angle/2.),sin(angle/2.)*axis);
    vec4 r=qMult(p,vec4(0.,v));
    return qMult(r,vec4(p.x,-p.yzw)).yzw;
}

#define ROT(t) mat2(cos(t), sin(t), -sin(t), cos(t))


float linearstep(float a, float b, float x)
{
    // Linear version of smoothstep, why is that not a thing of glsl functions yet?
    return clamp((x - a) / (b - a), 0.0, 1.0);
}

//moving figures
vec3 move( vec3 p, vec3 center, mat3 rot )
{
    vec3 q = vec3(p - center);
    q = inverse(rot)*q;
    return q;
}


