#define SCALE 5.0

#define SCENE_ROTATION_SPEED -0.1

#define PLANE_ROTATION_SPEED 0.3

#define EDGE_SHARPNESS 20000.0

#define AIR_PERSPECTIVE 2.0

#define rotate(a) mat2(cos(a),-sin(a),sin(a),cos(a))

float getChessboardLuminance(vec2 point) {
    float value =
        (1.0 - pow(sin(point.x), EDGE_SHARPNESS))
        * sign(cos(point.x));
    value *=
        (1.0 - pow(sin(point.y), EDGE_SHARPNESS))
        * sign(cos(point.y));
    return (value + 1.0) / 2.0;    
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 pixel = (fragCoord -.5 * iResolution.xy) / iResolution.y;
    pixel *= rotate(iTime * SCENE_ROTATION_SPEED);
    vec2 trans = vec2(pixel.x / pixel.y, 1.0 / pixel.y);
    trans *= rotate(iTime * PLANE_ROTATION_SPEED);    
    trans *= SCALE;

	float luminance = getChessboardLuminance(trans);

    // apply air perspective
    luminance *= pow(abs(pixel.y * 2.0), AIR_PERSPECTIVE);
    fragColor = vec4(vec3(luminance),1.0);
}
