//Ethan Alexander Shulman 2019, made on livestream at twitch.tv/ethanshulman

#define time iTime

float voronoi(vec2 u, float i) {
	#define l(i) length(fract(abs(u*.01+fract(i*vec2(1,8))+sin(u.yx*.2+i*8.)*.02+sin(u*.06+1.6+i*6.)*.1))-.5)
	return l(i);
}
float triwave(float t) {
	return t*2.-max(0.,t*4.-2.);
}

void mainImage( out vec4 o, in vec2 u )
{
    vec2 uv = (u*2.-iResolution.xy)/iResolution.y;
	float s = 0.,scal = 10.+triwave(fract(time*.02))*200.;
	for (int i = 0; i < 8; i++) {
		s += (sin(voronoi(uv*scal*float(i+1),float(i)/8.0)*pow(float(i+2),2.)-time*pow(float(i+1),1.2))*.5+.5)/float(i+1);
	}
	s = pow(s*.35,2.);
	o = vec4(s,s,s,1);
}
