// Moving White Blurry Circle
// inspired by The Art od Code - Shadertoy Tutorials
// https://www.youtube.com/playlist?list=PLGmrMu-IwbguU_nY2egTFmlg691DN7uE5

float Circle(vec2 uv, float r, float blur) {
    float d = length(uv);
    float c = smoothstep(r, r-blur, d);

    return c;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv1 = fragCoord.xy / iResolution.xy; 	// 0. <> 1.
    vec2 uv2 = fragCoord.xy / iResolution.xy; 	// 0. <> 1.

    float ldiffx1 = .2;							// choose the shape of the Lissajous curve
 	float ldiffy1 = .17;						// 1. and 1. = ellipse
    float mx1 = .5+(sin(iTime*ldiffx1)*.35);	// create a modulated
    float my1 = .5+(cos(iTime*ldiffy1)*.22);	// Lissajous Curve
    float mr1 = .15+(sin(iTime*.39)*.1);		// modulate the radius of the circle

    float ldiffx2 = .6;							// choose the shape of the Lissajous curve
 	float ldiffy2 = .45;						// 1. and 1. = ellipse
    float mx2 = .5+(sin(iTime*ldiffx2)*.25);	// create a modulated
    float my2 = .5+(cos(iTime*ldiffy2)*.24);	// Lissajous Curve
    float mr2 = .15+(sin(iTime*.33)*.13);		// modulate the radius of the circle

    uv1 -= vec2(mx1,my1);							// -0.5 <> 0.5
    uv1.x *= iResolution.x/iResolution.y;

    uv2 -= vec2(mx2,my2);							// -0.5 <> 0.5
    uv2.x *= iResolution.x/iResolution.y;

    float mask = Circle(uv1, mr1, .2-(mr1*.12));
	mask += Circle(uv2, mr2, .2-(mr2*.12));

    fragColor = vec4(vec3(mask), 1.0);
}
