// Hidden Image Illusion
// by @P_Malin
//
// Saccades when looking around this image should make the hidden texture appear. Or it might just give you a headache.

#define PI acos(-1.0)
#define TAU (PI * 2.0)

#define AA 16.

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord/iResolution.xy;    
    float shade = 0.0;
    float total = 0.0;

    for ( float x=0.; x<AA; x++ )
    {
        vec2 uv2 = uv + vec2(x/AA,0.0) / iResolution.xy;

        float barCount = 100.0;

        //shade = cos(barCount * uv.x * TAU)*0.5+0.5;    
        float bar = step( fract ( uv2.x * barCount ), 0.5 );

        vec3 tex = texture( iChannel0, uv2 ).rgb;
        float Y = dot(tex, vec3(0.2126, 0.7152, 0.0722));      

        float strength = 0.1;
        shade += mix( bar, Y, strength );
        total += 1.0f;
    }    

    shade = shade / total;

    //shade = shade * (1.0 - Y * strength);
    vec3 col = vec3(shade);

    float p = abs ( fract( iTime * 0.3 ) * 2.0 - 1.0 );
    if ( uv.y > 0.9 && uv.y < 0.94 )
    {
        if ( uv.x > (p - 0.01) && uv.x < (p+0.01) )
        {
        	col = vec3(1,0,0);
        }
    }

    if ( iChannelResolution[0].x <= 0.0f )
    {
        col = vec3(0.0);
    }


    fragColor = vec4(col,1.0);
}
