vec4 p(float n,float p,float s,float r,vec2 g,vec2 t){
    s+=3.6*(sin(n+p+s+iTime)*0.5+0.75)/1000.;
    n*=2.;
    vec2 pos = vec2(0.5+(sin(iTime)*0.5+0.7)*sin((0.5+0.7)*iTime*s+p)*n*0.25,0.5+(sin(iTime)*0.5+0.7)*cos(iTime*s+p)*n*0.25);
    vec3 col = vec3(20./distance(t.xy*pos,g.xy)*(sin(n+p+s+iTime)*0.5+0.75));
    vec4 d = vec4(vec3(sin(iTime*r)*0.5+0.5,sin(iTime*4.24+r)*0.5+0.5,cos(iTime*1.3*r)*0.5+0.5)*0.5*col,col);

    return d;
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 full = vec2(iResolution.x*(iResolution.y/iResolution.x),iResolution.y);
    vec2 pos = fragCoord.xy+vec2(-0.5*full.x,.0);
    vec4 col;
    col += p(0.5,0.,1.,1.,pos.xy, full.xy);
    col += p(0.74,1.,1.3,3.,pos.xy, full.xy);
    col += p(0.53,43.,0.7,.2,pos.xy, full.xy);
    col += p(0.37,0.4,1.7,.9,pos.xy, full.xy);
    col += p(0.55,1.,1.73,1.4,pos.xy, full.xy);
    col += p(0.52,4.1,0.37,.3,pos.xy, full.xy);
    col += p(0.95,0.,1.,41.,pos.xy, full.xy);
    col += p(0.274,1.8,1.93,6.3,pos.xy, full.xy);
    col += p(0.653,33.55,0.297,.2,pos.xy, full.xy);
    col += p(0.537,0.24,1.87,.79,pos.xy, full.xy);
    col += p(0.755,1.7,1.5733,1.44,pos.xy, full.xy);
    col += p(0.452,4.31,0.337,.63,pos.xy, full.xy);

    fragColor = col;
}
