// License Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License.
#define PI 3.14159265359
#define TWO_PI 6.28318530718
// 


//// Phong Model Week 8
vec3 ambientColor = vec3(0.9, 0.15, 0.2);
vec3 diffuseColor = vec3(0.9, 0.6, 0.8);
vec3 specularColor = vec3(1.0, 1.0, 1.0);
vec3 lightDir = normalize(vec3(0.0, 4.0, 5.0));
////

/// Color Week 3
vec3 spherePos = vec3(0.0, 0.5, 0.0);
///

float polygon(vec2 uv, vec2 pos, float size, float blur, int sides){
    uv = uv-pos;
    //Projection Week 2
    float angle = atan(uv.x,uv.y)+PI;
    float rad = TWO_PI/float(sides);
    float dist = cos(floor(0.5+angle/rad)*rad-angle)*length(uv);
    float poly = smoothstep(size, size-blur, dist);
    ///
    return poly;
}


vec4 moveRectangle(vec4 try,vec2 uv){
	/// Transformation Week 5 
    vec2 translate = vec2(cos(iTime),sin(iTime));
    uv += translate*0.35;
    ///
    float rectangle = polygon(uv, vec2(0.0,0.0), 0.1, 0.001, 4);
    // Color Week 3
    vec3 giveColor = vec3(1.0,1.0,0.0) * rectangle;
    try = vec4(giveColor,1.0);
    ///

    return try;

}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    //Projection Week 2
    vec2 p = (-iResolution.xy + 2.0 * fragCoord.xy) / iResolution.y;
    ///
    //Color Week 3
    vec3 eye = vec3(.5, 1.0, 2.0);
    ///

    //Transformation Week 5 + Event Handler Week 4
    vec2 rot = 6.2831 * (vec2(0.1 + iTime * 0.25, 0.0) + vec2(1.0, 0.0) * (iMouse.xy - iResolution.xy * 0.25) / iResolution.x);
    eye.yz = cos(rot.y) * eye.yz + sin(rot.y) * eye.zy * vec2(-1.0, 1.0);
    eye.xz = cos(rot.x) * eye.xz + sin(rot.x) * eye.zx * vec2(1.0, -1.0);
    ////

    //Color Week 3 + Phong Week 8
    vec3 ro = eye;
    vec3 ta = vec3(0.0, 0.2, 1.0);
    ///

    //Phong Week 8 
    vec3 cw = normalize(ta - eye);
    vec3 cu = normalize(cross(vec3(0.0, 1.0, 0.0), cw));
    vec3 cv = normalize(cross(cw, cu));
    mat3 cam = mat3(cu, cv, cw);

    vec3 rd = cam * normalize(vec3(p.xy, 1.5));
    ///

    vec3 color;

    float tmin = 0.1;
    float tmax = 50.0;

    ///Transformation Week 5
    vec2 translate = vec2(cos(iTime),sin(iTime));
    p += translate*0.35;
    float rectangle = polygon(p, vec2(0.0,0.0), 0.5, 0.001, 4);
    ////

    float t = rectangle;

    if (t > tmin && t < tmax) {
        /// Week 8 Phong
        vec3 pos = ro + rd * t;
        vec3 norm = normalize(pos - spherePos);
        float occ = 0.5 + 0.5 * norm.y;

        float amb = clamp(0.5 + 0.5 * norm.y, 0.0, 1.0);
        float dif = clamp(dot(lightDir, norm), 0.0, 1.0);

        vec3 h = normalize(-rd + lightDir);
        float spe = pow(clamp(dot(h, norm), 0.0, 1.0), 64.0);

        color = amb * ambientColor * occ;
        color += dif * diffuseColor * occ;
        color += dif * spe * specularColor * occ;
        ////
    }
	/// Color Week 3
    vec3 gamma = vec3(1.0 / 2.2);
    fragColor += vec4(pow(color, gamma), 1.0);
    ////
}
