// 3-story variant of https://shadertoy.com/view/WslSW4
// textured variant of https://shadertoy.com/view/3sXSW8

//#define C(x,y) 2.*texelFetch(iChannel1, ivec2(U+vec2(x,y)),0).r//
#define C(x,y) sin(1e3*length (ceil(U)+vec2(x,y)))//
//
void mainImage(out vec4 O, vec2 u) {
    vec2 R = iResolution.xy, w,v,
         U =  ( u+u - R ) / R.y * mat2(1,-2,1,2);
    U.y += iTime;
    R = fract( U *= 5. );
    bool r = R.x+R.y > 1.;
    w = .5+cos(12.6*R*mat2(1,1,0,1));
    vec4 a = max(w.xxxx,w.y),
         b = texture(iChannel1,U/8.).grba,
         c = .4+.1*cos(12.6*(R.y+R.xxxx)),
         d = 1.7*texture(iChannel0,U/4.);
    O  =  C(3,-3)>.9       ? d         /* 3rd story */
        : C(3,-2)>.9 &&  r ? a*2.
        : C(2,-3)>.9 && !r ? c
        : C(2,-2)>.9 ?   r ? c : a*2.
        : C(2,-2)>.7       ? d*.7      /* 2nd story */
        : C(2,-1)>.7 &&  r ? a
        : C(1,-2)>.7 && !r ? c
        : C(1,-1)>.7 ?   r ? c : a
        : C(1,-1)>.5       ? d*.5      /* 1st story */
        : C(1, 0)>.5 &&  r ? a*.7
        : C(0,-1)>.5 && !r ? c*.9
        : C(0, 0)>.5 ?   r ? c*.9 : a*.7
        : C(-1,0)>.5       ? b*.5      /* ground lev*/
        :                    b;
}
