// Suguru, using the generic workflow of https://shadertoy.com/view/3t2XD3

void mainImage( out vec4 O, vec2 u ) // === manage display
{
    float s = cellsize;
    vec2 U = u - offset,
         C = mod(U,s), G = ceil(U /= s);    // G: cell ID   C: pixel in cell
    vec4 data = T(G);                       // .x: value  .y: type (block ID)

                                            // --- draw grid
    if (   C.x < border+thick && data.y != T(G-vec2(1,0)).y // thick borders at block transitions
        || C.y < border+thick && data.y != T(G-vec2(0,1)).y ) border+=thick, U+=thick/2./s; 
    O = vec4( min(C.x,C.y) > border && U.x > border/s && U.x < N+border/s );
  //O = vec4( smoothstep(-.5,.5,min(d - border, s*min (U.x,N+border/s-U.x))) ); // AA version

    if ( data.y >= 0.)                      // display cell type
        O *= mix(vec4(1),hue(data.y/10.),.1);   // option: id -> bg = hue
    else if (data.y == -2.) O -= O;         // black cell (e.g. crosswords)

    if ( T(0).zw==G && fract(3.*iTime)<.7 ) // --- flash current cell      
        O.gb *= .8;
                                            // --- display cell value
    float v = char( fract(U)-border/2./s, 
                    int( minKey+ (data.x<-1. ? -2.-data.x:data.x) ) ).x;
    if (data.x >= 0. )  if ( data.z > 0. ) O -= v; else O.rg -= v;
    if (data.x < -1. )  O.gb -= v;

    if (U.x>N) O += pInt(1.7*R.x/offset.x*(u-vec2(R.x-offset.x,0))/R.y, iTime).x; // --- timer

    O = clamp(O,0.,1.);    
    if (O==vec4(0)) O.g = Tb(u).x; else O.rb -= Tb(u).x;  // --- add draft layer  
}

// === manage grid and interactions

void mainImage( out vec4 O, vec2 U )
{
    U -= .5;

    if (iFrame==0) {                 // --- init grid and controls
        O = vec4(-1);                // O.x = value  O.y = cell type
        // init values:              // O.z > 0 : not erasable
#define set(X,Y,v) if (U==vec2(X,Y)) O.x = v, O.z = 1.
        set(6,8, 5.); // hardcoded init grid: no auto-generator here :-)
        set(1,7, 5.);
        set(2,7, 1.);
        set(7,7, 2.);
        set(4,6, 4.);
        set(8,5, 3.);
        set(1,3, 3.);
        set(2,3, 4.);
        set(8,2, 2.);
        set(2,1, 1.);
        set(6,1, 2.);

        // draw your blocks ID here:
ivec2 I=ivec2(U),V; // helpers: Cell, Horiz, Vert, X-shape, Block. choose unique ID ( = hue )
#define C(X,Y,v)   if (I==ivec2(X,Y)) O.y = v
#define H(X,Y,L,v) if (I.y==Y && I.x>=X && I.x<X+L) O.y = v
#define V(X,Y,H,v) if (I.x==X && I.y>=Y && I.y<Y+H) O.y = v
#define X(X,Y,v)   V= abs(I-ivec2(X,Y)); if (I.x==X && V.y<=1 || I.y==Y && V.x<=1 ) O.y = v 
#define B(X,Y,C,v) {vec2 V= abs(U-vec2(X,Y)); if (max(V.x,V.y) <= C ) O.y = v;}
        X(3,6, 0.);
        X(6,6, 9.);
        X(4,3, 4.);
        X(6,2, 0.1);
        X(2,4, 8.); C(1,5, 8.); // ( 1 cell will be overwritten later ;-) )
        C(7,1, 1.3);
        V(1,1,4,  1.); C(2,1, 1.);
        H(2,2,2,  3.); H(3,1,3, 3.); 
        V(8,1,3,  5.); C(7,3, 5.);
        H(4,5,2, 1.1); H(5,4,3, 1.1);
        V(8,4,4, 3.1); C(7,5, 3.1);
        B(1.5,7.5,.5, 4.1); C(1,6, 4.1);
        H(3,8,2, 1.2); H(4,7,2, 1.2);
        H(5,8,4, 5.1); C(7,7, 5.1);

        // for demo, init cur cell:
        if (U==vec2(0)) O.zw = vec2(4,3);
        return;
    }

    O = T(U);                        // recover prev state -----------------

    if (U==vec2(0)) {                // --- control active cell
        if keyDown(37) if (--O.z < 1.) O.x += N;        //  with arrow keys
        if keyDown(39) if (++O.z > N ) O.x -= N;
        if keyDown(38) if (++O.w > N ) O.y -= N;
        if keyDown(40) if (--O.w < 1.) O.y += N;
        if ( iMouse.z>0. ) O.zw = ceil((iMouse.xy-offset)/cellsize); // or mouse
    }

    if ( U == T(0).zw ) {            // --- write keyboard value in cell
        if ( O.z > 0. ) return;      // can't edit initial values 
        for( float i = minKey; i <= maxKey; i++)
            if keyDown(i) {          // entry validation (special case for shift (16) on letters)
                i = i-minKey +(maxKey>96. && i>64. && keyClick(16)?32.:0.);
                for (int k=0; k<9; k++)     // check 3x3 neighborhood
                    if (T(U+vec2(k%3-1,k/3-1)).x == i) { O.x = -i-2.; return;} // already in 3x3 neighborhood
                for (float x=1.; x<=N; x++) // check block
                  for (float y=1.; y<=N; y++) {
                    vec4 data = T(vec2(x,y));
                    if (data.y==T(U).y && data.x == i) { O.x = -i-2.; return;} // already in block
                  }
                O.x = i;                    // valid entry
                return;
            }
        if (keyDown(32) || keyDown(8) || keyDown(46)) O.x = -1.;  // erase cell
    }         
}

// === free draw notepad superimposed to grid ( user draft notes )

float line(vec2 p, vec2 a,vec2 b) { // draw line segment. From  https://www.shadertoy.com/view/4dcfW8
    float h = dot(p-=a, b-=a) / dot(b, b);                  // proj coord on line
    return h==clamp(h, 0., 1.) ? max(0., 1.-length(p - b * h)) : 0.; // length = dist to line
}

void mainImage( out vec4 O, vec2 U )
{
    O = T(U);
    if (iFrame==0) { O = vec4(0); if (U==vec2(.5)) O.zw = vec2(-1); }

    if (U==vec2(.5)) O.zw = iMouse.z>0. ? iMouse.xy : vec2(-1);    // last curve pos or -1
    if (iMouse.z>0. && T(0).z>0. ) O += line(U,T(0).zw,iMouse.xy); // draw from prev mouse pos

    vec2 G = ceil( (U-offset)/cellsize ), C = Tb(0).zw;            // erase cell or margin
    if (   ( G == C || (G.x<1. && C.x<1.)  || ( G.x>N && C.x>N ) )
        && ( keyDown(32) || keyDown(8) || keyDown(46)) ) O -= O; 
}
