// golfing 1627 chars yuntaRobo's cables2 https://www.shadertoy.com/view/wlKXWc
// -10 by xavierseb

#define L length
void mainImage( out vec4 O, vec2 U ) {
    vec3 R = iResolution,
         P = vec3(0,0,10), p,
         D = vec3( U+U-R.xy, -3.*min(R.x, R.y) ); D /= L(D);

    for (float A=0., z = 0.; z < 15. && A<20.; z += 15./128.) {
        p = P;
        p.yz *= mat2(cos(3.14/5. + vec4(0,11,33,0) ) );
        p.y -= .5;
        float d = 1e2, N=50., s, r, i=0., a=i, t = iTime;

        R = vec3( cos(t), 0, sin(t) ) * 3.;              // reaction
        s = L(R-p);
        p += (R-p)/s / exp(s);

        for ( r = atan(p.z, p.x) * 3. ; i < N; i++)      // cables
            r += .5/N * 6.3,
            s = .5 + sin( i * 1.618 * 6.28) /4. + sin(t + i ) * .1,
            s = L( vec2(L(p.xz) + cos(r) * s - 3., p.y + sin(r) * s) ) - .035,
            s < d ? d=s, a=i : a;

        s = L(R-p) - .1;                                 // sphere
        s < d ? d=s, a=0. : a;

        if (d < .01) {
            s = fract(a * 1.618);
            O = s > .1 && s < .45 ? vec4(.175, .1, .1, 1) 
              : s < .05 ? vec4(1) 
              : mix( vec4(.1, .1, .5, 1), vec4(0, 0, 1, 1), s ) ;
            O = sqrt( O / exp(z) );
            return;
        }

        A += d *= .6;
        P += D * d;
    }

    O = abs(D.yyyy);
}
