// reusing hexastairs  https://shadertoy.com/view/3sGfWm
float ease( float x ) {                              // rewritten from https://shadertoy.com/view/3ddBWB
	return x > .5 ? x = 1.-x, 1. - 4.* x*x*x         // stepier than smoothstep or .5-.5*cos                  :                4.* x*x*x ;
                  :                4.* x*x*x ;
}

void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy,
         U = 1.5* ( 2.*u  - R ) / R.y;

    float t = iTime/2., s, l;
    U *= mat2( cos( 3.1416*( floor(t)+ease(fract(t)) + vec4(0,.5,-.5,0)) ) ); // rotation
    l = length(U+vec2(.18,-.94));                          // for sphere
    U += R/R.y;

    U *= mat2(1,0,.5,.87);                                 // parallelogram frame

    vec3  V = vec3( U, U.y-U.x +3.);                       // 3 axial coords
    ivec3 I = ivec3(floor(V));
          I += I.yzx;
    I.x += 4; I /= 3;                                      // I.xy = hexagon id
 // V = mod( V + vec3( I.y, I.y+I.x, I.x ), 2. ).yzx;      // local coords
    V.xy = mod( V.yz + vec2( I.y+I.x, I.x ), 2. );         // local coords

    O = mix(
        vec4( 
          I.xy==ivec2(2,1)                                 // central cube
            ? s = mod(4.5*V.y-1.,2.)-1.,                   // strip slope  for stairs. Side dents below
              abs( .1+2.*V.x-V.y +(abs(s)-5.5)/4.5 ) > .9 ? .5 : s < 0. ? .3 : 1. // draw stairs
            : 0.                                           // background
        ),
        vec4(sqrt(max(0.,1.-l/.1)),0,0,0), smoothstep(4./R.y,0., l-.1) ); // red sphere
}
