vec2 Center = vec2(0.5,0.5);

float Rad = 1.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec4 FinalColor = vec4( 0.5, 0.5, 1.0, 1.0 );

    float Distance = sqrt((uv.x - Center.x)*(uv.x - Center.x) +
                            (uv.y - Center.y)*(uv.y - Center.y)); 

    FinalColor.xy = (uv - Center)/ Distance *2.0 *3.14159265 * Rad ;

    FinalColor.xy = cos(FinalColor.xy + iTime);

    FinalColor.xy = FinalColor.xy*0.5 + 0.5;


    // Output to screen
    fragColor = FinalColor;
}
