#define iM iMouse
#define iR iResolution

#define vec1 float

//code of this shader merges map()==remap() and tonemap() by calling a shared subroutine mapu()
//without using or showing ANY of that.
//the remap() function is not used by this shader.
//the toemap() function is not used by this shader.
//i just wanted to store this memo-code in a tiny unrelated shader to load fast.
//
//map() use case:
//whenever a shader buffers screenspace coords
//, and you want to translate between different resolutions
//, you may need map()=remap()
//
//this shader shows more in terms of simplicity

/*
//for example:
//Ãou buffer [previous] resolution=get(BufferAddressOfPreviousResolution)
//You want to rescale to a current iResolution.xy (for incremental Averaging (within TemporalReprojection))
//And You want to rescale from and to a normalized Uv coord.
//
//for example, you want to pixelate the resolution of your scene by any percentage, by remapping to a lower resolution.

//We have 3  different domains, and a var with the same RELATIVE point in them.
fragCoordNow___domain=[    vec2( 0),iResolution.xy]
fragCoordPrev__domain=[    vec2( 0),   previous.xy] previous.xy=iResolution.xy, but buffered in prefious frame, and read 1 frame later.
       uv______domain=[-aspectRatio,aspectRatio.xy] aspectRatio.xy=vec2(1,iResolution.y/iResolution.x) (or was it its inverse?)

//
//We can use map() translate between these 3 domains:
vec2 uv           =map(fragCoordNow  ,        vec2( 0),iResolution.xy , -aspectRatio.xy,aspectRatio.xy) //==frame(uvNow)
vec2 uv           =map(fragCoordPrev ,        vec2( 0),    revious.xy , -aspectRatio.xy,aspectRatio.xy)
//
vec2 fragCoordNow =map(fragCoordPrev ,        vec2( 0),   previous.xy , vec2(0),iResolution.xy)
vec2 fragCoordNow =map(uv            , -aspectRatio.xy,aspectRatio.xy , vec2(0),iResolution.xy)
//
vec2 fragCoordPrev=map(uv            , -aspectRatio.xy,aspectRatio.xy , vec2(0),previous.xy)
vec2 fragCoordPrev=map(fragCoordNow  ,        vec2( 0),iResolution.xy , vec2(0),previous.xy)
*/

//https://www.shadertoy.com/view/WlGyzR
//defines are used to do vec2 vec3 vec4 inputs in parallel (and also, because a special case is a subroutine here (kinda strange))
//mapu() == (unholy core) union of tonemap() and map()remap() 
#define mapu(a,b,c,d,e) (a)*(b)/((c)+(d)*(e))
//
//map() returns [x] of domain[a,b] (current resolution) remap()ped to domain [c,d] (previous resolution), as if a=c && b=d;transform [a,b]->[c,d], (to remap/reproject from current to previous resolution for incremental averaging buffers)
#define map(x,a,b,c,d) mapu(x,1.,0.,a+d-(b)-(c))+(c)

//common special vases of the above general cases are:

//common quadratic gamma correction tonemap:
vec3 tonemap(vec3 x){return mapu(x,2.51*x+.06,.14,x,.59+2.43*x);}

//common special case remap() 
//return [x] of domain[a,b] remap()ped to domain [0,1], as if a=0 && b=1;transform [a,b]->[0,1] == remap(x,a,b,0.,1.) 
//as in smoothstep(a,b,x) https://www.khronos.org/registry/OpenGL-Refpages/gl4/html/smoothstep.xhtml
#define maps(x,a,b) (x-(a))/(b-(a))
//#define map(x,a,b,c,d) (maps(x,a,b)*(d-(c))+c) //convoluted alternative

//map(), but [ d == -c ] 
//to translate from Min&&maxBoundaries to Center&&width
#define mapm(x,a,b,d) (maps(x,a,b)*(d+d)-(d))
//map(), but [ b == -a ] 
//to translate from Center&&width to Min&&maxBoundaries
#define mapc(x,b,c,d) mapu(x,1.,0.,a+a+d-(c))+(c)


//remap u.x and u.y (==fragCoord.xy) to [-1,1]*s (also unsquashing aspectRatio)
vec2 frame(vec2 u,vec2  s){s*=vec2(iR.x/iR.y,1);return mapm(u,vec2(0),iR.xy,s);}
vec2 frame(vec2 u,float s){return frame(u,vec2(s));} // [0,iR.xy] -> [-1,1]
vec2 frame(vec2 u        ){return frame(u,1.);}//default zoom is 1.
//frame(u) is identical to: {u=u/iR.xy;u=u*2.-1.;u.x*=iR.x/iR.y;return u;}

float mi(vec2 a){return min(a.x,a.y);}
float mi(vec4 a){return min(mi(a.xy),mi(a.zw));}
//#define ma(a) (-(mi(-(a))))

