float sdRegularPolygon(in vec2 p, in float r, in float n) {
    float a = 6.28 / n, ha = 0.5 * a, hr = 0.5 * r, c = cos(a), s = sin(a);
    float e = hr * length(vec2(c - 1.0, s)), er = hr * length(vec2(c + 1.0, s));
    p = sin(mod(atan(p.y, p.x) + ha, a) - ha + vec2(1.57, 0.0)) * length(p);
    return length(vec2(p.x -= er, max(0.0, abs(p.y) - e))) * sign(p.x);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 center = 0.5 * iResolution.xy;
    vec2 mouse = (iMouse.xy - center) / iResolution.y;
    vec2 uv = (fragCoord - center) / iResolution.y;
    float unit = 2.0 / iResolution.y;
    float time = iTime * 2.0;

    float r = mix(0.2, 0.3, 0.5 + 0.5 * cos(iTime));
    float n = mix(3.0, 12.0, 0.5 + 0.5 * sin(iTime));
    float d = sdRegularPolygon(uv, r, n);

    // Nice colors from iq
    vec3 color = 1.0 - sign(d) * vec3(0.1, 0.4, 0.7);
	color *= 1.0 - exp(-4.0 * abs(d));
	color *= 0.8 + 0.2 * cos(140.0 * d);
	color = mix(color, vec3(1.0), 1.0 - smoothstep(0.0, 0.015, abs(d)));

    fragColor = vec4(color, 1.0);
}
