//=======================================================
//= Slope Distribution Function : GGX Isotropic 
//=======================================================
//== Slope distributions :
//== *- Beckmann Isotropic 	 : https://www.shadertoy.com/view/WtGXRt
//== *- Beckmann Anisotropic : https://www.shadertoy.com/view/WlGXRt
//== *- GGX Isotropic 		 : https://www.shadertoy.com/view/WlKXDm
//== *- GGX Anisotropic 	 : https://www.shadertoy.com/view/3tyXRt
//== In-Engine ready NDFs:
//== *- GGX Isotropic 	     : https://www.shadertoy.com/view/flsyWX
//== *- GGX Anisotropic 	 : https://www.shadertoy.com/view/NtlyWX
//== *- Beckmann Isotropic   : https://www.shadertoy.com/view/fllcWX
//== *- Beckmann Anisotropic : https://www.shadertoy.com/view/ftsyWX
//== Related stuff:
//== *- Student-t Aniso NDF  : https://www.shadertoy.com/view/sdjXWw
//== *- Specular AA          : https://www.shadertoy.com/view/WssyR7
//== *- LEADR mapping        : https://www.shadertoy.com/view/WdXfRn
//=======================================================
//== Remapping : 
//== *- roughness 	-> alpha = clamp(0.1+uiSlider(0),0.,0.8 )^2;
//== *- metallic 	-> 0 or 1
//== *- reflectance used for non-metallic F0 (Filament)
//==    -> F0 = 0.16*reflectance*reflectance*(1.0-metallic) + base_color*metallic;
//=======================================================
//== Implementations details :  
//== *- Computations are done in tangent space (PBRT-like)
//== *- Code can be a lot simplified but I wanted a verbose/self-explained code 
//== *- Code could have more checks (non-zero length before normalization, etc.)
//== *- NDFs are expressed using the slope distribution functions P22
//== *- Fresnel Term uses the Schlick Approximation
//== *- Some equations are written with the lovely style of @valentingalea (https://www.shadertoy.com/view/XtBXDz)
//== 	( again it's a personal taste :D )
//== *- Checked P22() and D() by numerical integration
//==    Checked Lambda with the Weak White Furnace Test
//=======================================================
//== Using some wonderful ressources on BRDFs/NDFs/Masking :
//== *- Xavier Chermain's thesis (FR): https://tel.archives-ouvertes.fr/tel-02446425/document
//== *- Jonathan Dupuy's thesis  (EN): https://hal.archives-ouvertes.fr/tel-01291974v2/document
//== *- Eric Heitz's thesis 	 (FR): https://tel.archives-ouvertes.fr/tel-01073518v2/document
//== *- Eric Heitz : Understanding the Masking-Shadowing Function in Microfacet-Based BRDFs
//== 	(PDF + SLIDES) : http://jcgt.org/published/0003/02/03/ 
//== *- PBRT (book : http://www.pbr-book.org/ | code : https://github.com/mmp/pbrt-v3/ )
//== *- Filament documentation : https://google.github.io/filament/Filament.html
//== *- Self Shadow blog : https://blog.selfshadow.com/
//=======================================================
//== I also used some code from :
//== *- @XT95's "UI easy to integrate" on Shadertoy 	  
//=   	https://www.shadertoy.com/view/ldKSDm				
//== *- @nimitz's "Cheap orthonormal basis" on Shadertoy
//==	https://www.shadertoy.com/view/4sSSW3
//== *- @iq's intersectors ( Sphere )
//== 	https://iquilezles.org/articles/intersectors
//=======================================================
//== Arthur Cavalier
//=======================================================


//-----------------------------------------------------------------------------
//-- Constants ----------------------------------------------------------------
const bool  USE_DIFFUSE_TERM = true;

const vec4  UNIT_SPHERE     = vec4(0.,0.,0.,1.);
const float LIGHT_INTENSITY = 2.;
const vec3  LIGHT_DIRECTION = normalize(vec3(-0.5,1.5,0.1));
const vec3  BACKGROUND_GREY = vec3(0.0);

//-----------------------------------------------------------------------------
//-- GGX Distribution ---------------------------------------------------------
float p22_ggx_isotropic(float x, float y, float alpha)
{
    float x_sqr = x*x;
    float y_sqr = y*y;
    float alpha_sqr = alpha*alpha;
    float denom = ( 1. + (x_sqr/alpha_sqr) + (y_sqr/alpha_sqr) ); 
    return( 
                            1.
    / //--------------------------------------------------
           (( m_pi * alpha_sqr) * (denom*denom))
    );
}

float ndf_ggx_isotropic(vec3 omega_h, float alpha)
{
    float slope_x = - omega_h.x / omega_h.z;
    float slope_y = - omega_h.y / omega_h.z;
    float cos_theta = cos_theta(omega_h);
    float cos_2_theta = cos_theta * cos_theta;
    float cos_4_theta = cos_2_theta * cos_2_theta;
    float ggx_p22 = p22_ggx_isotropic(slope_x,slope_y,alpha);
    return(
                  ggx_p22
    / //---------------------------
                cos_4_theta
    );
}

float lambda_ggx_isotropic(vec3 omega, float alpha)
{
    float a = 1. / (alpha * tan_theta(omega));  
    return( (-1. + sqrt(1.+1./(a*a))) * 0.5 ); 
}


//-----------------------------------------------------------------------------
//-- Fresnel ------------------------------------------------------------------
vec3 fresnel_schlick(in float wo_dot_wh, in vec3 F0)
{
    return F0 + (1. - F0) * pow(1. - wo_dot_wh, 5.);
}

//-----------------------------------------------------------------------------
//-- World Functions ----------------------------------------------------------
float 	get_light_intensity()            { return LIGHT_INTENSITY; }
vec3 	get_light_direction()            { return LIGHT_DIRECTION; }
vec3 	get_background_color(in vec3 rd) { return BACKGROUND_GREY; }

//-----------------------------------------------------------------------------
//-- UI -----------------------------------------------------------------------
//-- From XT95's https://www.shadertoy.com/view/ldKSDm
//-----------------------------------------------------------------------------
float 	uiSlider(int id) {return texture(iChannel0, vec2(float(id)+.5,0.5)/iResolution.xy).r;}
vec3 	uiColor(int id)	 {return texture(iChannel0, vec2(float(id)+.5,1.5)/iResolution.xy).rgb;}

//-----------------------------------------------------------------------------
//-- Render Functions -----------------------------------------------------------
vec3 render(in vec3 ro, in vec3 rd)
{
    vec3  color         = get_background_color(rd);
    float sphere_hit    = sphere_get_hit(ro,rd,UNIT_SPHERE);
    if(sphere_hit > 0.0) 
    {
        vec3    P = ro + sphere_hit * rd;
        vec3    L = get_light_direction();
        vec3    N = sphere_get_normal(P,UNIT_SPHERE);
        vec3    V = -rd;

        mat3 TBN    = orthonormal_basis(N);
        mat3 TBN_t  = transpose(TBN);
        vec3 wo = normalize(TBN_t * V);
        vec3 wi = normalize(TBN_t * L);
        vec3 wg = normalize(TBN_t * N); // ( should be (0,0,1)^t )
        vec3 wh = normalize(wo+wi);		// ( could check it not zero)

        float wi_dot_wh = clamp(dot(wi,wh),0.,1.); 		// saturate(dot(L,H))
        float wg_dot_wi = clamp(cos_theta(wi),0.,1.); 	// saturate(dot(N,L))

        //-- Filament BRDF parametrization
        //-- Single scattering
        //-- diffuse lambertian term + specular term
        float reflectance 	= clamp(uiSlider(1),0.35,1.); 	// [0.35;1]
        float metallic 		= float(uiSlider(2)>0.5); 		// 0. or 1.
        float roughness 	= clamp( 0.1 + uiSlider(0), 0., 0.8);
    	float alpha 		= roughness*roughness;
        vec3  base_color    = uiColor(0);
        vec3 F0 = 0.16*reflectance*reflectance*(1.0-metallic) + base_color*metallic;

        //-- NDF + G + F
        float lambda_wo = lambda_ggx_isotropic(wo,alpha);
        float lambda_wi = lambda_ggx_isotropic(wi,alpha);
        float D = ndf_ggx_isotropic(wh,alpha);
        float G = 1. / (1. + lambda_wo + lambda_wi);


		vec3 F  = fresnel_schlick(wi_dot_wh,F0);

        //-- Lighting
        vec3 specular_microfacet = (D * F * G) / ( 4. * cos_theta(wi) * cos_theta(wo) ) ;
        vec3 diffuse_lambert = m_i_pi*(1.-metallic)*base_color;
        vec3 diffuse_factor  = vec3(1.)-F; 
        if(!USE_DIFFUSE_TERM){diffuse_lambert = vec3(0.);}

        color = wg_dot_wi * get_light_intensity() * (diffuse_factor*diffuse_lambert + specular_microfacet);
    }
    return color;
}

//-----------------------------------------------------------------------------
//-- Main ---------------------------------------------------------------------
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{  
    vec2 screen_coords = ( 2.*gl_FragCoord.xy - iResolution.xy ) / iResolution.y;
    vec3 ro = vec3( 3. * cos(iTime), 0.5 , 3. * sin(iTime) );
    vec3 rd = compute_camera_ray_direction(screen_coords, ro, vec3(0.), 0.);

    fragColor = vec4( render(ro,rd) , 1.);
    fragColor.rgb = fragColor.rgb / (fragColor.rgb + vec3(1.0)); //-- Reinhard Tonemapping
    fragColor.rgb = pow(fragColor.rgb,vec3(0.4545));			 //-- Gamma Correction

    //-- UI integration -----------------------------------------------
    vec4 ui = texture(iChannel0,gl_FragCoord.xy/iResolution.xy);
    fragColor.rgb = mix(fragColor.rgb,ui.rgb, ui.a*.8);
}

//-----------------------------------------------------------------------------
//-- Constants ----------------------------------------------------------------
const float m_pi        = 3.14159265359;           /* MathConstant: PI                                 */
const float m_i_pi      = 0.31830988618;           /* MathConstant: 1 / PI                             */
const float m_sqrt_2    = 1.41421356237;           /* MathConstant: sqrt(2)                            */
const float m_i_sqrt_2  = 0.70710678119;           /* MathConstant: 1/sqrt(2)                          */

const float m_eps_3f    = 0.001;            
const float m_eps_4f    = 0.0001;            

//-----------------------------------------------------------------------------
//-- Tangent Space Maths (PBRT) -----------------------------------------------
float cos_theta(const vec3 w)       {return w.z;}
float cos_2_theta(const vec3 w)     {return w.z*w.z;}
float sin_2_theta(const vec3 w)     {return max(0., 1. - cos_2_theta(w));}
float sin_theta(const vec3 w)       {return sqrt(sin_2_theta(w));}
float tan_theta(const vec3 w)       {return sin_theta(w) / cos_theta(w);}
float cos_phi(const vec3 w)         {return (sin_theta(w) == 0.) ? 1. : clamp(w.x / sin_theta(w), -1., 1.);}
float sin_phi(const vec3 w)         {return (sin_theta(w) == 0.) ? 0. : clamp(w.y / sin_theta(w), -1., 1.);}
float cos_2_phi(const vec3 w)       {return cos_phi(w) * cos_phi(w);}
float sin_2_phi(const vec3 w)       {return sin_phi(w) * sin_phi(w);} 

//-----------------------------------------------------------------------------
//-- Ray ----------------------------------------------------------------------
vec3 compute_camera_ray_direction(in vec2 screen_coordinates, in vec3 origin, in vec3 target, float cr )
{
	vec3 cw = normalize(target-origin);
	vec3 cp = vec3(sin(cr), cos(cr),0.0);
	vec3 cu = normalize( cross(cw,cp) );
	vec3 cv =          ( cross(cu,cw) );
    return mat3( cu, cv, cw )*normalize(vec3(screen_coordinates,2.0));
}

//-----------------------------------------------------------------------------
//-- Sphere Intersection Functions --------------------------------------------
float sphere_get_hit(in vec3 ro, in vec3 rd, vec4 sph )
{
    vec3 oc = ro - sph.xyz;
    float b = dot( oc, rd );
    float c = dot( oc, oc ) - sph.w*sph.w;
    float h = b*b - c;
    if( h<0.0 ) return(-1.0);
    h = sqrt( h );
    return min( -b-h, -b+h );
}

vec3 sphere_get_normal( in vec3 pos, in vec4 sph )
{
    return normalize(pos-sph.xyz);
}

//-----------------------------------------------------------------------------
//-- Orthonormal Basis Function -----------------------------------------------
//-- @nimitz's "Cheap orthonormal basis" on Shadertoy
//-- https://www.shadertoy.com/view/4sSSW3
mat3 orthonormal_basis(in vec3 n)
{
    vec3 f,r;
    if(n.z < -0.999999)
    {
        f = vec3(0 , -1, 0);
        r = vec3(-1, 0, 0);
    }
    else
    {
        float a = 1./(1. + n.z);
        float b = -n.x*n.y*a;
        f = normalize(vec3(1. - n.x*n.x*a, b, -n.x));
        r = normalize(vec3(b, 1. - n.y*n.y*a , -n.y));
    }
    return( mat3(f,r,n) );
}

