vec4 noise(float t){return texture(iChannel1,vec2(floor(t), floor(t))/256.);}
vec4 valueNoise(vec2 t, float w){
    vec2 fr = fract(t);
	return 
        mix(
            mix( 
                texture(iChannel1,vec2(floor(t.x), floor(t.y))/256.),
                texture(iChannel1,vec2(floor(t.x), floor(t.y) + 1.)/256.),
            	smoothstep(0.,1.,fr.y)
            ),
            mix( 
                texture(iChannel1,vec2(floor(t.x) + 1.,floor(t.y))/256.),
                texture(iChannel1,vec2(floor(t.x) + 1.,floor(t.y) + 1.)/256.),
            	smoothstep(0.,1.,fr.y)
            ),
            smoothstep(0.,1.,pow(fr.x, w)));
}
vec4 fbm(vec2 uv){
	vec4 n = vec4(0);
    uv *= iResolution.xy/vec2(800.,450.);
	n += valueNoise(uv*60.,0.1);
    n += valueNoise(uv*1700.,0.1)*0.5;
    n += valueNoise(uv*10.,1.1)*0.1;
    n -= valueNoise(uv*10. + 4.,0.1)*1.;
    n -= valueNoise(uv*20.,0.5)*0.2;


    //n = valueNoise(uv*3.,19.9)*1.;
    n = max(n, 0.);
    return n;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col = texture(iChannel0,uv).xyz;




    //col = 1. - col;
    col = max(col,0.002);


	vec4 f = smoothstep(0.,1.,fbm(uv*50.));
	vec4 g = smoothstep(0.,1.,fbm(uv*60. - 0.4));

    f.x = pow(f.x, 5.);

    float n = 0.;
    n += f.x*.5;
    n += pow(f.y,7.)*2.5;
    n += pow(f.z,6.)*2.5;
    n += pow(f.w,3.)*0.1;

    n += pow(g.x,6.)*3.;
    n += pow(g.y,4.)*1.;
    n += pow(g.z,4.)*1.;


    n = min(n,0.5);



    if(length(col) > 0.2){
        vec3 nn = vec3(n*0.6,n,n)*2.;
        col *= 1. - nn;
        //col -= pow(g,5.)*2.5;
        //col -= pow(g,2.)*.9;
    }
    else{
        col += pow(n,2.)*.4;
    	//col += f*4.;

    }    

    col *= vec3(1.06,0.98,0.9);


    col = pow(col,vec3(0.4545));


    fragColor = vec4(col,1.0);
}

#define SPEED 1.

#define pointa 10.


#define xor(a,b,c) min(max(a,-b), max(-a + c,b))

#define pi acos(-1.)
#define rmod(a,uv,md,amt) max(a, -abs(mod(atan((uv).y,(uv).x),md*pi) - md*pi*0.5) + md*pi*amt)

#define rot(j) mat2(cos(j),-sin(j),sin(j),cos(j))

#define pal(a,b,c,d,e) (a + (b)*sin((c)*(d) + (e)))

float r11(float p)
{
    p = fract(p * .1031);
    p *= p + 33.33;
    p *= p + p;
    return fract(p);
}
vec2 r12(float p)
{
	vec3 p3 = fract(vec3(p) * vec3(.1031, .1030, .0973));
	p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.xx+p3.yz)*p3.zy);
}





