//  1 out, 2 in...
float hash12(vec2 p)
{
	vec3 p3  = fract(vec3(p.xyx) * .1031);
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.x + p3.y) * p3.z);
}

vec2 kink(vec2 p, vec2 c, float k) {
    p -= c;
    //to polar coordinates
    float ang = atan(p.x, p.y);
    float len = length(p);
    //warp angle with sigmoid function
    ang -= ang/sqrt(1.+ang*ang)*(1.-k);
    //to cartesian coordiantes
    return vec2(sin(ang),cos(ang))*len + c;
}

float sdTri( in vec2 p, in vec2 q )
{
    p.x = abs(p.x);
    vec2 a = p - q*clamp( dot(p,q)/dot(q,q), 0.0, 1.0 );
    vec2 b = p - q*vec2( clamp( p.x/q.x, 0.0, 1.0 ), 1.0 );
    float s = -sign( q.y );
    vec2 d = min( vec2( dot(a,a), s*(p.x*q.y-p.y*q.x) ),
                  vec2( dot(b,b), s*(p.y-q.y)  ));
    return -sqrt(d.x)*sign(d.y);
}
float sdBox( in vec2 p, in vec2 q )
{
    p = abs(p) - q;
    return max(p.y,p.x);
}

float sdLine( in vec2 p, in vec2 a, in vec2 b )
{
    vec2 pa = p-a, ba = b-a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h );
}


float sdLine(vec2 p, vec2 A, vec2 B, float bally){
    vec2 AB = B - A;

    float angle = atan(AB.y, AB.x);

    vec2 n = normalize(vec2(sin(-angle),cos(-angle)));

    float d = dot(p - B,n);

    d = abs(d);
    if(bool(bally)){
        if(dot(p - A,AB) < 0.)
            d = length(p - A);
        if(dot(p - B,AB) > 0.)
            d = length(p - B);
    } else {
        d = max(d,dot(p-A,n*rot(0.5*pi)));
        d = max(d,-dot(p-B,n*rot(0.5*pi)));
        /*
        if(dot(p - A,AB) < 0.)
            d = length(p - A);
        if(dot(p - B,AB) > 0.)
            d = length(p - B);
    	*/

    }


    return d;
}


// from iq
float sdSine( in vec2 p, in float a, in float b, in float c, in float d )
{
    // convert all data to a primitive cosine wave
    p = c*(p-vec2(d,a));

    // reduce to principal half cycle
    const float TPI = 6.28318530718;
    p.x = mod( p.x, TPI); if( p.x>(0.5*TPI) ) p.x = TPI - p.x;

    // find zero of derivative (minimize distance)
    float xa = 0.0, xb = TPI;
    for( int i=0; i<24; i++ ) // 24 bit precision
    {
        float x = 0.5*(xa+xb);
        float y = x-p.x+b*c*sin(x)*(p.y-b*c*cos(x));
        if( y<0.0 ) xa = x; else xb = x;
    }
    float x = 0.5*(xa+xb);

    // compute distance    
    vec2 q = vec2(x,b*c*cos(x));
    return length(p-q)/c;
}

float sdPolygon( in vec2[4] v, in vec2 p )
{
    float d = dot(p-v[0],p-v[0]);
    float s = 1.0;
    for( int i=0, j=4-1; i<4; j=i, i++ )
    {
        vec2 e = v[j] - v[i];
        vec2 w =    p - v[i];
        vec2 b = w - e*clamp( dot(w,e)/dot(e,e), 0.0, 1.0 );
        d = min( d, dot(b,b) );
        bvec3 c = bvec3(p.y>=v[i].y,p.y<v[j].y,e.x*w.y>e.y*w.x);
        if( all(c) || all(not(c)) ) s*=-1.0;  
    }
    return s*sqrt(d);
}

