float df = 0.;

vec2 guv;

// ----------


vec3 colour( float db, vec3 ca, vec3 cb, int mode, int stroke, float strokeparam){



    float deb;

    if (stroke == 0) {
    	deb = smoothstep(df,0.,db );
    } else if (stroke == 1){
        // airbrush/chalk
    	deb = smoothstep(df*strokeparam*0.01 + texture(iChannel0,guv*5.).x*0.004*strokeparam,0.,db );
    } else if (stroke == 2){
        // abstract
    	deb = smoothstep(df*strokeparam + texture(iChannel0,guv*5.).x*0.1*strokeparam,0.,db );
    }



    vec3 ob = mix(vec3(0),cb,deb);
    vec3 co = vec3(0);

    vec4 t = texture(iChannel2,guv);

    cb*=1. - smoothstep(1.,0.,abs(db)*7. + t.z*1.53)*0.2;


    float odeb = max(deb - pow(smoothstep(0.,1.,t.y*0.77), 4.25), 0.);


    if(mode == 0){

        // 0 replace

    	co = mix( ca, cb, max(deb - pow(smoothstep(0.,1.,t.y*0.77), 1.25), 0.));
    	//co = mix( oa, cb, deb);

    } else if(mode == 1){

        // 1 multiply

        co = mix(ca, ca*cb, odeb);
    } else if(mode == 2){

        // 2 overlay

        if (length(ca) < 0.5){
    		co = mix(ca, 2.*ca*cb, odeb);    	
        } else {
    		co = mix(ca, 1. - 2.*(1. - ca)*(1. - cb), odeb);

        }
    } else if(mode == 3){

        // 3 darken

    	co = mix(ca, min(ca,cb)*length(ca /* cb */), odeb);
    } else if(mode == 4){

        // 4 burn

    	co = mix(ca, clamp(1.0 - (1.0 - ca) / cb,0.,1.), odeb);
    } else if(mode == 5){

        // 5 linear burn

    	co = mix(ca, ca + cb - 1., odeb);
    } else if(mode == 6){

        // 6 color dodge

    	co = mix(ca, length(ca) > .5 ? 1.0 - 2.0 * (1.0 - ca) * (1.0 - cb) : 2.0 * ca * cb, odeb);
    }

    return co;
}

float N_i_1 (in float t, in float i)
{
    // return 1 if i < t < i+1, else return 0
    return step(i, t) * step(t,i+1.0);
}

float N_i_2 (in float t, in float i)
{
    return
        N_i_1(t, i)       * (t - i) +
        N_i_1(t, i + 1.) * (i + 2. - t);
}

float N_i_3 (in float t, in float i)
{
    return
        N_i_2(t, i)       * (t - i) / 2.0 +
        N_i_2(t, i +1.) * (i + 3.0 - t) / 2.0;
}

float seed = 0.;
const int cnt = 5;

float SplineValue(in float t)
{
    float[cnt] cp;

    float res = 0.;

    float amp = 0.01;

    vec4 na = valueNoise(vec2(t*4. + seed,0),1.3);
    vec4 nb = valueNoise(vec2(t*19. + seed,0),1.3);

    amp += na.x*0.7 - nb.x*0.01;

    t += na.x*0.5;

    for(float i = 0.; i < float(cnt); i++){
    	int idx = int(i);

        cp[idx] = r11(i + seed)*amp;


        res += cp[idx] * N_i_3(t, i);
    }

    //res -= smoothstep(0.,1.,t*0.12)*0.5;

    res -= max(smoothstep(0.,1.,guv.x*0.4 + 0.1),0.);

    res += max(smoothstep(0.,1.,-guv.x*0.27),0.);

    //res -= clamp(0.,1.,guv.x);
    //res -= smoothstep(0.,1.,guv.x*1.42)*0.5;

    //res += sin(t + cos(t*2.)*0.01)*0.07;

    //res += sin(t + cos(t*2.)*0.01)*0.07;

    return res;
}

float F ( in vec2 coords )
{
    // time in this curve goes from 0.0 to 10.0 but values
    // are only valid between 2.0 and 8.0
    float T = coords.x*float(cnt)*1. + 1.0;
    return SplineValue(T) - coords.y;
}

float SDF( in vec2 coords )
{
    float v = F(coords);
    float slope = dFdx(v) / dFdx(coords.x);
    float d = abs(v)/length(vec2(slope, -1.0)); 

    d = max(d,-coords.x - 0.2);
    d = max(d,coords.x - 0.7);

    return d;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = (fragCoord - 0.5*iResolution.xy)/iResolution.y;

    guv = uv;

    int offs = 4;

    float frame = floor(float(iFrame - offs)*SPEED);

    seed = r11(frame)*2000.;

    df = dFdx(uv.x);

    guv = uv;

    uv += texture(iChannel1,uv).xy*0.003 + texture(iChannel2,uv*0.05).xy*0.01;

    vec3 col = frame < 2. ? vec3(1) : texture(iChannel3,fragCoord/iResolution.xy).xyz;


    float d = 10e5;


    // -- // 
    d = 10e5;
    // COOLINE


    if(mod(float(iFrame)*SPEED,1.) < SPEED){
    	vec2 roll = r12(seed);

        d = SDF( uv + (r12(seed)*2. - 1.)*0.7 ) - 0.001;


        if(frame < pointa){
    		col = colour( d, col, vec3(0.00,0.00,0.0), 0,2,9.);
        } else {
        	col = colour( d + 0.005, col, vec3(1.), 0,1,4.);
        }

        float lseed = seed;
        vec2[4] points;

        //if(frame > pointa && frame < pointa + 7.){
        if(frame > pointa + 20. && frame < pointa + 200.){

        	lseed +=4.7;
        	points = vec2[](r12(lseed),r12(lseed*1.221),r12(lseed*1.5),r12(lseed*1.2));

            for(int i = 0; i < 4; i++){
                points[i] = (points[i]*2. - 1.)*0.5;

                points[i].xy *= 1.5;
            }

        	d = sdPolygon( points, uv ) - 0.00;

        	//col = colour( d, col, pal(0.7,0.2,vec3(6,3,4. - roll.x*1.6),0.5,seed)*1.1, 1,0,9.);


        } else if (frame < 4.){
        	lseed +=1.7;
        	points = vec2[](r12(lseed),r12(lseed*1.221),r12(lseed*1.5),r12(lseed*1.2));

            for(int i = 0; i < 4; i++){
                points[i] = (points[i]*2. - 1.)*0.5;

                points[i].x *= 2.5;
            }

        	d = sdPolygon( points, uv ) - 0.00;

        	col = colour( d, col, pal(0.5,0.4,vec3(3,3,4. - roll.x*1.6),0.5,seed)*1.1, 1,2,49.);


        }


    }


    //col = max(col,0.);


    fragColor = vec4(col,1.0);
}
