// Check if point (current uv coords) has same sign as third point in compare with straight line
// of triangle
bool isInPlace(vec2 curPos, vec2 start, vec2 end, vec2 other)
{
    float a = (curPos.x - start.x) / (end.x - start.x);
	float b = (curPos.y - start.y) / (end.y - start.y);
    float diff = a - b;

    float a1 = (other.x - start.x) / (end.x - start.x);
	float b1 = (other.y - start.y) / (end.y - start.y);
    float diff1 = a1 - b1;

    return (diff1 >= 0.0 && diff >= 0.0) || (diff < 0.0 && diff1 < 0.0);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = vec3(1.0, 1.0, 1.0);


    //Setup your points for triangle
    vec2 aPoint = vec2(0.1213, 0.313);
    vec2 bPoint = vec2(0.43895, 0.821398);
    vec2 cPoint = vec2(0.4934, 0.6405);


    if (isInPlace(uv, aPoint, bPoint, cPoint) &&
        isInPlace(uv, aPoint, cPoint, bPoint) &&
        isInPlace(uv, bPoint, cPoint, aPoint))
    {
    	col = vec3(0.0, 0.0, 0.0);        
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}
