void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;

    uv *= 3.; //zoom

    vec2 c = uv;

    float i2 = 0.;

    float maxI = 25. + iTime;

    for( float i = 0.; i < maxI; i++) //iterations
    {
        //complex multiplication: c.x = real, c.y = imaginary;
        float a = c.x * c.x - c.y * c.y;
        float b = 2. * c.x * c.y;
        c = vec2( a, b) + uv;

        //first time it is sure to escape (real | imaginary > 2)
        if( i2 == 0. && (abs(c.x) > 2. || abs(c.y) > 2.))
        {
            i2 = i;
        }
    }

    //red = real, green = imaginary
    vec3 col = vec3(abs(c.x), abs(c.y), 0.);

    if( abs(c.x) > 2. || abs(c.y) > 2.)
    {
        col = vec3(0.);
    }

    //blue = iterations
    col += vec3(0.,0.,i2/maxI);


    // Output to screen
    fragColor = vec4(col,1.0);
}
